-- ----------------------------
-- Table structure for about_us
-- ----------------------------
DROP TABLE IF EXISTS `about_us`;
CREATE TABLE `about_us` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '关于我们id',
  `title` varchar(50) NOT NULL COMMENT '标题',
  `content` text NOT NULL,
  `time` int(11) unsigned NOT NULL COMMENT '发布时间',
  `status` tinyint(1) unsigned DEFAULT '0' COMMENT '删除为1，默认为0',
  `intro` varchar(255) NOT NULL COMMENT '首页介绍',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 COMMENT='关于我们';

-- ----------------------------
-- Records of about_us
-- ----------------------------
INSERT INTO `about_us` VALUES ('1', '华谊兄弟公益基金', '<p>2011年，华谊兄弟的明星和员工共同发起了一项专注于“创造力公益”并倡导教育公平的专项基金——华谊兄弟公益基金。有别于以往公益项目中简单直接的物质馈赠形式，我们一直坚信，精神的滋养对于孩子的成长来说尤为重要。我们希望通过光影的力量，开拓孩子们的视野、丰富孩子们的生活，让他们获得更多的精神财富从而受益终生。<br/></p><p>创立华谊兄弟公益基金的初心来源于华谊兄弟创始人王中军和王中磊先生对“创造力公益”的概念的探索与实践，他们希望发展出一种不断创新、可持续的公益模式，以推动电影教育的发展、倡导并践行教育公平理念、同时促进国际联系交流。</p><p>6年间，我们在全国十余个省份的偏远地区成立了100所“零钱电影院”，一方面推动了学校和教师认识电影在儿童发展和教育中所发挥的重要作用，另一方面提升了贫困地区儿童对电影的认识和了解，开阔了视野。与此同时，我们在更广泛的地区推广社区电影教育，培养社区居民观影习惯。而在2014年上映的公益电影《有一天》，更促进了社会对特殊儿童群体的认知。此外，我们定期在全球范围内举办展示贫困地区儿童创造力的“小小画展”、组织了我国第一支融合教育合唱团“有一天合唱团”、不断支持融合教育项目、重症儿童舒缓疗护项目……我们坚信，每一个孩子都有被照顾、被尊重和受教育的权利，教育公平将会推动整个社会的发展与进步。</p><p>一路走来，我们还积极汇聚资源，以多种方式实践善举，投身国际合作及灾后重建等公益项目，聚点滴小爱为大爱。我们的公益历程，代表着推广电影教育的初步成果，也是华谊兄弟企业“创造力”基因的传承与延续。我们见证了电影激发了更多孩子与生俱来的创造力，更惊喜于小朋友的不断进步。孩子们的转变也激励着我们不断创新，开创更加可持续的公益发展模式，予力整个社会的发展与进步。未来，我们希望与您一起，让创造力随影而生，让创造力公益薪火相传。</p>', '1497965535', '0', ' 2011年，华谊兄弟的明星和员工共同发起了一项专注于“创造力公益”并倡导教育公平的专项基金——华谊兄弟公益基金。有别于以往公益项目中简单直接的物质馈赠形式，我们一直坚信，精神的滋养对于孩子的成长来说尤为重要。我们希望通过光影的力量，开拓孩子们的视野、丰富孩子们的生活，让他们获得更多的精神财富从而受益终生。');
INSERT INTO `about_us` VALUES ('2', '华谊兄弟', '<p style=\"text-indent: 2em;\">华谊兄弟传媒股份有限公司（深交所：300027），中国大陆最知名的综合性娱乐集团，由王中军、王中磊兄弟于1994年创立，1998年华谊兄弟正式进入影视界，开发、制作及发行中国最受欢迎的影视作品；2009年率先登陆创业板，成为中国影视行业首家上市公司，被称为“中国影视娱乐第一股”。近年来，华谊兄弟以前瞻性思维引领业务布局，不断坚持创新，被业界评价为中国娱乐行业的先锋和领军者。</p><p style=\"text-indent: 2em;\">目前，华谊兄弟投资及运营四大业务板块：以电影、电视剧、艺人经纪等业务为代表的影视娱乐板块；以电影城、电影世界、电影小镇、文化城等业务为代表的品牌授权与实景娱乐板块；以游戏、新媒体、粉丝社区等业务为代表的互联网娱乐板块；以及第四大业务板块产业投资板块。2014年，阿里巴巴、腾讯公司、中国平安宣布入股华谊，成为华谊兄弟突破行业边界限制的强大后盾。</p><p style=\"text-indent: 2em;\">如今，伴随公司整体布局日益完善、各业务健康稳定发展，华谊兄弟将继续秉持三大发展战略：强内核战略、国际化战略、大娱乐生态圈战略，以“提供优质娱乐内容”为核心，全力打造中国首屈一指的“全娱乐创新工厂”，致力于成长为中国血统的世界级娱乐集团，将精彩的娱乐内容和产品呈现给全球受众。</p>', '1499922158', '0', ' 华谊兄弟传媒股份有限公司（深交所：300027），中国大陆最知名的综合性娱乐集团，由王中军、王中磊兄弟于1994年创立，1998年华谊兄弟正式进入影视界，开发、制作及发行中国最受欢迎的影视作品；2009年率先登陆创业板，成为中国影视行业首家上市公司，被称为“中国影视娱乐第一股”。近年来，华谊兄弟以前瞻性思维引领业务布局，不断坚持创新，被业界评价为中国娱乐行业的先锋和领军者。 ');
INSERT INTO `about_us` VALUES ('3', '愿景与使命', '<p style=\"text-indent: 2em; text-align: left; margin-bottom: 5px;\">愿景：我们希望每一个孩子都能享受电影带来的快乐和启迪，有更多机会发挥创造力。<br style=\"text-indent: 2em; text-align: left;\"/></p><p style=\"text-indent: 2em; text-align: left; margin-bottom: 5px;\">使命：我们致力于成为创造力公益的实践者、电影教育的推动者、教育公平的倡导者，和行业内国际交流的先行者。希望通过我们的努力，发展出一种不断创新、可持续的公益模式，让使更多的儿童得以受益。</p><p style=\"text-indent: 2em; text-align: left; margin-bottom: 5px;\">1.&nbsp;推动电影教育发展：让越来越多的贫困地区儿童可以免费看到好电影，通过光影的力量开拓孩子们的视野、丰富孩子们的生活。</p><p style=\"text-indent: 2em; text-align: left; margin-bottom: 5px;\">2.&nbsp;倡导教育公平理念：相信每一个孩子都有被照顾、被尊重和受教育的权利，倡导和践行融合、平等的理念。</p><p style=\"text-indent: 2em; text-align: left; margin-bottom: 5px;\">3.&nbsp;促进国际联系交流：学习其他国家电影教育的理论和实践经验，从本土实际出发，思考与完善中国儿童电影教育体系。</p><p><br style=\"text-indent: 2em; text-align: left;\"/></p>', '1499922215', '0', '我们希望每一个孩子都能享受电影带来的快乐和启迪，有更多机会发挥创造力，也致力于成为创造力公益的实践者、电影教育的推动者、教育公平的倡导者，和行业内国际交流的先行者。希望通过我们的努力，发展出一种不断创新、可持续的公益模式，让使更多的儿童得以受益。     ');
INSERT INTO `about_us` VALUES ('4', '零钱电影院简介', '<p>“零钱电影院”是华谊兄弟公益基金的核心项目。一方面，我们希望有更多贫困地区儿童可以用“零分钱”免费欣赏电影；另一方面，我们也倡导量力而行做公益，每个人献出一点小小的爱心，就可以用“零花钱”帮助贫困地区儿童的教育和发展。<br style=\"text-indent: 2em; text-align: left;\"/></p><p>项目在贫困地区学校及打工弟子学校搭建起一个个可以让孩子们尽情发挥创造力的艺术空间——“零钱电影院”。我们相信，一本书、一幅画、一首歌、一部电影都可以激发孩子们与生俱来的创造力与艺术感知力，甚至开启一扇梦想之门。我们希望以“零钱电影院”为沃土，让更多的儿童可以全面地认识世界，从而更有勇气追逐梦想，创造未来的无限可能。</p><p>截至2016年底，华谊兄弟公益基金已在北京、上海、广州、河南、云南、青海、甘肃、四川、贵州、新疆等十余个省份，捐赠了100所“零钱电影院”，惠及贫困儿童达15万人。</p><p>此外，对于走访中发现的学校基础设施建设薄弱、设施陈旧等问题，我们也通过多种形式对“零钱电影院”所在学校进行了援助。</p><p><br style=\"text-indent: 2em; text-align: left;\"/></p>', '1497965713', '0', ' “零钱电影院”是华谊兄弟公益基金的核心项目。一方面，我们希望有更多贫困地区儿童可以用“零分钱”免费欣赏电影；另一方面，我们也倡导量力而行做公益，每个人献出一点小小的爱心，就可以用“零花钱”帮助贫困地区儿童的教育和发展。');
INSERT INTO `about_us` VALUES ('5', '零钱电影院', '<p style=\"text-align: justify;\">我们曾做过一个调查，发现电影远没有我们想象中那么普及。即使生活在北京，也有不少打工子弟因为经济原因从未体验过电影带来的震撼与启迪。从2011年起，我们开始走访偏远地区，为社区群众放映电影，普及电影教育。随后，“零钱电影院”应运而生，成为了华谊兄弟公益基金的第一个公益项目。<br style=\"text-align: left;\"/></p><p style=\"text-align: justify;\">&nbsp;所谓“零钱电影院”，一方面，是希望更多贫困地区儿童可以用“零分钱”免费欣赏电影；另一方面，我们也倡导量力而行做公益，每个人献出一点小小的爱心，就可以用“零花钱”帮助贫困地区儿童的教育和发展。</p><p style=\"text-align: justify;\">&nbsp;6年间，我们在全国100多个偏远地区的学校里搭建“零钱电影院”，将其闲置的图书馆、自习室或教室等进行精心的布置，并配备全套的放映设备和电影光盘，让孩子们在一个轻松、舒适的环境里去了解电影、感受电影，以此来丰富他们的精神世界。同时，我们会对学校老师进行培训，在观影后进一步开展电影分享课、绘画课等，让孩子们表达出他们心中的电影世界。“零钱电影院”项目为贫困地区儿童带来的不只是好电影，还为他们搭建了一个可以尽情发挥创造力的艺术空间。</p><p style=\"text-align: justify;\">&nbsp;从2017年开始，“零钱电影院”也开启了2.0阶段的新尝试。在不断增加“零钱电影院”数量、为孩子们开展电影启蒙教育的同时，华谊兄弟公益基金将进一步深耕电影教育领域，加强对教师的培训与指导、提升硬件支持，并与其他机构和项目合作进行资源的拓展，促进教育效果的提升。对于专注于内容创造的华谊兄弟而言，我们不仅仅希望带给广大观众启迪与欢乐，更希望给予更多人逐梦的勇气和探寻生命意义的线索。小小的“零钱电影院”，是孩子们梦开始的地方，一部部优秀而富有教育意义的影片将化为宝贵的精神财富，陪伴他们成长。</p><p style=\"text-align: justify;\">&nbsp;在对“零钱电影院”所在学校进行走访和回访的过程中，我们也发现，尽管国家对偏远贫困地区的教育投入十分重视，但受到资源总量的限制，有限的资金往往投入到最重要的教学设施的建设和改造上，没有余力对学校的其他设施进行全面的更新。</p><p style=\"text-align: justify;\">&nbsp;面对偏远地区学校基础设施建设薄弱、设施陈旧等问题，我们多方筹措资源，为学校寻找解决方法。2016年初，由华谊兄弟公益基金出资建设的云南省芒市营盘小学厕所和澡堂正式投入使用，孩子们从此告别了旱厕和简易冲凉房；2016年底，云南省玉龙县红麦完小的食堂建设工程正式启动，师生们即将在学年结束前迎来全新的食堂。目前，几个新的操场建设工程也即将开工，耐用防雨、稳定实用、可以减少运动伤害的塑胶操场，将成为孩子们追逐体育梦想的大舞台。</p><p><br style=\"text-align: left;\"/></p>', '1503282832', '0', ' 我们曾做过一个调查，发现电影远没有我们想象中那么普及。即使生活在北京，也有不少打工子弟因为经济原因从未体验过电影带来的震撼与启迪。从2011年起，我们开始走访偏远地区，为社区群众放映电影，普及电影教育。随后，“零钱电影院”应运而生，成为了华谊兄弟公益基金的第一个公益项目。 ');
INSERT INTO `about_us` VALUES ('6', '公益放映', '<p style=\"text-align: justify;\">公益放映是华谊兄弟公益基金一直坚持的公益实践。从2011年开始，华谊兄弟公益基金不断走访偏远地区，为当地的社区与学校带去好电影。</p><p style=\"text-align: justify;\">我们认为，贫困地区的群众吃饭、饮水、医疗需要帮助，他们也同样享有看电影的权力。因此，将优秀的电影引入乡村社区，让越来越多的乡村群众免费看到好电影，丰富贫困地区群众的精神生活，是华谊兄弟作为影视行业领军者的一份社会责任。与此同时，公益放映也是社区电影教育的一种形式，通过这种形式培养城市和农村社区居民观影习惯，提升其对电影的欣赏力，也将对中国电影产业的发展起到积极的作用。</p><p><br style=\"text-indent: 2em; text-align: left;\"/></p>', '1503282853', '0', ' 公益放映是华谊兄弟公益基金一直坚持的公益实践。从2011年开始，华谊兄弟公益基金不断走访偏远地区，为当地的社区与学校带去好电影。 ');
INSERT INTO `about_us` VALUES ('7', '重大灾害捐助', '<p></p><p></p><p style=\"text-align: justify;\">六年里，华谊兄弟公益基金多次在重大灾害发生后及时给予捐赠和援助。2013年四川雅安地震发生后，基金工作人员多次前往灾区，不仅在灾区组织公益放映，发挥基金资源优势为灾区群众带来精神抚慰，也向灾区捐资重建学校，在多方面支持灾区重建工作。</p><p style=\"text-align: justify;\">2015年，天津港爆炸事故发生后，基金也向在天津港“8·12”特别重大火灾爆炸事故中牺牲及负伤的消防战士烈士家属捐款200万元。</p><p style=\"text-align: justify;\">2016年，由华谊兄弟公益基金在 “4·20雅安地震”发生后全额资助的芦山县三江小学重建工作正式竣工并通过验收。曾经损毁严重的校舍，现已变身为宽敞明亮、抗震七级的新教学楼，这也是第一所以华谊兄弟的名义命名的学校。</p><p><br/></p><p><br/></p>', '1503282905', '0', ' 六年里，华谊兄弟公益基金多次在重大灾害发生后及时给予捐赠和援助。2013年四川雅安地震发生后，基金工作人员多次前往灾区，不仅在灾区组织公益放映，发挥基金资源优势为灾区群众带来精神抚慰，也向灾区捐资重建学校，在多方面支持灾区重建工作。 ');
INSERT INTO `about_us` VALUES ('8', '公益电影《有一天》', '<p style=\"text-align: justify;\">在开展公益服务的过程中，我们接触了很多特殊的儿童群体，也感受到社会对这些群体存在的误解和偏见。因此，我们希望通过影像的力量，将我们的公益理念传递给公众。2012年初，公益电影《有一天》正式开始了筹备工作。历经900天的艰苦筹备和拍摄，这部由华谊兄弟公益基金自行筹款发起制作的影片终于在2014年9月10日进行了全国公映。<br/></p><p style=\"text-align: justify;\">《有一天》是中国第一部全明星阵容“零片酬”参与的电影，包括周迅、张涵予、徐帆、冯绍峰、韩庚、袁姗姗、斯琴高娃、王千源、安以轩、咏梅、冯嘉怡、熊乃瑾等在内的演员都采取“捐时间”的方式，不计报酬地参与影片的拍摄工作。在发行方面，它也成为中国第一部“零分账”电影，华谊兄弟在《有一天》发行中不收取任何票房收益，并首次推出5元公益票价全国院线公映，开创了公益领域及电影发行的新模式。</p><p style=\"text-align: justify;\">同时，《有一天》也收到包括第21届日本东京国际儿童电影节、第37届法国克莱蒙·费朗国际短片电影节、第7届比利时布鲁塞尔国际儿童电影节、第37届德国卢卡斯国际儿童电影节、第6届澳门国际电影节、第19届印度国际儿童电影节等国际性电影节的邀请参与展映，并荣获第9届美国西雅图儿童电影节全球聚焦奖、Vcare中国公益映像节金蝴蝶奖、旅游卫视行者影像节年度公益影片，澳门国际电影节最佳公益影片奖等多个奖项。</p><p style=\"text-align: justify;\">此外，2014年12月，《有一天》也作为唯一一部中国影片，受邀走进位于美国纽约的联合国总部，在联合国残疾人电影节上进行了特别放映，让中国的特殊儿童群体得到更多人积极、正面的关注和了解。</p><p><br/></p><p><br/></p>', '1503282867', '0', ' 在开展公益服务的过程中，我们接触了很多特殊的儿童群体，也感受到社会对这些群体存在的误解和偏见。因此，我们希望通过影像的力量，将我们的公益理念传递给公众。2012年初，公益电影《有一天》正式开始了筹备工作。历经900天的艰苦筹备和拍摄，这部由华谊兄弟公益基金自行筹款发起制作的影片终于在2014年9月10日进行了全国公映。 ');
INSERT INTO `about_us` VALUES ('9', '有一天合唱团', '<p style=\"text-align: justify;\">“有一天合唱团” 由华谊兄弟公益基金创建于2015年，是中国第一支“融合教育”合唱团。合唱团由中国第一部公益电影《有一天》孕育而生，每年进行一次演出。“有一天合唱团”成员由来自不同生活背景的孩子们组成，每一次表演都是一个彼此从陌生、相识、接纳、融合、到拥抱爱的过程。<br/></p><p style=\"text-align: justify;\">2015年6月14日，“有一天合唱团”与国际钢琴家郎朗共同在联合国70周年音乐会上登台表演电影《有一天》主题曲《给小孩》，用音乐的方式向世界传递和平与发展的声音。2016年7月16日，“有一天合唱团”在北京奥体中心体育馆登上由周迅携手华谊兄弟公益基金举办的“ONE NIGHT给小孩”关爱残障儿童公益演唱会的舞台。两场演出向更多人传递了“融合教育”的理念，展现残障儿童的潜能，推动公众对特殊儿童群体的观念改变，鼓励更多的特殊儿童群体自信、快乐地成长。</p><p style=\"text-align: justify;\"><br/></p>', '1503368691', '0', ' “有一天合唱团” 由华谊兄弟公益基金创建于2015年，是中国第一支“融合教育”合唱团。合唱团由中国第一部公益电影《有一天》孕育而生，每年进行一次演出。“有一天合唱团”成员由来自不同生活背景的孩子们组成，每一次表演都是一个彼此从陌生、相识、接纳、融合、到拥抱爱的过程。   ');

-- ----------------------------
-- Table structure for admin_user
-- ----------------------------
DROP TABLE IF EXISTS `admin_user`;
CREATE TABLE `admin_user` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '后台管理员表',
  `name` varchar(30) NOT NULL COMMENT '用户名',
  `password` char(32) NOT NULL COMMENT '密码',
  `status` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `name` (`name`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='后台管理员表';

-- ----------------------------
-- Records of admin_user
-- ----------------------------
INSERT INTO `admin_user` VALUES ('1', 'admin', '21232f297a57a5a743894a0e4a801fc3', '0');

-- ----------------------------
-- Table structure for ads
-- ----------------------------
DROP TABLE IF EXISTS `ads`;
CREATE TABLE `ads` (
  `ad_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `ad_title` varchar(100) NOT NULL COMMENT '广告位名称',
  `ad_position` tinyint(3) NOT NULL COMMENT '//广告位置',
  `ad_type` tinyint(3) NOT NULL COMMENT '//广告类型',
  `ad_width` smallint(11) unsigned NOT NULL COMMENT '//图片宽度',
  `ad_height` smallint(11) unsigned NOT NULL COMMENT '//图片高度',
  `ad_thumb` varchar(255) NOT NULL COMMENT '//图片',
  `ad_time` int(11) unsigned NOT NULL,
  `ad_status` tinyint(1) unsigned zerofill NOT NULL COMMENT '//是否删除',
  `ad_thumbs` varchar(255) NOT NULL COMMENT '大图',
  `link` varchar(255) NOT NULL COMMENT '图片链接',
  PRIMARY KEY (`ad_id`)
) ENGINE=InnoDB AUTO_INCREMENT=125 DEFAULT CHARSET=utf8 COMMENT='广告位表';

