-- ----------------------------
-- Table structure for configs
-- ----------------------------
DROP TABLE IF EXISTS `configs`;
CREATE TABLE `configs` (
  `name` varchar(100) NOT NULL COMMENT '配置名称',
  `value` varchar(255) DEFAULT NULL COMMENT '配置值'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='网站配置信息表';

-- ----------------------------
-- Records of configs
-- ----------------------------
INSERT INTO `configs` VALUES ('title', '华谊兄弟公益基金_公益网站_明星公益');
INSERT INTO `configs` VALUES ('keywords', '华谊兄弟,公益基金,公益网站,明星公益,零钱电影院');
INSERT INTO `configs` VALUES ('description', '华谊兄弟公益基金是由华谊兄弟的明星和员工共同发起的专项公募基金，其使命是让越来越多的贫困儿童免费看到好电影，改善贫困地区儿童的精神教育。 华谊兄弟公益基金的核心公益项目是在贫困地区的学校搭建寓教于乐于一体的“零钱电影院”，在这里，孩子们可以轻松愉快的学习、看电影、阅读。');
INSERT INTO `configs` VALUES ('copyright', '版权信息');

-- ----------------------------
-- Table structure for contact
-- ----------------------------
DROP TABLE IF EXISTS `contact`;
CREATE TABLE `contact` (
  `con_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `con_address` varchar(50) NOT NULL COMMENT '地址',
  `con_email` varchar(50) NOT NULL COMMENT '邮箱',
  `con_tel` varchar(50) NOT NULL COMMENT '电话',
  `con_weibo` varchar(50) NOT NULL COMMENT '微博名',
  `con_img` varchar(255) NOT NULL COMMENT '图片',
  PRIMARY KEY (`con_id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='联系我们';

-- ----------------------------
-- Records of contact
-- ----------------------------
INSERT INTO `contact` VALUES ('1', '北京市朝阳区新源南路甲2号华谊兄弟办公大楼6F', 'hygongyi@huayimedia.com', '010-65805780', '@关注就是力量', '/upload/20170623/149815068291341.jpg;/upload/20170624/149831470625237.png;/upload/20170624/149831472225287.png;');

-- ----------------------------
-- Table structure for growth
-- ----------------------------
DROP TABLE IF EXISTS `growth`;
CREATE TABLE `growth` (
  `grow_id` int(255) unsigned NOT NULL AUTO_INCREMENT,
  `grow_title` varchar(50) NOT NULL COMMENT '标题',
  `grow_content` text NOT NULL COMMENT '内容',
  `grow_status` tinyint(1) unsigned DEFAULT '0' COMMENT '1删除',
  `create_time` int(10) unsigned NOT NULL COMMENT '发布时间',
  `grow_time` int(10) unsigned NOT NULL COMMENT '事件时间',
  PRIMARY KEY (`grow_id`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of growth
-- ----------------------------
INSERT INTO `growth` VALUES ('1', '222', '222', '1', '111', '0');
INSERT INTO `growth` VALUES ('2', '333', '444', '1', '555', '0');
INSERT INTO `growth` VALUES ('3', '华谊兄弟公益基金正式启动，“零钱电影院”项目公布', '<p><img src=\"/uploads/ueditor/php/upload/image/1496544626884612.png\" title=\"1496544626884612.png\" alt=\"image.png\"/></p><p style=\"text-align: justify; line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">华谊兄弟的明星和员工共同发起了一项专注于“创造力公益”并倡导教育公平的专项基金——华谊兄弟公益基金。成立当天，华谊兄弟公益基金宣布了其核心公益项目 “零钱电影院”，旨在让越来越多的贫困儿童免费看到好电影，改善贫困地区儿童的精神教育。</span></p>', '0', '1503368872', '1306888411');
INSERT INTO `growth` VALUES ('4', '第一所“零钱电影院”建成并投入使用', '<p><img src=\"/uploads/ueditor/php/upload/image/1503370885304059.jpeg\" title=\"1503370885304059.jpeg\" alt=\"2011年10月 华谊兄弟公益基金在北京搭建了第一所“零钱电影院”.JPG\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">华谊兄弟公益基金在北京的一所打工子弟学校搭建了首个“零钱电影院”。“零钱电影院”由华谊兄弟公益基金整体监制并提供高质量的片源，希望让孩子们在寓教于乐的环境下，感受光影的魅力。</span></p>', '0', '1503370887', '1319070811');
INSERT INTO `growth` VALUES ('5', '《有一天》公益电影项目启动', '<p><img src=\"/uploads/ueditor/php/upload/image/1496545075527199.png\" title=\"1496545075527199.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">华谊兄弟公益基金发起了呼吁关注弱势儿童的公益电影《有一天》 （ONE DAY）项目，希望通过影像的力量让儿童得到更多的关注与帮助。&nbsp;</span></p>', '0', '1503370785', '1326328412');
INSERT INTO `growth` VALUES ('6', '“关爱特殊儿童ONE NIGHT公益晚会”举办', '<p><img src=\"/uploads/ueditor/php/upload/image/1496546067101204.png\" title=\"1496546067101204.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">与公益电影《有一天》一脉相承的“关爱特殊儿童ONE NIGHT公益晚会”由《有一天》参演演员周迅携手华谊兄弟公益基金在杭州黄龙体育馆温暖举行，延续《有一天》的公益理念。当晚，演唱会嘉宾与电影《有一天》中的四位小主演在现场零距离互动，呼吁社会关爱、关注特殊儿童。</span></p>', '0', '1503370763', '1405470814');
INSERT INTO `growth` VALUES ('7', '公益电影《有一天》（ONE DAY）全国公映', '<p><img src=\"/uploads/ueditor/php/upload/image/1503370732674292.jpeg\" title=\"1503370732674292.jpeg\" alt=\"p2198749463_副本.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">年度最公益电影《有一天》全国公映。电影聚焦九个特殊儿童群体，通过9个感人而有趣的故事，呼吁大家关注特殊儿童群体。该片由华谊兄弟公益基金自行筹款发起制作，是中国第一部全明星阵容“零片酬”参与的电影，由华谊“零分账”出品和发行，不收取任何票房收益，并首次推出5元公益票价全国院线公映，树立了中国公益电影新标杆。</span></p>', '0', '1503370734', '1410309214');
INSERT INTO `growth` VALUES ('8', '《有一天》受邀访问联合国纽约总部', '<p><img src=\"/uploads/ueditor/php/upload/image/1496546314857350.png\" title=\"1496546314857350.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">在世界唐氏综合症日，华谊兄弟公益基金代表及参演小演员受邀到联合国纽约总部进行演讲，分享《有一天》的拍摄理念，并让世界关注唐氏综合症群体在中国的现状。</span></p>', '0', '1503370493', '1426898015');
INSERT INTO `growth` VALUES ('9', '捐助天津港“8•12”事件烈士家属', '<p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"font-size: 14px;\">天津港“</span><span style=\"font-size: 14px;\">8·12”</span><span style=\"font-size: 14px;\">特别重大火灾爆炸事故发生后，华谊兄弟公益基金迅速行动，向在事故中牺牲的烈士家属进行了捐款。</span></span></p>', '0', '1503370467', '1439339615');
INSERT INTO `growth` VALUES ('10', '联手联合国推动少数民族地区妇女赋权', '<p><img src=\"/uploads/ueditor/php/upload/image/1496546480814147.png\" title=\"1496546480814147.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">华谊兄弟公益基金与联合国开发计划署在北京签署合作协议，共同推动少数民族女性赋权与发展。该合作计划通过发展传统手工艺，帮助少数民族地区贫困弱势群体摆脱贫困，同时保护本民族传统文化。</span></p>', '0', '1503370447', '1448325215');
INSERT INTO `growth` VALUES ('11', ' “ONE NIGHT 给小孩”公益演唱会举行', '<p><img src=\"/uploads/ueditor/php/upload/image/1496546546793886.png\" title=\"1496546546793886.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">周迅再度携手华谊兄弟公益基金于北京奥体中心举行第三届“ONE NIGHT给小孩” 关爱残障儿童公益演唱会，共同关注孤残儿童康复与教育。由华谊兄弟公益基金创办的中国首个融合教育合唱团“有一天合唱团”为演唱会温暖献唱，展现残障儿童的潜能，向更多人传递“融合教育”的理念。</span></p>', '0', '1503370427', '1468642815');
INSERT INTO `growth` VALUES ('12', '四川雅安捐建学校竣工', '<p><img src=\"/uploads/ueditor/php/upload/image/1496546736370680.png\" title=\"1496546736370680.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 0em;\">在 2013年雅安 4.20地震之后，华谊兄弟公益基金向雅安灾区捐赠善款，用于</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 0em; text-align: center;\">芦山县飞仙关镇三江小学的重建。2016 年，工程全部通过验收，</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-indent: 0em; text-align: center; background: white;\">曾经损毁严重的校舍，现已变身为宽敞明亮、抗震七级的新教学楼，这也是第一所以华谊兄弟的名义命名的学校。</span></p><p style=\"text-indent: 2em; text-align: left;\"><br/></p>', '0', '1503370401', '1475727615');
INSERT INTO `growth` VALUES ('13', '公益5周年，第100所“零钱电影院”落成', '<p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1496546931816856.png\" title=\"1496546931816856.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">时值华谊兄弟公益基金成立5周年之际，第100所“零钱电影院”于云南省丽江市玉龙县海南完小正式落成并举办捐赠仪式。截至2016年底，华谊兄弟公益基金共在北京、上海、广州、河南、云南、青海、甘肃、四川、贵州、新疆等十余个省份，捐赠了100所零钱电影院，惠及贫困儿童达15万人。</span></p><p><br style=\"text-indent: 2em; text-align: left;\"/></p>', '0', '1503370357', '1477442016');
INSERT INTO `growth` VALUES ('14', '“零钱电影院”升级计划启动', '<p><img src=\"/uploads/ueditor/php/upload/image/1498621565502816.png\" title=\"1498621565502816.png\" alt=\"图片.png\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">2017年初，“零钱电影院”开启升级计划：在进行硬件更新与升级的同时，进一步深耕儿童电影教育领域，在软件上加强对教师课程安排、授课技巧的培训与指导，促进教育效果的进一步提升。</span></p>', '0', '1503370332', '1484668800');
INSERT INTO `growth` VALUES ('15', '举办成立六周年庆祝活动', '<p><img src=\"/uploads/ueditor/php/upload/image/1498621642639286.png\" title=\"1498621642639286.png\" alt=\"图片.png\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1498621654626991.png\" title=\"1498621654626991.png\" alt=\"图片.png\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">2017年儿童节，时值成立六周年，华谊兄弟公益基金团队与华谊兄弟员工代表来到了甘肃省会宁县焦河小学与孩子们共庆六一，参与了孩子们的联欢会活动。此外，员工们为孩子们精心准备了一堂生动的电影分享课，用光影与他们一起庆祝这个有着丰富意义的日子。</span></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">儿童节期间，华谊兄弟公益基金共对甘肃省5所“零钱电影院”所在学校进行了回访，并准备了4000件精心挑选的儿童节礼物送给孩子们。</span></p>', '0', '1503370296', '1496277217');
INSERT INTO `growth` VALUES ('16', '第一届“零钱电影院”校长峰会召开', '<p><img src=\"/uploads/ueditor/php/upload/image/1503370148411333.jpeg\" title=\"1503370148411333.jpeg\" alt=\"百位“零钱电影院”校长与王中军、王中磊等嘉宾合影留念_副本.jpg\"/></p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">7月23-29日，为期一周的华谊兄弟公益基金第一届“零钱电影院”校长峰会在北京召开，共有来自全国10个省市的100余位“零钱电影院”所在学校校长出席并参加了儿童电影教育和参与式教学的相关培训，为进一步提升学校对儿童电影教育重要性的认识和推动儿童电影教育的落地普及打下了良好基础。</span></p>', '0', '1503370242', '1501288417');

