-- ----------------------------
DROP TABLE IF EXISTS `others`;
CREATE TABLE `others` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '关于我们id',
  `organization` varchar(50) NOT NULL COMMENT '合作机构',
  `title` varchar(50) NOT NULL COMMENT '简称',
  `fullname` varchar(255) NOT NULL COMMENT '项目全名',
  `content` text NOT NULL,
  `time` int(11) unsigned NOT NULL COMMENT '发布时间',
  `status` tinyint(1) unsigned DEFAULT '0' COMMENT '删除为1，默认为0',
  `sort` int(11) unsigned NOT NULL COMMENT '排序',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COMMENT='关于我们';

-- ----------------------------
-- Records of others
-- ----------------------------
INSERT INTO `others` VALUES ('1', '华谊兄弟公益基金×UNDP', '指尖上的幸福', '“指尖上的幸福”促进少数民族地区女性赋权与发展项目', '<p style=\"text-align: justify;\">华谊兄弟公益基金一直与联合国保持着良好的合作关系。2015年11月，联合国开发计划署与华谊兄弟公益基金在北京签署合作协议，支持第一期“促进少数民族地区女性综合发展项目”。该项目的实施地正是华谊兄弟公益基金“零钱电影院”项目覆盖的贵州黔东南地区。通过发展传统手工艺，希望推动少数民族地区减贫工作，帮助少数民族地区贫困弱势群体进一步参与中国社会经济发展，并公平享有发展成果。</p><p style=\"text-align: justify;\">&nbsp;&nbsp;&nbsp; 项目开展的一年间，在三个苗寨里举办了苗绣技术培训、手工艺传承等一系列活动，更多的女性和儿童加入了传承苗绣文化的行列中。一个个精美的绣片承载着少数民族妇女发展的希望，将为当地妇女生活水平的改善做出更大贡献。</p><p style=\"text-align: justify;\">&nbsp;</p><p><br/></p>', '1503283559', '0', '5');
INSERT INTO `others` VALUES ('2', '华谊兄弟公益基金×乡村之眼', '民族影像教育', '民族影像教育项目', '<p style=\"text-align: justify;\">华谊兄弟公益基金与云南乡村之眼多年来有着非常密切的合作。作为推动民族文化影像记录的公益机构，乡村之眼在华谊兄弟公益基金的支持下，在云南、青海等地开展了公益放映、少数民族影像记录教学等公益项目。</p><p style=\"text-align: justify;\">从2015年起，华谊兄弟公益基金资助了在青海省第一所“零钱电影院”所在学校开设的民族影像教育培训课程。对于学校的同学们而言，有机会拿起相机，记录家乡的自然风光、传统文化和社会变化，是一种非常新鲜的体验，也对保存民族传统文化具有重要的意义。如今，参加培训的20多名同学已经开始尝试拍摄短片，他们的影片也将成为少数民族文化记录和传承的重要载体。</p><p><br/></p>', '1503283569', '0', '4');
INSERT INTO `others` VALUES ('3', '华谊兄弟公益基金×广州慧灵幼儿园', '学前融合教育示范', '学前融合教育示范项目', '<p style=\"text-align: justify;\">广州慧灵旗下幼儿园是国内最早开展融合教育的一家机构，在20多年的探索中积累了大量的融合教育实践经验。2016“ONE NIGHT 给小孩” 关爱残障儿童公益演唱会携手华谊兄弟公益基金，支持广州慧灵幼儿园学前融合教育示范项目。</p><p style=\"text-align: justify;\">在慧灵幼儿园的日常活动中，不同肤色、不同信仰、不同民族、不同身体状况的小朋友在幼儿园里一起玩耍，充分、自由地融入集体，在与其他孩子的接触交往中，认识自己，接纳别人，学习爱。目前，广州市已经从政策层面开始推动融合教育在幼儿园和小学阶段的实施，未来，融合教育也可能会在更多的地方得到接纳和实践。慧灵在融合教育方面的探索，将为家长、学校乃至全社会提供有益的经验。</p><p><br/></p>', '1503283587', '0', '3');
INSERT INTO `others` VALUES ('4', '华谊兄弟公益基金×南京世茂彩虹重症儿童安护中心', '重症孤儿临终关怀', '重症孤儿临终关怀项目', '<p style=\"text-align: justify;\">南京彩虹中心是国内为数不多的从事重症儿童舒缓疗护与临终关怀项目的机构。2016“ONE NIGHT 给小孩”关爱残障儿童公益演唱会与华谊兄弟公益基金共同支持彩虹中心重症孤儿临终关怀项目。</p><p style=\"text-align: justify;\">儿童来到彩虹中心后，由保育员提供24小时间断日常生活护理，包括孩子的一日三餐、日常活动安排、洗澡、喂药等。保育员在照料孩子的同时，需随时记录下孩子的身心状况，以保证医疗护理的跟进。此外，孩子每日所需的医疗护理服务均由获得护士资格证书的专业护士负责。在机构工作人员的精心护理下，离世儿童的预期生命平均约延长9个月。在彩虹中心，孩子们将得到更为全面的照顾与呵护，拥有更加和谐、快乐、健康的生活环境。</p><p><br/></p>', '1503283597', '0', '2');
INSERT INTO `others` VALUES ('5', '华谊兄弟公益基金×浩德蓝天之家', '孤残儿童康复治疗', '孤残儿童康复治疗项目', '<p style=\"text-align: justify;\">浩德蓝天之家成立于2004年，是一家致力于帮助孤残儿童医疗救治和康复的机构。2016“ONE NIGHT 给小孩”关爱残障儿童公益演唱会与华谊兄弟公益基金对浩德蓝天之家的孤残儿童康复治疗项目进行了支持。</p><p style=\"text-align: justify;\">浩德蓝天之家的救助儿童来源于全国各地的福利院和偏远贫困地区。依托北京优良的医疗资源，浩德蓝天之家得以为这些孤残儿童安排医学治疗，并提供术前、术后护理和照料。许多孩子在术后找到了收养家庭，开启了一段新的旅程。对于那些暂时未能找到收养家庭的孩子，机构也安排了志愿教师提供课程，使这些因身体原因不能正常入学的孩子也有机会接受启蒙教育。</p><p><br/></p>', '1503283547', '0', '6');

