-- ----------------------------
-- Table structure for strategy
-- ----------------------------
DROP TABLE IF EXISTS `strategy`;
CREATE TABLE `strategy` (
  `str_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '//攻略id',
  `str_title` varchar(100) NOT NULL COMMENT '//攻略标题',
  `str_thumb` varchar(255) NOT NULL COMMENT '//题图',
  `str_content` text NOT NULL COMMENT '//文章',
  `str_like` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '//点赞数',
  `str_hot` tinyint(1) unsigned zerofill NOT NULL COMMENT '//最热',
  `str_commend` tinyint(1) unsigned zerofill NOT NULL COMMENT '//推荐',
  `cat_id` int(11) NOT NULL COMMENT '//分类id',
  `str_time` int(11) unsigned NOT NULL COMMENT '//发布时间',
  `str_view` int(11) NOT NULL COMMENT '//查看次数',
  `str_status` tinyint(1) unsigned NOT NULL COMMENT '//删除状态',
  `user_id` int(11) unsigned NOT NULL COMMENT '//用户',
  `str_publish` tinyint(1) unsigned zerofill NOT NULL COMMENT '//是否审核',
  `index_title` varchar(100) NOT NULL COMMENT '标题分词',
  `index_content` text NOT NULL COMMENT '分词内容',
  `str_link` varchar(255) DEFAULT NULL COMMENT '//新闻外链',
  `str_guide` varchar(255) DEFAULT NULL COMMENT '新闻摘要',
  `keyword` varchar(50) DEFAULT NULL COMMENT '关键词',
  `news_time` varchar(50) DEFAULT NULL COMMENT '新闻时间',
  PRIMARY KEY (`str_id`),
  FULLTEXT KEY `title` (`index_title`,`index_content`)
) ENGINE=MyISAM AUTO_INCREMENT=84 DEFAULT CHARSET=utf8 COMMENT='文章表';

-- ----------------------------
-- Records of strategy
-- ----------------------------
INSERT INTO `strategy` VALUES ('8', '《老夫子》举办儿童公益首映', '/uploads/images/GmBHDfhnvb1502866110.jpg', '<p style=\"text-align: justify;\">2011年1月24日上午，由华谊兄弟与老夫子哈媒体共同出品的电影《老夫子之小水虎传奇》举行了特别意义的“公益首映”，邀请同心实验学校、北京木兰花开文化发展中心、白云路小学等近百名师生共同参加。</p><p><br/></p><p style=\"text-align: justify;\">参加放映的学生大多是留京过年的农民工子弟，他们中的很多人从没有机会走进真正意义上的影院，更没有机会能与城市里的孩子同步享受到优秀电影。这次公益首映让他们有机会提前看到自己喜欢的动画片《老夫子》，孩子们都感到非常高兴。当天，华谊兄弟总裁王中磊更亲自带队《老夫子》明星配音团的蔡康永和李晨，和孩子们一起观影，并为孩子们提前庆贺春节。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1496840711393824.png\" title=\"1496840711393824.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 12px;\">农民工子弟第一次走进电影院，纷纷合影留念</span></p><p>&nbsp;</p><p style=\"text-align: justify;\">首次观看《老夫子》全片的蔡康永，更是在影片开始前就来到影院，悄悄坐在孩子们中间。观影结束后，蔡康永和李晨与孩子们现场互动，两位明星现场重现电影中的台词，小朋友们很快就猜到了他们配音的角色，最后老夫子和大番薯两樽玩偶的现身，让气氛更加热闹。电影制片人、华谊兄弟总裁王中磊先生还代表出品方给每位孩子发送了新年礼物——《老夫子》的电影衍生品如红包袋、小水虎面具等，他表示“今天这场观影就是《老夫子》的首映礼，这部电影是为孩子们拍的，今天的这些孩子还没有机会进过电影院，今天我们把他们接过来，希望能给孩子们留下美好记忆。今后这种形式我们还会陆续做，让更多的孩子们看到好电影”。“华谊兄弟从今年开始，会拿出很多精力在公益方面，今天迈出了一小步，希望我们能汇聚更多的力量，让更多的孩子们享受到电影的魅力 。”</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">在下午的发布会上，王中磊进一步表示：“这是华谊兄弟第一次尝试动画电影，我们做的很努力很用心，希望孩子们能喜欢。”此外他更现场透露：“此次参与《老夫子》配音的明星，都是无偿配音，一切为了孩子”。蔡康永则表示：“看到小朋友们笑声连连，对电影里的桥段都心领神会，我很高兴，尽管配音时连着工作八小时，最后连嗓子都发不出声，但一切都很值得。”</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1496840729511737.png\" title=\"1496840729511737.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 12px;\">王中磊、蔡康永、李晨 与打工子弟合影</span></p><p>&nbsp;</p><p><br/></p>', '0', '0', '0', '6', '1502866458', '0', '0', '0', '0', '', '', null, '由华谊兄弟与老夫子哈媒体共同出品的电影《老夫子之小水虎传奇》举行了特别的“公益首映”。 ', '华谊兄弟，公益，首映', '2011-01-24');
INSERT INTO `strategy` VALUES ('15', '王中军：我要用画笔，为孩子的梦想添点儿色彩', '/uploads/images/7k8ER0kyNc1502782924.jpg', '<p style=\"text-align: justify;\">提及王中军，企业界人士往往最先想到的是“华谊兄弟的创始人”、“成功企业家”这样的标签；而在艺术圈里，人们已经开始认识作为画家的王中军：2015年4月，王中军举办名为“作为画家的王中军——王中军个展”，这也是他首次以画家的身份出现在大众面前，与所有人分享自己多年的艺术创作。在艺术家身份的背后，王中军的另一重身份和创作的驱动力，却一直鲜为人知。</p><p><br/></p><p style=\"text-align: center; line-height: normal;\"><img src=\"/uploads/ueditor/php/upload/image/1502791195441490.png\" title=\"1502791195441490.png\" alt=\"image.png\"/></p><p style=\"text-align: center; line-height: normal;\"><strong><span style=\"font-size:16px;font-family:宋体;border:none windowtext 1px;padding:0\"><br/></span></strong></p><p style=\"text-align: center;\"><strong>公益画家与“零钱电影院”</strong></p><p><span style=\"font-family: 宋体; border: 1px none windowtext; padding: 0px;\"><br/></span></p><p style=\"text-align: justify;\">“当时画画的主要动力是为了我们的华谊兄弟公益基金，买我画的人很多，就继续画，为公益、为慈善做贡献。”2015年，华谊兄弟董事长王中军在媒体专访中回忆道。</p><p><br/></p><p style=\"text-align: justify;\">自孩提时代开始，王中军就热爱艺术，喜爱绘画，有着敏锐的审美感知力。然而，囿于时代原因，高中毕业后他不得不选择参军，复原后才获得机会进入大学学习绘画。后来种种因缘际会，出国、创业、引领华谊兄弟成为第一家上市的影视企业……可以说，王中军事业上的成就和幼年时期的艺术启蒙有着深厚的渊源。再度拾起画笔时，王中军已逾不惑之年，对于他来说，绘画所承载的意义，已经不是未尽之梦的延续，而是新的梦想接力。</p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: justify;\">2011年，“华谊兄弟公益基金”正式成立，其核心项目是“零钱电影院”。据华谊兄弟公益基金负责人介绍，零钱电影院计划的灵感来源于电影《天堂电影院》。片中讲述了意大利西西里岛小村庄中的一家电影院，照耀了小男孩萨尔瓦多的人生路，并最终引领他走出乡村，成为一位大导演。华谊兄弟公益基金希望让零钱电影院成为“一个奇特的、让孩子接受艺术与文化启蒙的地方”。在全国，有基数庞大的各地贫困学校、打工子弟学校的孩子们，“零钱电影院”希望成为他们的“电影梦空间”——利用学校周边一些废弃不用的场地，或者是周边的文化馆，改造成一个“影院+”领地；除了公益放映之外，孩子们可以在这里看电影、读书、绘画、做与梦想有关的主题活动，引领孩子从心底萌生对知识的渴望和美好事物的热爱，给予他们改变和冒险的勇气。</p><p style=\"text-align: justify;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><br/></span></p><p style=\"text-align: center; line-height: normal;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><img src=\"/uploads/ueditor/php/upload/image/1502791338470403.png\" title=\"1502791338470403.png\" alt=\"image.png\"/></span></p><p style=\"line-height: normal;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><br/></span></p><p style=\"text-align: justify;\">2013年，王中军在元·空间举办“中军和他的朋友们”公益画展，邀请打工子弟学校的学生现场参观，王中军也现场跟孩子分享绘画的感受。那次画展绘画作品义卖所得全部都捐给华谊兄弟公益基金，此举能让更多的贫困儿童和打工子弟的孩子们能够在学校的“零钱电影院”里看到好电影，获得更多的乐趣。</p><p style=\"text-align: justify;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><br/></span></p><p style=\"text-align: center; line-height: normal;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><img src=\"/uploads/ueditor/php/upload/image/1502791380823864.png\" title=\"1502791380823864.png\" alt=\"image.png\"/></span></p><p style=\"line-height: normal;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><br/></span></p><p style=\"text-align: center; line-height: normal;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><img src=\"/uploads/ueditor/php/upload/image/1502791393261262.png\" title=\"1502791393261262.png\" alt=\"image.png\"/></span></p><p style=\"line-height: normal;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><br/></span></p><p style=\"text-align: center; line-height: normal;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><img src=\"/uploads/ueditor/php/upload/image/1502791405262420.png\" title=\"1502791405262420.png\" alt=\"image.png\"/></span></p><p style=\"line-height: normal;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><br/></span></p><p style=\"text-align: justify;\">2015年4月，王中军举办首次公开个展，个展中的画作都继续以义卖的形式出售，并将全部善款捐赠给华谊兄弟公益基金，用于贫困儿童的电影启蒙教育。这次展览大部分作品是新作，所售的报酬也作为善款全部捐赠。截至2016年5月，王中军已通过义卖画作为华谊兄弟公益基金筹款近两千万元。</p><p style=\"text-align: justify;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><br/></span></p><p style=\"text-align: center;\"><strong>《有一天》——每个孩子都有梦想的权利</strong></p><p style=\"text-align: center;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"line-height: 150%; font-size: 16px; border: 1px none windowtext; padding: 0px;\"><br/></span></strong></span></p><p style=\"text-align: justify;\">2014年，华谊兄弟公益基金拍摄了中国第一部真正意义上的公益电影《有一天》，并于9月在全国院线同步上映。这部众多明星零片酬出演、华谊兄弟零分账的公益电影，赢得了社会公众的一致关注与好评，同时也唤起了大众对特殊儿童群体的理解与关爱。</p><p style=\"text-align: justify;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><br/></span></p><p style=\"text-align: center; line-height: normal;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><img src=\"/uploads/ueditor/php/upload/image/1502791466140699.png\" title=\"1502791466140699.png\" alt=\"image.png\"/></span></p><p style=\"line-height: normal;\"><span style=\"border: 1px none windowtext; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; font-size: 16px; padding: 0px;\"><br/></span></p><p style=\"text-align: justify;\">《有一天》由9个温暖励志的故事组成，讲述9个特殊儿童追梦的电影。世界九大影展齐邀参加，在两大电影节获奖，并在联合国总部特别放映。王中军作为出品人亲力亲为，站台为电影做宣传，鼓励更多的社会力量关注并帮助特殊儿童和贫困儿童的成长。</p><p style=\"text-align: justify;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: justify;\">零钱电影院自创建之初，王中军就积极带着华谊明星为零钱电影院捐赠善款，更发动冯绍峰等明星和员工自愿去零钱电影院当“公益放映员”，通过明星的影响力给孩子传递改变人生的正能量。久而久之，这些善行也渐渐在华谊员工当中形成一种价值理念——“帮助他人，人生才能更有意义”。华谊兄弟年会的抽奖环节中，先为华谊兄弟公益基金预留善款也几乎成为了一种年会惯例。</p><p style=\"text-align: justify;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: center;\"><strong>公益行，在路上</strong></p><p><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-size: 16px; font-family: 宋体; border: 1px none windowtext; padding: 0px;\"><br/></span></strong></span></p><p style=\"text-align: center; line-height: normal;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-size: 16px; font-family: 宋体; border: 1px none windowtext; padding: 0px;\"><img src=\"/uploads/ueditor/php/upload/image/1502791535827699.png\" title=\"1502791535827699.png\" alt=\"image.png\"/></span></strong></span></p><p style=\"line-height: normal;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-size: 16px; font-family: 宋体; border: 1px none windowtext; padding: 0px;\"><br/></span></strong></span></p><p style=\"text-align: center; line-height: normal;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-size: 16px; font-family: 宋体; border: 1px none windowtext; padding: 0px;\"><img src=\"/uploads/ueditor/php/upload/image/1502791545865506.png\" title=\"1502791545865506.png\" alt=\"image.png\"/></span></strong></span></p><p style=\"line-height: normal;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"font-size: 16px; font-family: 宋体; border: 1px none windowtext; padding: 0px;\"><br/></span></strong></span></p><p style=\"text-align: justify;\">王中军对公益慈善的热忱不只体现在扶持华谊兄弟自己的项目上。2015年的芭莎明星慈善夜上，王中军无偿捐赠作品《白色大茶花系列之四》，最后拍得400万元的高价，全部用于公益事业。</p><p style=\"text-align: justify;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: center; line-height: normal;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502791573949608.png\" title=\"1502791573949608.png\" alt=\"image.png\"/></span></p><p style=\"line-height: normal;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: justify;\">今年是华谊兄弟公益基金成立5周年，王中军希望，通过自己的不懈创作和各方力量的共同推动，在年底之前建成100所“零钱电影院”，让电影点亮更多孩子的梦想，令他们看到广阔多彩的世界。</p><p style=\"text-align: justify;\"><span style=\"border: 1px none windowtext; line-height: 150%; font-size: 16px; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: center;\">最后祝福所有小朋友们，六一儿童节快乐！</p><p style=\"text-align: center;\"><br/></p>', '0', '1', '0', '4', '1502965624', '0', '0', '0', '1', '', '', null, '王中军希望，通过自己的不懈创作和各方力量的共同推动，让电影点亮更多孩子的梦想，令他们看到广阔多彩的世界。                ', '王中军；艺术；公益', '2016-06-01');
INSERT INTO `strategy` VALUES ('9', '公益放映走进大凉山', '/uploads/images/5rNIu0HsiH1497924484.jpg', '<p>2011年3月中旬，华谊兄弟公益基金走进大凉山做项目考察，并为索玛花爱心学校的孩子们放映了露天电影，凉山的孩子们第一次看电影，被跳动的画面深深吸引。露天电影带给我们的是美好回忆，带给山里的孩子们的更多是新鲜，全校三百多名彝族小学生围坐在操场上盼着。一年级的小朋友，刚刚开始学汉语，不太能看得懂，但依然边看边笑，动画片就是有这样的魔力。</p><p>放映队的工作人员说，他们一年中大半年都是在各村落中度过，条件艰苦，在这样的条件下依然坚持传递美好，应该向他们致敬。学校校长说，要不是我们来，放映队很难走进学校，不是不关注这里，一年3269场乡村放映，对于11台长途跋涉的放映车，确实力不从心。我们来了，很好。</p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1496841053584712.png\" title=\"1496841053584712.png\" alt=\"image.png\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1496841087446706.png\" title=\"1496841087446706.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1496841108706360.png\" title=\"1496841108706360.png\" alt=\"image.png\"/></p>', '0', '1', '1', '4', '1497969646', '0', '1', '0', '1', '', '', null, '2011年3月中旬，华谊兄弟公益基金走进大凉山做项目考察，并为索玛花爱心学校的孩子们放映了露天电影。   ', null, null);
INSERT INTO `strategy` VALUES ('10', '华谊兄弟公益基金成立 启动零钱电影计划', '/uploads/images/1ygYYbq2Kt1497924468.jpg', '<p>6月1日，华谊兄弟公益基金在京举行启动仪式。华谊兄弟公益基金是由华谊兄弟的明星和员工共同发起，在中华思源工程基金会的支持和管理下设立的专项公益基金。<br/></p><p>&nbsp;</p><p>　　王中磊公布“零钱电影计划”&nbsp;</p><p style=\"text-align:center\"><br/></p><p>　　华谊兄弟总裁王中磊公布了第一个公益梦想——“零钱电影计划”，核心公益产品是分布在各个贫困学校中、寓教于乐的“零钱电影院”。除完整的电影放映设备外，影院内还将有儿童课外图书、电脑、互联网等配套设施，让孩子能通过电影获得课本外的知识。“零钱电影计划”传达的公益观念是：小零钱大爱心，搭建孩子的电影天堂。作为全娱乐传媒集团，华谊兄弟也在积极尝试探索一条公益文化之路。</p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1496841202573058.png\" title=\"1496841202573058.png\" alt=\"image.png\"/></p><p>　　王中磊表示：“华谊兄弟在做公益方面是个新人，每一步都要脚踏实地。华谊兄弟公益基金在挑选公益项目方向时，经过多次的摸索和讨论，最终确定在电影与儿童的结合。‘零钱电影计划’也就此诞生。一个企业做公益，用其自身最大的资源去最大限度地帮助更多人，是负责任的。儿童与电影是最适合我们的，电影对儿童的影响是深远的。电影也是华谊人都能发挥作用的。华谊兄弟公益基金倡导可持续的公益行为，从每个人力所能及的事做起，哪怕我们每年为孩子们坚持做一件事，都是好的。”</p><p>&nbsp;</p><p>当日，王中磊也表示：“华谊兄弟公益基金的发起人是华谊兄弟，但这个基金是一个公众的平台，希望和愿意加入进来的朋友，一起努力，实现公益梦想未来。”相信也会有越来越多的伙伴力量加入进来，大手拉小手，把爱传下去，一个人的承诺，带动更多人的关注，就可以有所改变。</p>', '0', '0', '1', '4', '1497924468', '0', '1', '0', '1', '', '', null, '6月1日，华谊兄弟公益基金在京举行启动仪式。华谊兄弟公益基金是由华谊兄弟的明星和员工共同发起，在中华思源工程基金会的支持和管理下设立的专项公益基金。    ', null, null);
INSERT INTO `strategy` VALUES ('14', '“ONE NIGHT”公益晚会成功举办 华谊兄弟践行“公益+”新模式', '/uploads/images/eBuKqHoOgN1502876629.JPG', '<p style=\"text-align: justify;\">由周迅发起的“关爱残障ONENIGHT公益晚会”本月16日在北京举行，今年的主题是“关爱残障儿童成长与发展”。演唱会所获得的相关收益，除去本次演出成本后，全部捐赠给华谊兄弟公益基金，用于心智障碍儿童融合教育、孤残儿童康复治疗、重症孤儿临终关怀项目。</p><p><br/></p><p style=\"text-align: justify;\">今年“ONENIGHT给小孩”公益演唱会邀请到许巍、周笔畅两位资深实力唱将，德云社“hiphop担当”岳云鹏与金牌搭档孙越，作为演唱会相声嘉宾，在现场负责起了搞笑担当的角色；而去年参与“ONE NIGHT”的台湾主持人阿雅，今年也继续助阵并担任主持人。此外，周迅携手周笔畅同台献唱，为公益发声，更是引爆全场。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">晚会上最温暖的表演当属，中国第一支“融合教育”的合唱团——华谊兄弟公益基金创办的“有一天合唱团”登台献唱的主题曲《给小孩》，将开场气氛推向高潮。合唱团的孩子们来自不同背景，有残障儿童、聋哑儿童、服刑人员子女、普通的孩子，在合唱团里彼此接纳、包容、一起成长。 “有一天”合唱团曾经在2015年登上过联合国70周年音乐会，将“融合教育”的理念传播到世界。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497668213968523.jpg\" width=\"502\" height=\"320\" border=\"0\" vspace=\"0\" title=\"\" alt=\"\" style=\"width: 502px; height: 320px;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">“有一天合唱团”</span></p><p><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: justify;\">“ONE NIGHT”公益演唱会的举办，源于华谊兄弟益基金筹拍的公益电影《ONE DAY》（《有一天》）(以下简称《ONE DAY》)。谈及发起&quot;ONENIGHT&quot;的初衷，周迅表示，《ONE DAY》是华谊兄弟公益基金拍摄的一部关爱特殊儿童群体的公益电影，她自己也出演了九个故事中的一个，温暖的故事和公益理念让她希望能为这些孩子做更多，对应着“ONE DAY”就有了“ONE NIGHT”。让所有的孩子都可以免费看到好电影，拥有一个美好的童年和未来，这也是举办‘ONE NIGHT’的意义所在。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497668205856489.jpg\" width=\"437\" height=\"280\" border=\"0\" vspace=\"0\" title=\"\" alt=\"\" style=\"width: 437px; height: 280px;\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">“有一天合唱团”的孩子们在演出结束后获得奖状</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><br/></span></p><p style=\"text-align: justify;\">继2014年“关爱九种特殊儿童”和2015年“关爱自闭症儿童”后，今年是第三年举办“ONENIGHT”公益演唱会。华谊兄弟公益基金再次成为“ONE NIGHT”的合作伙伴，其专业度及公信力也成为公益行业的典范。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497668190436602.jpg\" width=\"459\" height=\"320\" border=\"0\" vspace=\"0\" title=\"\" alt=\"\" style=\"width: 459px; height: 320px;\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">华谊兄弟公益基金负责人与周迅共同庆祝演唱会成功举办</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><br/></span></p><p style=\"text-align: justify;\">华谊兄弟公益基金自2011年正式启动以来，已经走过了爱与希望的5年：截止到2015年，先后在全国设立了61所“零钱电影院”；举办了五届“小小画展”；与联合国五度携手合作，支持中国公益项目；为天津8.12爆炸事件捐款；参与雅安灾后重建……华谊兄弟公益基金不仅惠及了大量而广泛的受助者，也为中国草根公益组织的发展贡献力量。今年“ONE NIGHT”的善款将由华谊兄弟公益基金进行专业化、透明化的管理。华谊兄弟公益基金负责人孙阿美表示：“公益不仅需要专注，还需要团结更多的伙伴力量一起行动起来。”</p><p style=\"text-align: justify;\"><br/></p>', '0', '0', '0', '4', '1502939009', '0', '0', '0', '1', '', '', null, '由周迅携手华谊兄弟公益基金发起的“关爱残障ONENIGHT公益晚会”本月16日在北京举行，演唱会所获得的相关收益，除去本次演出成本后，全部捐赠给华谊兄弟公益基金，用于心智障碍儿童融合教育、孤残儿童康复治疗、重症孤儿临终关怀项目。     ', '华谊兄弟，公益，有一天合唱团，ONE NIGHT', '2016-07-16');
INSERT INTO `strategy` VALUES ('11', '群星闪耀华谊ELLE之夜 为公益儿童项目呐喊', '/uploads/images/LMTJiJCVRH1498071429.jpg', '<p style=\"text-indent: 2em; line-height: 2em;\">6月12日，由华谊兄弟时尚文化传媒与《ELLE世界时装之苑》联手主办的“2011星光上海——华谊ELLE之夜”在毗邻东外滩的全新时尚地标——上海国际时尚中心盛大举办。华谊兄弟传媒股份有限公司总裁王中磊先生、华谊兄弟时尚文化传媒董事总经理赵磊先生、桦榭中国董事总经理艾弥尔先生、桦榭中国出版总经理杨玟女士、ELLE编辑总监晓雪女士、上海市杨浦区副区长唐海东等共同到场庆祝这一娱乐与时尚界的盛事。</p><p style=\"text-indent: 2em;\">现场红毯星光熠熠，李冰冰、刘晓庆、高圆圆、安以轩、霍思燕、张涵予、那英、桂纶镁、邓超、李晨等一众名星悉数亮相，名贵汽车与惊艳明星使现场高潮迭起，气氛不断升温。ELLE TV APP更在华谊之夜全程直播，尊享60多位明星齐聚的奢华盛宴。</p><p style=\"text-indent: 2em;\">当天新闻发布会，王中磊正式宣布了华谊兄弟全新子公司华谊兄弟时尚传媒有限公司的成立，这也预示着国内影视娱乐巨头正式进军时尚界，王中磊表示：“华谊兄弟一直关注于中国时尚业的发展，时刻掌握国内外时尚动态。在过去的几年里，华谊旗下的艺人积极参与时尚活动；同时，我们也成功举办了在文化艺术和时尚潮流界都颇具影响力的‘华谊之夜’等盛会。在电影、电视、唱片、主持、广告等领域，华谊的艺人均展现出现代、时尚、健康的形象，我认为华谊进军时尚业是水到渠成的事。”</p><p style=\"text-indent: 2em;\">而对于此次华谊兄弟时尚与ELLE杂志的强强联手，桦榭中国集团董事总经理艾弥尔表示：“这次的星光上海——华谊ELLE之夜，开创了国际高端女性杂志与顶尖影视时尚公司的合作新模式，在共享资源、扩展影响力的同时，我们也深刻意识到作为各自领域的佼佼者，我们肩负着重要的公益使命与社会责任，这也是为何此次活动加重了公益的环节，并希望所有来宾以及观众都能积极投身到关爱儿童的公益项目之中。”</p><p style=\"text-indent: 2em;\">自今年华谊ELLE之夜宣布设立公益环节以来，吸引了诸多品牌的鼎力支持，各大品牌纷纷献出各自经典产品，在MY ELLE PIECE上慷慨义卖，并将所得款项全数捐于今年儿童节正式成立的“华谊兄弟公益基金”，为贫困儿童建立“零钱电影院”；其中，来自西藏的高端天然矿泉水品牌夏木拉更当场宣布捐赠义卖等值金额给予“华谊兄弟公益基金”，携手“华谊ELLE之夜”一同在北京落实建立一个零钱电影院。</p><p style=\"text-indent: 2em;\">“ELLE是进入中国的第一本国际高端女性大刊，在风尚领域，电影和时装一直互相激发灵感与火花，这也是我们此次携手华谊的初衷。此外，ELLE一直致力于推广Love Kids的爱心项目，在我们杂志的6月刊上可以看到许多明星身穿我们ELLE Love Kids特别T恤拍摄富有爱心的大片，华谊兄弟公益基金也恰好在这时推出，可以说是孩子、是公益让我们两家走到了一起。”ELLE编辑总监晓雪如是说，“我真切地希望大家都来关注这个项目，从一点点小事做起，就像华谊兄弟公益基金的标语一样：‘关注就是力量’！”</p><p style=\"text-indent: 2em;\">与以往的“华谊之夜”相比，今年的华谊ELLE之夜处处体现出“星光上海”的群星汇聚与摩登时尚。红毯之后的时装秀精彩纷呈。华谊时尚旗下国际超模秦舒培、奚梦瑶，港台名模Lisa.S、Ana.R、林嘉绮等同场走秀、争奇斗艳，堪称近年来罕见的超豪华走秀阵容，由此体现了华谊时尚的超模阵容。此次华谊时尚特别邀请来自纽约的制作团队，为观众及时装迷们呈上了一场极富创意、惊喜连连的时尚盛宴；国内音乐才子李泉与来自丹麦的知名DJ Johannas Thrope跨国界表演令在场所有来宾及媒体啧啧称赞。</p><p style=\"text-indent: 2em;\">作为上海国际电影节期间的指定活动，2011星光上海——华谊ELLE之夜不忘其初衷，商界名流、明星名人、文化权威，照例在这每年一度的集体派对上欢聚一堂。华谊时尚董事总经理赵磊说：“华谊之夜今年已经是第4个年头，我们看着它一步步成长，从最初华谊兄弟的内部联谊到如今成为一场有主题、有内容，并肩负社会责任感和公益影响力的盛会，这是我们大家都为之欣喜的。相信今后的华谊ELLE之夜会更成功，也希望有更多的新朋友加入进来！”</p><p style=\"text-indent: 2em;\">仲夏之夜，黄浦江畔，华谊兄弟时尚与ELLE杂志为我们带来了一场充满爱的时尚盛宴，正如活动主题：“Style with love&quot;，有爱心，世界更有型！华谊ELLE之夜，期待来年再见！</p><p><br/></p>', '0', '1', '1', '4', '1500006862', '0', '1', '0', '1', '', '', null, '自今年华谊ELLE之夜宣布设立公益环节以来，吸引了诸多品牌的鼎力支持，各大品牌纷纷为华谊兄弟公益基金捐款。        ', '明星公益；华谊；华谊兄弟', null);
INSERT INTO `strategy` VALUES ('12', '北京民政部门将设专人管理慈善组织 推动公务人员退出', '/uploads/images/zcKqR0UBaU1497081241.gif', '<p>本报讯（记者 赵颖彦）本市今年将推动公务人员逐步退出慈善公益组织。今天上午，记者从首都慈善公益组织联合会获悉，此举有利于还社会组织民间化的本色。为了完成对接，今年各区县的民政局内也将设专人管慈善。</p><p>慈善公益组织最重要的特点是自愿性、社会性和民间性，政府和慈善公益组织的职能应该明确区分开来。记者了解到，目前北京市民政局已专门成立了慈善处来负责慈善工作。为了工作顺利对接，本市各区县年内也将在民政局设专门科室并由专人负责慈善政策落实、搭建平台、整合慈善资源等多项工作。</p><p>据了解，此前各区县的慈善工作基本全落到北京市慈善协会的“肩上”。</p><p>北京市民政局慈善工作处处长程立岩表示，慈善协会仅仅是一个慈善公益组织，更多的只是负责具体的项目运作。</p><p>但随着慈善事业的进一步发展，政府和慈善公益组织的职能应该更加分明：慈善组织负责募捐、项目运作等具体的业务；政府则是担当规则“策划者”，负责制定、落实慈善政策，搭建公益平台、整合公益慈善资源等。</p><p>因此，今年将推动公务人员逐步退出慈善组织，还原其民间化的本色。</p><p>此外，今年本市还将构建覆盖全市16个区县的慈善组织网络。目前本市各区县的社会捐助站点还处于独立运行状态，未来本市将在此基础上建成一个统一的慈善组织网络，以此改变当前运动式的慈善公益活动模式，并方便公众参与和监督各项慈善活动。</p><p><br/></p>', '0', '1', '0', '7', '1497176885', '0', '1', '0', '1', '', '', 'http://news.ifeng.com/mainland/detail_2012_03/19/13294689_0.shtml', null, null, null);
INSERT INTO `strategy` VALUES ('16', '华谊兄弟公益与联合国五度合作 助阵“全球首次大熊猫艺术巡展”', '/uploads/images/t2hJKboHcG1497682191.jpg', '<p style=\"text-align: left;\">2016年5月26日，由联合国开发计划署、成都大熊猫繁育研究基地、华谊兄弟公益基金等联合举办的“全球首次大熊猫艺术巡展-北京站”于北京自然博物馆开幕，这是华谊兄弟公益基金与联合国的第五次合作。由华谊兄弟公益基金创立的“有一天合唱团”受邀参加开幕仪式，并向世界表达了特殊儿童群体对大熊猫的喜爱之情。</p><p style=\"text-align:center\"><img src=\"http://www.hbfoundation.cn/Uploads/%E2%80%9C%E6%9C%89%E4%B8%80%E5%A4%A9%E5%90%88%E5%94%B1%E5%9B%A2%E2%80%9D%E5%9C%A8%E5%B1%95%E8%A7%88%E5%9C%BA%E5%9C%B0%E5%89%8D%E5%90%88%E5%BD%B1(1).jpg\" width=\"400\" height=\"267\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">开幕式现场，“有一天合唱团”的小朋友与“联合国开发计划署熊猫明星使者”、“成都熊猫大使”袁姗姗共同创作一幅大熊猫画作，将“联合国开发计划署动物形象大使”的卡通形象展现在画纸上，用画笔表达对保护大熊猫的关注和支持。该幅作品随后在现场展出，在巡展结束后将被成都大熊猫繁育研究基地收录。</p><p style=\"text-align:center\"><img src=\"http://www.hbfoundation.cn/Uploads/%E8%A2%81%E5%A7%97%E5%A7%97%E4%B8%8E%E2%80%9C%E6%9C%89%E4%B8%80%E5%A4%A9%E5%90%88%E5%94%B1%E5%9B%A2%E2%80%9D%E7%8E%B0%E5%9C%BA%E5%88%9B%E4%BD%9C.JPG\" width=\"400\" height=\"267\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">“有一天合唱团”代表与“联合国开发计划署熊猫明星使者”、</p><p style=\"text-align: left;\">“成都熊猫大使”袁姗姗现场创作大熊猫画作</p><p style=\"text-align: left;\">为了迎接“全球首次大熊猫艺术巡展”落地北京，“有一天合唱团”成员独立创作七幅绘画作品，展现了他们心目中的大熊猫形象，成员们兴奋的与自己的画作合影。这些画作与知名艺术家们的艺术作品共同展出，在参观其他作品之后，小朋友们对艺术创作又有了新的认识。</p><p style=\"text-align:center\"><img src=\"http://www.hbfoundation.cn/Uploads/%E2%80%9C%E6%9C%89%E4%B8%80%E5%A4%A9%E5%90%88%E5%94%B1%E5%9B%A2%E2%80%9D%E6%88%90%E5%91%98%E4%B8%8E%E8%87%AA%E5%B7%B1%E7%9A%84%E7%94%BB%E4%BD%9C%E5%90%88%E5%BD%B1(2).JPG\" width=\"400\" height=\"267\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">“有一天合唱团”成员与自己的画作合影</p><p style=\"text-align:center\"><img src=\"http://www.hbfoundation.cn/Uploads/%E2%80%9C%E6%9C%89%E4%B8%80%E5%A4%A9%E5%90%88%E5%94%B1%E5%9B%A2%E2%80%9D%E6%88%90%E5%91%98%E6%AC%A3%E8%B5%8F%E5%90%8D%E5%AE%B6%E8%89%BA%E6%9C%AF%E5%93%81.jpg\" width=\"400\" height=\"300\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">“有一天合唱团”成员欣赏名家艺术品</p><p style=\"text-align:center\"><img src=\"http://www.hbfoundation.cn/Uploads/%E2%80%9C%E6%9C%89%E4%B8%80%E5%A4%A9%E5%90%88%E5%94%B1%E5%9B%A2%E2%80%9D%E6%88%90%E5%91%98%E6%BC%94%E5%94%B1%E3%80%8A%E7%86%8A%E7%8C%AB%E5%92%AA%E5%92%AA%E3%80%8B(2).JPG\" width=\"400\" height=\"267\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">“有一天合唱团”成员演唱《熊猫咪咪》</p><p style=\"text-align: left;\">在开幕仪式现场，“有一天合唱团”清唱一曲《熊猫咪咪》，赢得了现场的热烈掌声。华谊兄弟公益基金希望通过“有一天合唱团”以歌声和绘画的形式向世界表达特殊儿童群体的美好心愿，帮助他们融入社会这个大家庭。</p>', '0', '1', '0', '1', '1497682888', '0', '0', '0', '1', '', '', null, '2016年5月26日，由联合国开发计划署、成都大熊猫繁育研究基地、华谊兄弟公益基金等联合举办的“全球首次大熊猫艺术巡展-北京站”于北京自然博物馆开幕，这是华谊兄弟公益基金与联合国的第五次合作。由华谊兄弟公益基金创立的“有一天合唱团”受邀参加开幕仪式，并向世界表达了特殊儿童群体对大熊猫的喜爱之情。', null, null);
INSERT INTO `strategy` VALUES ('17', '王中磊和威廉弟弟再回云南，这次，他们见证了暖心改变', '/uploads/images/Sa7Hc4RyhI1502786549.jpg', '<p style=\"text-align: justify;\">&nbsp;2016年4月1日，华谊兄弟副董事长兼总经理王中磊先生与家人随华谊兄弟公益基金的工作人员专程前往云南拜访云南芒市西山乡营盘民族小学的师生们，此行除了对学校进行回访，王中磊一行还特地参观了由他个人出资捐建的学校卫生设施。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502786293940356.png\" title=\"1502786293940356.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><img src=\"http://www.hbfoundation.cn/Uploads/%E4%B8%89%E5%8F%A3.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/><span style=\"font-size: 14px;\">西山乡营盘民族小学，这里的学生以景颇族居多<br/></span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502786308165480.png\" title=\"1502786308165480.png\" alt=\"image.png\"/><img src=\"http://www.hbfoundation.cn/Uploads/%E7%88%B6%E5%AD%90.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">父子俩再次同行献爱心</span></p><p><br/></p><p style=\"text-align: justify;\">这是王中磊和威廉弟弟第二次来到云南芒市西山乡营盘民族小学。2014年5月，他们带着《爸爸回来了》摄制组第一次来到这里。当时，学校的孩子们还在用水井里的水洗澡，学校还是清扫不便的旱厕，基础设施的落后让孩子们的生活存有诸多不便。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">目睹了这里简陋的生活条件，王中磊和威廉弟弟想着为他们做点什么，于是在那次造访之后，王中磊先生个人出资捐建了学校的卫生设施，希望师生们能在更好的条件下学习、生活。时隔两年，当他们再次到来，崭新的澡堂和厕所刚刚竣工。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502786320962565.png\" title=\"1502786320962565.png\" alt=\"image.png\"/><img src=\"http://www.hbfoundation.cn/Uploads/%E8%87%AA%E6%9D%A5%E6%B0%B4.png\" width=\"500\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">配备自来水设施的全新澡堂，让孩子们从此告别了用井水洗澡的历史</span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502786333879564.png\" title=\"1502786333879564.png\" alt=\"image.png\"/><img src=\"http://www.hbfoundation.cn/Uploads/%E6%BE%A1%E5%A0%82.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">澡堂里整齐的储物柜和洁净的环境</span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502786348673543.png\" title=\"1502786348673543.png\" alt=\"image.png\"/><img src=\"http://www.hbfoundation.cn/Uploads/girl.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">崭新的卫生设施，男生/女生分别用蓝色红色点缀区分</span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502786359743873.png\" title=\"1502786359743873.png\" alt=\"image.png\"/><img src=\"http://www.hbfoundation.cn/Uploads/boy.jpg\" width=\"500\" height=\"280\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">卫生设施的落成，让师生们有了更好的生活条件</span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align: justify;\">2014年5月，王中磊和威廉弟弟第一次来到学校，为孩子们担任乡村电影放映员，威廉弟弟还和爸爸根据观看电影的灵感创作了绘画作品。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502786371322749.png\" title=\"1502786371322749.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><img src=\"http://www.hbfoundation.cn/Uploads/%E7%88%B6%E5%AD%90%E4%BF%A9.jpg\" width=\"500\" height=\"375\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/><span style=\"font-size: 14px;\">父子俩第一次来到云南的“零钱电影院”，放电影、画电影<br/></span></p><p style=\"text-align: center;\"><span style=\"text-align: center;\"><br/></span></p><p style=\"text-align: justify;\">截至目前，华谊兄弟公益基金即将成立五周年，足迹已遍布全国各地的贫困地区，已搭建了61所“零钱电影院”，让贫困儿童可以在电影的陪伴下成长。</p><p style=\"text-align: justify;\"><img src=\"http://www.hbfoundation.cn/Uploads/%E6%A2%A6.jpg\" width=\"500\" height=\"521\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><strong>“零钱电影院”是孩子们梦开始的地方</strong></p><p style=\"text-align: center;\"><br/></p><p style=\"text-align: justify;\">华谊兄弟公益基金除了搭建“零钱电影院”，还会拿出更多时间陪伴学校成长、进行垂直资助，让学校在“零钱电影院”的带动下变得更好。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502786381349618.png\" title=\"1502786381349618.png\" alt=\"image.png\"/><img src=\"http://www.hbfoundation.cn/Uploads/%E6%A0%A1%E9%95%BF.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">学校校长向王中磊和基金负责人赠送民族特色锦旗</span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502786389991531.png\" title=\"1502786389991531.png\" alt=\"image.png\"/><img src=\"http://www.hbfoundation.cn/Uploads/%E7%8E%8B%E4%B8%AD%E7%A3%8A(1).jpg\" width=\"500\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">王中磊为小学新卫生设施落成剪彩&nbsp;</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502786402733399.png\" title=\"1502786402733399.png\" alt=\"image.png\"/><img src=\"http://www.hbfoundation.cn/Uploads/%E5%A4%AB%E5%A6%87.jpg\" width=\"500\" height=\"470\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">王中磊夫妇与威廉弟弟和师生们在一起</span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502786425741577.png\" title=\"1502786425741577.png\" alt=\"image.png\"/><img src=\"http://www.hbfoundation.cn/Uploads/%E4%BA%A4%E6%B5%81.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">威廉弟弟和学校的小伙伴现场交流</span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align: justify;\">和小伙伴在一起总是快乐的，威廉弟弟再次与boss老爸来到学校，喜欢踢足球的他说，下次要来和小朋友们一起比赛。</p><p style=\"text-align: justify;\"><br/></p>', '0', '1', '0', '4', '1502965504', '0', '0', '0', '1', '', '', null, '2016年4月1日，华谊兄弟副董事长兼总经理王中磊先生与家人前往云南拜访云南芒市西山乡营盘民族小学的师生们。       ', '王中磊，威廉,公益', '2016-04-01');
INSERT INTO `strategy` VALUES ('18', '应该注重电影的公益性', '/uploads/images/ISCc35whte1497924677.jpg', '<p style=\"text-align: left; text-indent: 2em;\">电影局近日发出关于进一步规范电影市场票务管理的指导意见征求意见稿，其中要求，会员票、团体票不得低于影院挂牌价格的７折。据悉，此举是为了保障电影发行和放映方的票房收入，促进电影业的良性发展。</p><p style=\"text-align: left; text-indent: 2em;\">这种初衷应当说是不错的。只是，电影终究是让人看的，如果因票价过高使进影院看电影的人越来越少，那电影业又咋能良性发展呢？</p><p style=\"text-align: left; text-indent: 2em;\">有人以１９８２年电影《少林寺》的１毛钱票价与今年《地心历险记２》的１２０元票价对比，发现电影票价３０年里涨了１２００倍。如此涨幅，堪称威猛。</p><p style=\"text-align: left; text-indent: 2em;\">好在不少电影院以会员票、团体票等方式对票价打了不少折扣，才使得观众没有与电影院“决裂”。但有网络调查显示，仍有９２％的人认为票价过高。在这样的民意之下，若真的按上述意见稿实行，则本来就不多的观众，又将有一些被逼离影院。</p><p style=\"text-align: left; text-indent: 2em;\">电影是丰富人们精神生活的重要文化产品，具有一定的公益性。中央关于深化文化体制改革的决定，以及国家“十二五”时期文化改革发展规划纲要，都提出“鼓励在电影放映中安排一定数量的低价场次或门票”。对此，我们应当努力抓好落实，让人们享到更多的电影发展之福。</p>', '0', '0', '1', '7', '1497924677', '0', '1', '0', '1', '', '', null, '电影局近日发出关于进一步规范电影市场票务管理的指导意见征求意见稿，其中要求，会员票、团体票不得低于影院挂牌价格的7折。据悉，此举是为了保障电影发行和放映方的票房收入，促进电影业的良性发展。    ', null, null);
INSERT INTO `strategy` VALUES ('19', '北京启动慈善事业立法 各届期待国家《慈善法》', '/uploads/images/jOhZ7Dv6o41497924705.jpg', '<p>日前，从北京市２０１２年民政会议上传出消息：慈善事业促进办法已纳入北京市人大立法规划，并列入市政府工作计划。未来的首都《慈善办法》应把所有在京登记的慈善公益组织都纳入监管范围，要求慈善公益组织的募捐款物信息披露必须落实到具体的慈善项目，并及时公示。有条件的慈善公益组织，还应在官方网站建立查询系统，供捐款人和公众随时查询、监督。</p><p>事实上，在国家层面的《慈善法》因为各种原因，迟迟未出台的背景下，很多地方已经纷纷开始进行尝试。</p><p>２０１０年１月２１日，《江苏省慈善事业促进条例》在江苏省第十一届人大常委会第十三次会议上获得通过，并于２０１０年５月１日开始实施，成为我国第一部地方性慈善法规，开创了慈善事业地方立法先河。《江苏省慈善事业促进条例》首次以立法的形式明确规定了慈善组织的信息披露义务和信息披露范围。</p><p>２０１０年１２月，在湖南省第十一届人大常委会第十九次会议审议通过的《湖南省募捐条例》，对募捐行为前后的信息公示作出规定，并于２０１１年５月１日起施行。</p><p>在江苏省出台《条例》后不久，广东省也启动了慈善立法工作。广东省民政厅已将《广东省慈善事业条例》草案提交广东省政府法制办，目前正处于修改讨论阶段。２月３日，２０１２年广东省民政工作会议举行，会上获悉，广东省将力争在今年出台《广东省慈善事业条例》，并明确在２０１２年５月１日前，各地级以上市要将上一年的慈善信息全部向社会公开。</p><p>据悉，《上海市社会募捐条例》目前已形成草案，相关立法程序也将启动。此外，深圳非营利组织法规亦在酝酿之中。据了解，这些尚未出台的法规纷纷将目光集中在了突破双重管理体制和建立有效监管规则之上。</p><p>对于近两年出现的地方慈善立法“春潮”，清华大学公共管理学院副教授贾西津认为：地方出台慈善法要关注它的实质内容，一方面是要看开放性，在现有的基础上有制度性的开放；一方面是对监管的细化程度，让透明化等问题更加有法可依；再一方面是促进性的制度，比如税收优惠、购买细则等。</p><p>清华大学公共管理学院创新与社会责任研究中心主任邓国胜表示，地方重视确实是好事，但国家出台慈善法才是最重要的。“各地分别出台法律法规，成本也是很高的，而且各地颁布的法规条例等也是参差不齐，慈善法有必要尽快出台，这样在全国的指导意义也更大。当然北京出台慈善法规也有其意义，国家慈善法没出台，地方在很多方面存在空白，比如谁有资格劝募，捐赠的财产怎么管理等，包括信息披露等问题，就是因为没有法律法规的依据。”尽管各界一直等待《慈善法》的早日出台，但自２００５年至今的数年等待并非毫无意义，各种当初起草时不存在的或者想不到的问题相继出现。彼时，没有人想到股权捐赠，而今，曹德旺的河仁基金开了中国慈善事业的先河，并给《慈善法》制订工作提供了一个新的议题；彼时，尚不知微博为何物，而今，数百名媒体人发起的“免费午餐”行动展示了民间慈善的生命力。</p>', '0', '1', '1', '7', '1499569172', '0', '1', '0', '1', '', '', null, '日前，从北京市２０１２年民政会议上传出消息：慈善事业促进办法已纳入北京市人大立法规划，并列入市政府工作计划。未来的首都《慈善办法》应把所有在京登记的慈善公益组织都纳入监管范围，要求慈善公益组织的募捐款物信息披露必须落实到具体的慈善项目，并及时公示。有条件的慈善公益组织，还应在官方网站建立查询系统，供捐款人和公众随时查询、监督。       ', '公益事业立法', null);
INSERT INTO `strategy` VALUES ('20', '北京：社会组织有望直接申请成立', '/uploads/images/xvtCfVGvmS1497708486.png', '<center style=\"text-align: justify;\">　　本报讯　（记者魏铭言）今年，公益慈善等类社会组织在北京登记注册，有望无须再找业务主管单位，直接向民政部门申请成立。昨天，２０１２年北京市民政工作会召开。市民政局首次提出，今年，将探索研究推动社会组织业务主管单位向业务指导单位转变。</center><p>　　探索突破社会组织双重管理</p><p>　　这一转变意味着，北京市将继广东“破冰”之后，探索突破社会组织的“双重管理”壁垒，实现真正意义上的直接登记。北京市社团办主任温庆云介绍，社会组织业务主管向业务指导转变的试点，可先从市民政局目前“一手托两家”（既做业务主管，也做登记主管）的社会组织开始。</p><p>　　社会组织登记前审批或成历史</p><p>　　我国现行的社会组织管理法规，由《社会团体登记管理条例》、《基金会管理条例》和《民办非企业单位登记管理暂行条例》组成，其中均规定，社会组织要获得“合法身份”，须先获得所在行业业务主管部门的审批，才能到民政部门登记管理机关申请登记。</p><p>　　温庆云透露，已在修订的三个社会组织行政法规今年有望获得通过，修订后的法规，有望从立法角度确立社会组织直接登记制度。社会组织登记成立前须找业务主管单位审批，将成为历史。</p><p>　　“双重管理”尚未真正破冰</p><p>　　去年２月，北京市在全国率先提出工商经济、公益慈善、社会福利、社会服务４类社会组织试点向民政部门“直接登记”，从操作层面降低社会组织的登记门槛。</p><p>　　当时，有社会组织误认为，今后申请登记不再需要业务主管部门审批。但北京市社团办相关负责人此后多次表示，北京对于社会组织的“双重管理”并未真正破冰，只是作出一些尝试，提高社会组织登记的可能性和成功率。譬如民政部门可代为寻找、协调业务主管单位，或“一手托两家”，既做业务主管单位，也做登记主管单位。</p><p>　　■　背景</p><p>　　广东率先试水“直接登记”</p><p>　　很多社会组织因找不到业务主管部门，无法在民政部门获得登记，只能在工商部门注册为企业，发展受到限制。还有不少社会组织无“合法身份”，资金募集难、无法申报政府购买服务项目。</p><p>　　去年１１月，广东省民政厅提出，从今年７月１日起，除特别规定、特殊领域外，将社会组织的业务主管单位改为业务指导单位，社会组织直接向民政部门申请成立。这也是我国率先提出摒弃社会组织“双头管理”，实现直接登记的地方改革方案。</p><p>　　去年年底，民政部部长李立国在全国民政工作会议上表示，要推广广东经验，支持有条件的地方将社会组织业务主管单位改为业务指导单位。李立国表示，民政部将继续协调、配合社会化组织三个行政法规的修订工作，做好相关衔接配套工作。</p><p>　　■　新政</p><p>　　●助孤残儿童融入家庭</p><p>　　今年，北京市将研究制定福利机构孤残儿童回归家庭、融入社会扶持政策。这意味着，将有更多孤残儿童能够被家庭合法收养。</p><p>　　●新建养老床位１万张</p><p>　　今年，北京市将新建养老床位１万张。将推进市老年社区二期、南城养老院、松鹤温泉新村建设。</p><p>　　●办养老机构将获补贴</p><p>　　通过以奖代补等形式推进养老机构星级评定，提升养老机构服务质量。对街道、乡镇政府办养老机构给予运营资助，补贴社会办养老机构新增床位开办费用。</p><p>　　■　相关新闻</p><p>　　社会组织从业者有望整体涨薪</p><p>　　全市社会组织将建最低工资标准，比照事业单位，工资纳入“工资基金”；上保险</p><p>　　本报讯　（记者魏铭言）民间组织从业者，今年起将逐步纳入由北京市民政局建立的“社会组织专职人员编制体系”，在编者的收入和保障水平，将大幅提高并建立工资增长机制。</p><p>　　社会组织工资水平有望涨一倍</p><p>　　市社团办的摸底调查显示，目前，北京社会组织从业者的收入较低，平均只有２０００元左右，很多人只能拿到１０００多元，收入低导致社会组织“留不住人”。</p><p>　　市社团办主任温庆云介绍，今年，争取首先对在市民政局登记的市级社会组织，全部核定编制和一个合理工资水平，推动社会组织为在职工作人员涨工资，完善各种保险。“比如，一个运行状况正常的社会组织，我们刨除其运行成本，人员的工资水平可以核定到３０００－４０００元，并要求社会组织为员工缴纳各种社会保险，包括生育险”。</p><p>　　市民政局还在争取制定社会组织的最低工资标准，比照全市平均工资增长，建立社会组织专职人员的工资增长机制。</p><p>　　社会组织将获税收优惠</p><p>　　不过，民政部门为社会组织核定的工资水平，只是根据不同社会组织的资金筹集量、工作量、争取政府购买服务的资金量等因素，为社会组织划定的指导工资标准。“因为社会组织的工资最终是自筹的，不能靠财政拨款”。温庆云称，比照事业单位，将社会组织的人员工资纳入“工资基金”管理，除了为社会组织从业者创造一个收入稳定增长的保障机制，还将为社会组织发展壮大，争取一些税收方面的政策优惠。</p><p>　　社会组织从业者将有职称</p><p>　　除了为社会组织专职人员涨工资，今年，市民政局还在探索设立社会组织从业者的职称系列。温庆云说，市社团办将研究不同类别社会组织的专业职称设计，“可以像工程师、医生一样，随着专业经验积累，拥有专业晋升空间”，通过良好的职业前景设计，吸引更多人进入社会组织工作。</p><p>　　北京探索建老人意外伤害保险</p><p>　　系商业险，考虑由老人自愿自费缴纳，困难群体或由财政埋单</p><p>　　本报讯　（记者魏铭言）今年，北京将探索建立６０周岁及以上户籍老年人意外伤害保险。</p><p>　　北京市老龄办常务副主任李建国介绍，老年意外伤害保险是一种商业险，主要由老人自愿自费缴纳；初步设想是每份保险价值不高，１０元或２０元一份，保价大概在数万元到十万元。</p><p>　　政府将通过财政埋单，为更多低保、贫困、高龄、独居等特殊困境老人购买老年意外保险。同时，市民政局和市老龄办还计划鼓励一些公益慈善性质的老龄关爱基金，募集社会善款，为特殊老人购买老年意外保险。</p><p>　　目前，我国尚无专门针对老龄人群的意外保险。市老龄协会相关负责人介绍，由于身体衰退，行动、反应迟缓，老人受到意外伤害的隐患，确实比普通人更多。因此，李建国认为，今后，养老机构和社区养老服务商，都可以为老人代购意外保险，给老人更多安全保障。</p><p>　　目前，北京６０岁以上户籍老人已达到２３５万人。市老龄办介绍，北京城市人口老龄化已进入提速期。</p><p>　　此外，北京今年还将探索建立老年人长期护理保险。被保险人从年轻时开始缴纳，在年老生病后，由护理保险来支付长期专业护理所需的高额费用。在老年护理保险发展较成熟的国家，人们一般从４０岁左右，就开始缴纳保险，为年老后的基本生存质量买一个保障。</p><p><br/></p>', '0', '0', '1', '1', '1497708521', '0', '0', '0', '1', '', '', null, '今年，公益慈善等类社会组织在北京登记注册，有望无须再找业务主管单位，直接向民政部门申请成立。昨天，２０１２年北京市民政工作会召开。市民政局首次提出，今年，将探索研究推动社会组织业务主管单位向业务指导单位转变。 ', null, null);
INSERT INTO `strategy` VALUES ('21', '民政部将每两年一次监测发布爱心GDP', '/uploads/images/10lYeOLTzU1497970770.jpg', '<p>　　本报讯　（记者魏铭言　实习生张婷）民政部计划每两年一次，常态监测全国城市“中国城市慈善指数”。</p><p>　　昨日，由民政部主办的第二届中国城市慈善指数编制发布活动在京举行，今年将调查并发布全国所有约７００座城市在２０１０－２０１１年两年中的慈善事业发展全貌。</p><p>　　具体负责“中国城市慈善指数”编制的机构，是民政部下属的中民慈善捐助信息中心。该中心负责人彭建梅介绍，“中国城市慈善指数”包括社会捐赠、慈善组织、慈善项目、志愿服务、政府支持和慈善文化六大层面的上百个具体指标，是一套对城市慈善事业发展水平进行综合监测与科学评价的指标体系，被民政部门形象地称为“爱心ＧＤＰ”。</p><p>　　目前，慈善事业发展状况尚未纳入国家统计体系，基础数据十分缺乏，不利于中央和地方政府职能部门了解慈善事业的真实基础状况，并作出正确决策。</p><p>　　民政部社会福利和慈善事业促进司副司长徐建中认为，中国城市慈善指数的编制，不仅可以评估一座城市的慈善发展水平，比较不同城市的发展差异，还可以诊断每个城市慈善事业的发展问题，引导城市自查不足。</p><p>　　记者了解到，除慈善款物的捐赠数量，一个城市拥有的慈善组织数量、质量、公信力与慈善项目的受益人数和社会效益，也是“中国城市公益慈善指数”编制中考察的重要指标。</p><p><br/></p>', '0', '1', '0', '7', '1497970770', '0', '1', '0', '1', '', '', null, '民政部计划每两年一次，常态监测全国城市“中国城市慈善指数”。   ', null, null);
INSERT INTO `strategy` VALUES ('22', '公益慈善组织将接受专项审计', '/uploads/images/qxG4OZDxLS1497970661.jpg', '<p>本报讯　（赵鹏）１月１２日，财政部、民政部有关负责人就进一步加强和完善基金会注册会计师审计制度答记者问。今后凡是符合条件的重大公益项目，以及公益慈善组织，因参与处理像汶川地震这样的自然灾害开展的募捐活动，都需进行专项审计，并将审计报告向社会公布。</p><p>　　财政部、民政部此次联合发布《关于进一步加强和完善基金会注册会计师审计制度的通知》。两部门有关负责人称，本“通知”规范的对象为在民政部门登记注册的基金会、境外基金会代表机构和其他具有公益性捐赠税前扣除资格的公益性社会团体。主要采取年度工作审计、离任和换届审计、专项审计３种方式，对基金会等公益组织的财务管理情况、理事会运作情况和项目开展情况进行全面审计。</p><p>　　“通知”的出台不仅重新强调了年度工作审计和离任换届审计的重要性，更为重要的是首次引入专项审计形式。今后，凡是符合条件的重大公益项目，以及基金会等公益慈善组织因参与处理像汶川地震这样的自然灾害等突发事件开展的募捐活动，都要进行专项审计，并且要将审计报告按照登记管理机关的要求向社会公布。</p><p>　　符合条件的重大公益项目，包括当年该项目的捐赠收入占基金会当年捐赠总收入的１／５以上，且金额超过人民币５０万元的；或当年该项目的支出占基金会当年总支出的１／５以上，且金额超过人民币５０万元的；或者持续时间超过３年的。</p><p>　　实施基金会注册会计师审计制度，是提高基金会社会公信力的重要手段。该制度有助于增强基金会财务报告的真实性和完整性。通知要求，经注册会计师审计的年度财务会计报告要在登记管理机关指定的统一信息公开平台上公布；离任和换届审计报告以及专项审计报告都要按登记管理机关的要求向社会公开。</p><p>　　实施这一审计制度，是创新基金会财务监督机制和提高公益资金管理水平的重要举措。该制度有助于弥补现行基金会财务监督体制的不足，形成政府审计、注册会计师审计和基金会内部审计协调运作、互为补充的监督体系。</p><p>　　财政部和民政部将尽快起草出台基金会审计业务指引，并共同在全国范围内组织开展基金会注册会计师审计制度培训。</p><p><br/></p>', '0', '1', '0', '7', '1497970661', '0', '1', '0', '1', '', '', null, '今后凡是符合条件的重大公益项目，以及公益慈善组织，因参与处理像汶川地震这样的自然灾害开展的募捐活动，都需进行专项审计。   ', null, null);
INSERT INTO `strategy` VALUES ('23', '中华慈善博物馆下半年开工 选址在原南通国棉二厂旧址', '/uploads/images/plEndqcPXY1497970681.jpg', '<p>南通网讯（记者吴银华）“今年下半年中华慈善博物馆将开工，力争２０１４年５月１８日建成运营。”１３日，在南通举办的中华慈善博物馆筹建座谈会上，市民政局局长杨建忠宣布了相关时间节点。</p><p>当天，国家民政部、省民政厅和复旦大学、华南理工大学的１０多位专家学者，会聚南通，对中华慈善博物馆的建筑设计、布展方案等进行专题论证。</p><p>据介绍，中华慈善博物馆选址原国棉二厂旧址，占地总建筑面积约３００００平米，投资规模初步估算为３．４６亿元。工程计划今年下半年开工，明年完成土建施工，并进行布展设备安装，力争２０１４年５月１８日国际博物馆日建成运营。</p><p>中华慈善博物馆将收集、整理和展出中华五千年丰富的慈善文物、史料，包括古今代表性慈善人物、典型的慈善活动、慈善事件、慈善思想精神等，展示中华慈善事业的历史源流，为传承和发展中华慈善事业提供综合性的场所。</p><p>领衔布展方案设计的复旦大学文化遗产研究中心文物与博物馆学系博物馆长、教授、博士生导师朱顺龙，向与会专家、学者汇报了布展大纲；担纲建筑设计的华南理工大学何镜堂院士派代表——建筑设计专业博士何小欣汇报了建筑设计方案。两位领军人物听取了与会专家、学者的建议。</p><p>会上，中华慈善博物馆向全社会有奖征集形象标识（ＬＯＧＯ）和馆训，同时寻求和社会各界合作的方案。</p>', '0', '1', '0', '7', '1497970681', '0', '1', '0', '1', '', '', null, '中华慈善博物馆筹建座谈会上，南通市民政局局长杨建忠宣布下半年中华慈善博物馆将开工，力争２０１４年５月１８日建成运营。 ', null, null);
INSERT INTO `strategy` VALUES ('24', '广州募捐条例获通过 民间公益性团体可开展募捐', '/uploads/images/jiA6HWPipw1497970180.jpg', '<p>除红十字会、慈善会、公募基金会以外，广州公益性的社会团体、民办非企业单位和非营利的事业单位也将可以开展募捐活动。昨日，市人大常委会第４５次会议通过备受关注的《广州市募捐条例》。据悉，条例删除了此前关于禁止自然人开展募捐活动的条款。</p><p>禁止自然人单独募捐条款被删除</p><p>扩大募捐主体是广州募捐条例的一大亮点。记者注意到，与之前审议稿不同的是，最终通过的条例对三类新增募捐主体增加了“公益性”的限定。条例规定，可以申请募捐的组织是为慈善目的设立的“公益性”社会团体、民办非企业单位和非营利的事业单位。</p><p>根据条例，为扶老、助残、救孤、济困或者赈灾目的而设立的公益性的社会团体、民办非企业单位和非营利性的事业单位经申请取得募捐许可后，在许可的范围和期限内开展募捐活动。</p><p>有专家认为，宪法规定公民在年老、疾病或者丧失劳动能力的情况下，有从国家和社会获得物质帮助的权利，因此原条例中关于，自然人不得单独开展募捐活动的规定被删除。</p><p>对新增三类募捐主体的行政许可问题此前曾引起广泛争议。对此，条例规定，市民政部门核发的募捐许可证应当写明组织名称，募捐目的、募捐方式、募捐期限、募捐地域等内容。许可募捐的期限不得超过三个月。许可募捐的期限届满后，募捐组织不得进行募捐。被许可人需要延期的，应当在该行政许可有效期届满５日前向市民政部门提出申请。</p><p>募捐财产使用期限超１年要年度审计</p><p>值得关注的是，为增强募捐财产使用的透明度，条例新增条款规定，募捐组织应当委托有资质的会计师事务所或者审计师事务所对募捐财产的使用情况进行审计，募捐财产使用期限在一年以上的，还应当进行年度审计。</p><p>根据条例，市民政部门应当建立募捐组织的守法档案，并定期向社会公布。民政部门还应当设置并且公开全市统一的举报和投诉电话，并在收到举报、投诉七日至二十日内将处理情况书面告知举报、投诉人。</p><p>另外，募捐组织采用募捐箱方式募集钱款的，在使用募捐箱前要在箱门处贴上封条，开箱时应当有两名以上的工作人员共同开启清点数额并签字确认。</p><p>工作成本不得超过募捐财产１０％</p><p>条例对募捐组织的工作成本进行了严格规定。条例要求，募捐组织应当厉行节约，降低工作成本，除组织募捐活动必不可少的工作经费、工作人员工资外，募捐财产及其增值应当用于募捐方案所确定的公益事业。在财政拨款经费中已经列支募捐工作成本的，不得在募捐财产中重复列支。</p><p>条例规定，开展募捐活动所产生的工作成本，国家有规定的，募捐组织不得超出国家规定的列支项目和标准。国家没有规定的，应当控制在已经公布的募捐方案所确定的工作成本列支项目和标准之内，除本条第三款规定的情形外，工作成本列支最高不得超过实际募捐财产价值的百分之十，义演、义赛、义卖募捐活动的工作成本列支最高不得超过本次募捐活动实际募捐财产价值的百分之二十。工作成本超标的，募捐组织应当委托会计师事务所或者审计师事务所编制预算的详细方案，在开展募捐活动前报市民政部门审核同意。</p><p>变相摊派可撤换主管人员</p><p>根据条例，募捐组织有下列情形之一的，由市民政部门给予警告，责令改正；拒不改正的，可以责令暂停募捐活动，并可以责令撤换其直接负责的主管人员：摊派或者变相摊派的；未按照规定履行信息公布义务的；未按照规定答复查询捐赠财产使用情况的捐赠人的；泄露捐赠人、受益人隐私，造成严重后果的。</p><p>同样可能面临被暂停募捐并撤换直接负责的主管人的情形还包括：以募捐名义进行营利活动的；涂改、出租、出借募捐许可证的；公布虚假信息的；未按照募捐方案规定的方式、期限、地域、工作成本进行募捐的；未按照募捐方案使用募捐财产的；未按照规定开具公益事业捐赠票据的；未按照规定设立募捐专用账号进行专账管理的；未按照规定妥善保管募捐物资的；未按照规定对募捐财产的使用情况进行审计的；拒不接受或者不按照规定接受监督检查的。上述行为有违法所得的，予以没收，并处一万元以上三万元以下罚款。</p><p>条例还规定，滞留‘私分、挪用、贪污或侵占募捐财产的，除了退还募捐款外，个人还将面临五千至三万以下的罚款，单位面临三至十万的罚款。</p><p><br/></p>', '0', '1', '0', '7', '1497970383', '0', '1', '0', '1', '', '', null, '备受关注的《广州市募捐条例》通过。据悉，条例删除了此前关于禁止自然人开展募捐活动的条款。     ', null, null);
INSERT INTO `strategy` VALUES ('25', '“有一天合唱团”纪录片出炉 讲述“爱与融合”的故事', '/uploads/images/m76ne1gA651497709269.png', '<p>２０１６年７月１６日，“ＯＮＥ　ＮＩＧＨＴ给小孩”的关爱残障儿童公益演唱会上，中国第一支“融合教育”合唱团　——“有一天合唱团”在北京奥体中心体育馆开场献唱，令全场观众感动不已。“有一天合唱团”由华谊兄弟公益基金一手创办，华谊公益基金特地用三个月时间，将“有一天合唱团”台前幕后“爱与融合”的特别旅程拍摄成了一段８分钟的纪录片，以此献给孩子们，献给关心他们成长的人们，希望可以鼓励更多孩子，更多家长。</p><p>纪录片用平实的镜头语言讲述了“有一天合唱团”中１５个来自不同生活背景的孩子台前幕后的故事。他们中有孤残儿童、聋哑儿童、服刑人员子女，也有拥完整家庭的普通儿童，他们聚集在一起，音乐让他们彼此接纳、包容、共同成长。对于这些孩子们来说，他们不仅收获了一次在“ＯＮＥ　ＮＩＧＨＴ公益晚会”的舞台上登台难忘经历，还收获了与小伙伴们的珍贵友谊。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497709174837598.jpg\" width=\"400\" height=\"206\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p><br/></p><p>“有一天合唱团”创建于２０１５年，伴随华谊兄弟公益基金拍摄的中国第一部公益电影《有一天》而诞生。合唱团每年举办一次演出，成员由不同生活背景的孩子们组成。每一次表演和排练，都是孩子们从陌生、相识、接纳、融合、到拥抱爱的过程。２０１５年联合国７０周年音乐会上，“有一天合唱团”与联合国和平大使郎朗同台演出，将“融合教育”的理念传播到世界。合唱团的创办人，也是电影《有一天》的发起人兼制片人孙阿美表示，她希望延续电影《有一天》的理念，用音乐的方式更多地发掘孩子们的潜能。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497709174938424.jpg\" width=\"400\" height=\"278\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p><br/></p><p>自２０１１年正式启动以来，华谊兄弟公益基金已经走过了爱与希望的５年：截至２０１５年，已先后在全国设立了６１所“零钱电影院”、举办了五届“小小画展”、与联合国五度携手合作、为天津８．１２爆炸事件捐款、参与雅安灾后重建……华谊兄弟公益基金不仅惠及了大量而广泛的受助者，也为中国草根公益组织的发展贡献了自己的力量。除去本次演出成本后，今年“ＯＮＥ　ＮＩＧＨＴ”演唱会所获得的全部收益将全部以“ＯＮＥ　ＮＩＧＨＴ　给小孩”项目名义捐赠给华谊兄弟公益基金，用于心智障碍儿童融合教育、孤残儿童康复治疗、重症孤儿临终关怀项目。并且，华谊兄弟公益基金将一如既往的进行专业化、透明化的管理。</p><p>诚然，外界的努力只是第一步，能够激发出孩子们内心的力量才是我们更想看到的。正如华谊兄弟公益基金负责人孙阿美在纪录片中所说，哪怕某天这些孩子离开了她，没有了这个合唱团，甚至没有了这个项目，“他们自己可以成为自己的超人，这个社会的超人。他们通过自己的能力去帮助自己成长的同时，也能帮助别人……”</p><p><br/></p>', '0', '0', '1', '4', '1497709283', '0', '1', '0', '1', '', '', null, '２０１６年７月１６日，“ＯＮＥ　ＮＩＧＨＴ给小孩”的关爱残障儿童公益演唱会上，中国第一支“融合教育”合唱团　——“有一天合唱团”在北京奥体中心体育馆开场献唱，令全场观众感动不已。 ', null, null);
INSERT INTO `strategy` VALUES ('26', '有一天，郎朗为这些孩子做了一件事', '/uploads/images/R4kvwNTZC51497709550.png', '<p>北京，１月１１日，联合国和平使者郎朗通过联合国开发计划署向华谊兄弟公益基金的“有一天合唱团”捐赠十万元人民币，用于改善特殊儿童的音乐教育。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497709424822691.jpg\" width=\"500\" height=\"426\" alt=\"\" style=\"padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\">“有一天合唱团”做客联合国</p><p>捐赠仪式在联合国开发计划署驻华代表处举行。郎朗在仪式上说：“教育是发展的关键，年轻人是未来的领导者，必须给予他们必要的工具完成人生的目标，让梦想成真。”</p><p>该笔赠款将用于为合唱团购买乐器，提供音乐课，提供更好的音乐教育，促进孩子们的全面发展。与此同时，联合国开发计划署将进一步与“有一天合唱团”开展合作，协助合唱团迈向更广阔的舞台，并呼吁公众关注儿童福祉，尤其是特殊儿童的教育问题。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497709425876193.jpg\" width=\"500\" height=\"336\" alt=\"\" style=\"padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\">孙阿美代表“有一天合唱团”接受捐款</p><p>“联合国可持续发展目标的一大原则是‘不落下任何人’，”开发署驻华代表处国别主任文霭洁女士在捐赠仪式上说道，“要实现可持续发展目标，就要让每个孩子都有学上，提高教育质量，建立包容的教育体系，让每个人都享有受教育的权利。”</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497709425975025.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\"padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\">郎朗与联合国开发署驻华国别主任文霭洁女士一同观看小朋友的画册</p><p>捐赠仪式后，郎朗、开发署驻华国别主任文霭洁女士、副国别主任何佩德先生与“有一天合唱团”的孩子们共同参加了“可持续发展目标记忆游戏”，寓教于乐，在游戏的过程中鼓励孩子们进一步了解１７个可持续发展目标。</p><p>“有一天合唱团”的发起人孙阿美在活动上说：“电影《有一天》通过电影的方式让更多人改变对特殊儿童群体的观念，‘有一天合唱团’通过音乐的方式为特殊儿童开启更多潜能。感谢郎朗和联合国开发计划署，让‘有一天合唱团’可持续发展，成为最幸福的合唱团。”</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497709425771357.jpg\" width=\"500\" height=\"332\" alt=\"\" style=\"padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\">２０１５年６月１４日，“有一天合唱团”与郎朗参加联合国７０周年音乐会</p><p>“有一天合唱团”创建于２０１５年，是由中国第一部公益电影《有一天》孕育而生的合唱团。合唱团的创办人，同时也是电影《有一天》的发起人兼制片人孙阿美，因为在从事公益事业的过程中遇到了很多不如我们幸运的儿童，而决定用电影的方式改变公众的观念，又用音乐的方式给孩子们创造新的潜能。</p><p><br/></p><p>视频链接：<a href=\"http://www.1905.com/video/play/904017.shtml\">ｈｔｔｐ：／／ｗｗｗ．１９０５．ｃｏｍ／ｖｉｄｅｏ／ｐｌａｙ／９０４０１７．ｓｈｔｍｌ</a>（演出视频回顾）</p><p><br/></p><p>２０１５年９月，联合国１９３个成员国在纽约总部正式通过了２０３０年可持续发展议程及１７个可持续发展目标，旨在消除贫困、抗击不平等、应对气候变化。其中，目标３“健康福祉”提倡健康的生活方式、促进各年龄段人群的福祉；目标４“优质教育”要求确保包容、公平的优质教育，促进全民享有终身学习机会。保障儿童尤其是特殊儿童的权利，为他们提供更优质的教育并改善他们的福祉有助于可持续发展目标的全面落实。</p>', '0', '0', '1', '1', '1497709577', '0', '0', '0', '1', '', '', null, '北京，１月１１日，联合国和平使者郎朗通过联合国开发计划署向华谊兄弟公益基金的“有一天合唱团”捐赠十万元人民币，用于改善特殊儿童的音乐教育。  ', null, null);
INSERT INTO `strategy` VALUES ('27', '顾长卫艺术展开幕 “零钱电影院”受益儿童成为特邀嘉宾', '/uploads/images/eX617tg1G61502876010.jpg', '<p style=\"text-align: left;\">2014年12月12日，“i-顾长卫当代艺术展”在上海当代艺术馆正式开幕，电影艺术家顾长卫、《ELLE世界时装之苑》编辑总监晓雪、演员袁姗姗莅临现场，并邀请上海大别山小学“零钱电影院”的孩子们走进艺术馆，成为第一批小观众们，顾长卫导演亲自全程讲解。</p><p><br type=\"_moz\" style=\"text-align: left;\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794366536513.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">“零钱电影院”的孩子们兴奋地前往参观顾长卫当代艺术展</span></p><p><br type=\"_moz\" style=\"text-align: left;\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794368643275.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">顾长卫、晓雪、袁姗姗与孩子们在一起</span></p><p>&nbsp;</p><p style=\"text-align: justify;\">“零钱电影院”是华谊兄弟公益基金的核心公益项目，致力于让越来越多的贫困儿童免费看到好电影。受邀前往艺术展的孩子们正是来自ELLE及华谊兄弟时尚共同协力支持的上海“零钱电影院”，ELLE主编晓雪作为上海“零钱电影院”的筹建者，当天特地赶来与孩子们见面。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">巧合的是，当天也是顾长卫导演的57岁生日，在特别的日子邀请“零钱电影院”的孩子们提前参观自己的艺术展并亲任讲解员，让这些热爱电影的孩子们有机会领略电影导演的艺术创作，并圆了他们与大导演近距离接触的梦想。</p><p><br type=\"_moz\" style=\"text-align: left;\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794368559988.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\">&nbsp;<span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: center;\">顾长卫向“零钱电影院”孩子们讲解自己的创作思路</span></p><p><br type=\"_moz\" style=\"text-align: left;\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794369945544.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\">&nbsp;<span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: center;\">顾长卫与晓雪</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794370912960.jpg\" width=\"500\" height=\"389\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">参观结束，众嘉宾与孩子们温暖合影</span></p><p><br type=\"_moz\" style=\"text-align: left;\"/></p><p style=\"text-align: left;\">相关背景：</p><p style=\"text-align: justify;\">顾长卫早年习画，1978年考入北京电影学院摄影系，进入电影行业。有关他在当代艺术领域的实践与探索却从未被外界知晓。本次举办的艺术个展，是以影像艺术为媒介进行深入对话交流的当代艺术展示活动，向公众展现了顾长卫独特的视角下充满冷静思考与饱含激情表述的全新世界。</p><p><br style=\"text-align: left;\"/></p>', '0', '0', '0', '5', '1503024073', '0', '0', '0', '0', '', '', null, '2014年12月12日，“i-顾长卫当代艺术展”在上海当代艺术馆正式开幕，电影艺术家顾长卫、《ELLE世界时装之苑》编辑总监晓雪、演员袁姗姗莅临现场，并邀请上海大别山小学“零钱电影院”的孩子们走进艺术馆，成为第一批小观众们，顾长卫导演亲自全程讲解。  ', '华谊兄弟，公益，顾长卫', '2014-12-12');
INSERT INTO `strategy` VALUES ('28', 'DAZZLE×岳敏君 缘自时尚与艺术的“零钱电影院”', '/uploads/images/ZGXOCTr5xj1502870913.jpg', '<p style=\"text-align: justify;\">今夏，独具时尚意识和文化内涵的女装品牌DAZZLE，携手国际著名艺术大师、中国当代艺术领军人物岳敏君先生，为华谊兄弟公益基金发起的“零钱电影院”公益项目，共同创作了一系列趣味与诙谐兼具的夏日限量版公益T恤，在全国进行公益售卖，并得到众多明星的鼎力支持。</p><p style=\"text-align: left;\"><br/></p><p style=\"text-align: justify;\">&nbsp; &nbsp;零钱电影院，致力于在贫困地区学校搭建寓教于乐的电影空间，开启孩子们了解世界的另一种方式，用电影点亮孩子们的梦想。作为华谊兄弟公益基金发起的重要公益项目，“零钱电影院”自2013年起就引起了DAZZLE-FASHION的高度关注。目前，DAZZLE-FASHION已通过一年一度的“华谊ELLE之夜”向华谊兄弟公益基金捐建了两所小学的“零钱电影院”,分别是信阳市平桥区邢集镇罗楼小学和平昌关镇朱庄小学。作为“华谊ELLE之夜”的联合发起方，华谊兄弟时尚凭借其在时尚领域的影响力，将时尚与公益进行了巧妙的结合，并已经在全国推动了三所“零钱电影院”的搭建。</p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794573885314.jpg\" width=\"250\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794574614117.jpg\" width=\"250\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">女TEE</span></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794575607029.jpg\" width=\"250\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794578123599.jpg\" width=\"250\" height=\"333\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">男TEE</span></p><p><br type=\"_moz\" style=\"text-align: left;\"/></p><p style=\"text-align: justify;\">&nbsp; &nbsp;DAZZLE×岳敏君限量版公益T恤，于2014年6月5日在DAZZLE全国54家指定店铺展开公开发售。项目所得全部利润，将捐给华谊兄弟公益基金的“零钱电影院”。这意味着：任何购买“零钱电影院”限量版公益T恤的小伙伴们，都将以自己的力量奉献一份爱心，帮助更多贫困地区的儿童实现看电影的美好愿望。唤起自己童心的同时，也带给更多孩子美好的童年……</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">&nbsp; &nbsp; 标志性的闭眼张口人物造型，透露强烈的岳敏君式个人风格；饱和度极高的色彩填充，让人回味斑斓的童年。一系列夸张开怀大笑的小人儿，仿佛把人投射到奇幻的外星球，一切只源于人们对爱的渴望。</p><p><br type=\"_moz\" style=\"text-align: left;\"/></p><p style=\"text-align: center;\">关于岳敏君</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794579439166.jpg\" width=\"100\" height=\"125\" alt=\"\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p><br/></p><p style=\"text-align: left;\">&nbsp; &nbsp;岳敏君，中国当代艺术的领军人物，国际著名艺术家。生活、创作于北京。</p><p style=\"text-align: justify;\">&nbsp; &nbsp;自90年代初，岳敏君就在画布上着意一个有夸张意味的“自我形象”的塑造，近年来这一形象蔓延到其雕塑和版画领域里。“它”有时独立出现；有时又以集体的面目亮相。“它”开口大笑，紧闭双眼；动作夸张，但却充满自信。 岳敏君无疑是中国当代艺术最重要作品的创造者之一。</p><p><br style=\"text-align: left;\"/></p>', '0', '0', '0', '5', '1502870913', '0', '0', '0', '0', '', '', null, '今夏，著名服装品牌DAZZLE，携手国际著名艺术大师、中国当代艺术领军人物岳敏君先生，为华谊兄弟公益基金发起的“零钱电影院”公益项目，共同创作了一系列趣味与诙谐兼具的夏日限量版公益T恤，在全国进行公益售卖。  ', '华谊兄弟，公益，时尚', '2014-06-01');
INSERT INTO `strategy` VALUES ('29', '告别“大黄鸭” 全明星助力“零钱电影院”', '/uploads/images/e6t54xNG7D1497794802.jpg', '<p style=\"text-align: justify;\">2013年10月24日下午，在古老的颐和园，北京设计周携手华谊兄弟公益基金共同组织了“大黄鸭”北京之旅万人告别活动，“鸭爸爸”荷兰的艺术家霍夫曼先生和功夫巨星成龙先生也亲临现场。来自华谊兄弟公益基金—北京明圆学校“零钱电影院”的50名打工子弟的同学和老师以及”爱心蓝天康复之家”的18名孩子和老师共同参与了这次充满温情的公益活动。 人手一只的“小黄鸭”也变成了孩子们最喜爱的礼物，霍夫曼先生也被孩子们的热情所感染，和孩子们开心的合影留念，记录下这难忘的瞬间。</p><p><br/></p><p style=\"text-align: justify;\">同时，由“大黄鸭“设计师霍夫曼先生授权设计的大黄鸭衍生品正版小黄鸭与冯绍峰、韩庚、倪妮、李小璐、贾乃亮、安以轩、杜淳、陈赫等热心公益事业的明星进行签名义卖，所得款项将扣除淘宝的运营成本捐给华谊兄弟公益基金的“零钱电影院”。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794726570467.jpg\" width=\"600\" height=\"400\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">孩子们拿到“大黄鸭”开心无比</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><br/></span></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1497794727684296.jpg\" width=\"600\" height=\"400\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">亲亲“大黄鸭”</span></p><p><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1497794727746469.jpg\" width=\"600\" height=\"400\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">“大黄鸭”开会</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><br/></span></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1497794729468946.jpg\" width=\"600\" height=\"400\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">孩子们和“鸭爸爸”霍夫曼先生合影</span></p><p><br/></p>', '0', '0', '0', '5', '1502873736', '0', '0', '0', '0', '', '', null, '2013年10月24日下午，北京设计周携手华谊兄弟公益基金在颐和园组织了“大黄鸭”北京之旅万人告别活动，“鸭爸爸”荷兰的艺术家霍夫曼先生和功夫巨星成龙先生也亲临现场。    ', '华谊兄弟，公益，零钱电影院', '2013-10-24');
INSERT INTO `strategy` VALUES ('30', '雅安芦山县流动“零钱电影院”盛夏放映', '/uploads/images/QJVKlqGgLI1497794935.jpg', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: 0px; list-style-type: none; color: rgb(102, 102, 102); text-indent: 24px; white-space: normal; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\">2013年4月20日四川雅安芦山县突发7.0级地震，房屋以及学校受到大面积损坏，六一儿童节当天，在芦山县平溪村小学校园内，孩子们欢蹦乱跳着，村民三五成群聚集在一起，欢笑声此起彼伏，手捧香喷喷的爆米花感受震后第一场电影带来的欢乐。来自华谊兄弟公益基金的志愿者-电影放映员杨世贵老师利用业余时间，坚持给地震重灾区的大河村、祥和村、中林村、春光村和宝盛乡等地的孩子们和周边群众百姓放映电影几十场，孩子们的欢笑声回荡在盛夏的夜晚，更多的村民和年轻人也都加入到了放映电影的队伍中来，一起来分享电影带来的感悟与快乐。</span></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; outline: 0px; list-style-type: none; color: rgb(102, 102, 102); text-indent: 24px; white-space: normal; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"><span style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"><br/></span></span></p><p style=\"text-align:center\"><img width=\"600\" height=\"337\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497794840477694.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\"><img width=\"600\" height=\"337\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497794841687047.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align:center\"><br/></p><p style=\"text-align:center\"><img width=\"600\" height=\"337\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497794841469065.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p><br/></p><p style=\"text-align:center\"><img width=\"600\" height=\"400\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497794842620376.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p><br/></p><p style=\"text-align:center\"><img width=\"600\" height=\"337\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497794842544326.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p><br/></p><p style=\"text-align: center;\"><img width=\"600\" height=\"337\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497794843813940.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p><br/></p>', '0', '1', '0', '6', '1502966408', '0', '0', '0', '0', '', '', null, '2013年六一儿童节当天，华谊兄弟公益基金在雅安震区组织了公益放映，让居民和孩子们感受震后第一场电影带来的欢乐。      ', '华谊兄弟，公益，零钱电影院', '2013-06-01');
INSERT INTO `strategy` VALUES ('31', '为北京新源学校开展公益放映活动', '/uploads/images/bn2qXYXdmm1497798508.JPG', '<p style=\"text-align: left;\">2012年11月22日上午，华谊兄弟公益基金工作人员来到新源学校为学校三年级二班的27名同学进行公益放映活动，观看电影《飞屋环游记》精彩片段，并且和孩子们共同讨论了关于梦想的话题，对于该怎么样去坚持梦想孩子们也都积极回答，反响热烈。</p><p style=\"text-align: left;\"><br/></p><p style=\"text-align:center\"><img width=\"600\" height=\"400\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497798417812688.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">孩子们正在观看《飞屋环游记》</span></p><p><br style=\"text-align: left;\"/></p>', '0', '1', '0', '6', '1502868208', '0', '0', '0', '0', '', '', null, '华谊兄弟公益基金工作人员来到北京的一所民办学校进行公益放映活动，观看电影《飞屋环游记》精彩片段。  ', '华谊兄弟，公益，公益放映', '2012-11-22');
INSERT INTO `strategy` VALUES ('32', '华谊兄弟公益基金为清红蓝小学举行公益放映活动', '/uploads/images/El0NERJgOL1497798665.JPG', '<p style=\"text-align: left;\">2012年11月21日,华谊兄弟公益基金的工作人员来到北京清红蓝小学为四年级2班的56名学生进行公益放映活动,孩子们对结合电影主题的绘画分享课感到即新奇又兴奋,积极性特别地高,在观看完电影片段《飞屋环游记》后，全情投入到绘画创作中。活动结束后，学生还依依不舍地围着着志愿者哥哥询问下次什么时候再来放映电影并画画。</p><p style=\"text-align: left;\"><br/></p><p style=\"text-align:center\"><img width=\"600\" height=\"400\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497798563632458.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">孩子们在观看电影《飞屋环游记》</span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align:center\"><img width=\"600\" height=\"400\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497798564853849.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">孩子们专注地观影</span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align:center\"><img width=\"600\" height=\"400\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497798565665386.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">学生正在结合电影主题进行绘画创作</span></p><p><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align:center\"><img width=\"600\" height=\"400\" alt=\"\" src=\"/uploads/ueditor/php/upload/image/1497798566630312.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">学生们观影后开始绘画创作</span></p><p><br style=\"text-align: left;\"/></p>', '0', '1', '0', '6', '1502868136', '0', '0', '0', '0', '', '', null, '华谊兄弟公益基金的工作人员来到北京清红蓝小学观看完电影片段《飞屋环游记》  。', '华谊兄弟，公益，公益放映', '2012-11-21');
INSERT INTO `strategy` VALUES ('33', '回访嘉谟小学 为学生开展公益放映活动', '/uploads/images/pstathGzsa1497798800.JPG', '<p style=\"text-align: left;\">2012年11月9日，华谊兄弟公益基金的员工来到四川隆昌县嘉谟小学，同行的还有志愿者华谊兄弟重庆影院南坪店店长梁永飞、督导经理马珑，我们一同回访了学校，并上了一堂丰富多彩电影主题绘画分享课。<br style=\"text-align: left;\"/></p><p style=\"text-align: left;\"><br/></p><p style=\"text-align: left;\">去年夏天，华谊兄弟重庆影院的同事来到学校，用影院票房捐款向学校捐赠了一套放映设备。今天，华谊兄弟公益基金带着500色铅笔来到学校，捐赠给热爱画画的孩子们，希望他们能够在观影之后，手握神笔，画出美丽的梦想。</p><p style=\"text-align: left;\"><br/></p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798708996099.jpg\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\">华谊兄弟重庆影院南坪店店长梁永飞和督导经理马珑正在和孩子们交流</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\"><br/></span></p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798709877459.jpg\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\">学生正在专注地观看电影《神笔》</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\"><br/></span></p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798710878014.jpg\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\">学生们踊跃地举手回答问题</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\"><br/></span></p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798710154265.jpg\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\">大家正在用500色铅笔绘画</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\"><br/></span></p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798711953343.jpg\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\">认真创作的孩子</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\"><br/></span></p><p style=\"text-align:center\"><img alt=\"\" width=\"400\" height=\"600\" src=\"/uploads/ueditor/php/upload/image/1497798712715446.jpg\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\">小男孩展示自己的作品</span></p><p style=\"text-align: left;\"><br/></p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798712155832.jpg\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\">绘画结束后大家兴奋地展示自己的绘画作品</span></p><p><br style=\"text-align: left;\"/></p>', '0', '1', '0', '6', '1502867988', '0', '0', '0', '0', '', '', null, '华谊兄弟公益基金的员工来到四川隆昌县嘉谟小学，对学校进行回访，并上了一堂丰富多彩电影主题绘画分享课。  ', '华谊兄弟，公益，公益放映', '2012-11-09');
INSERT INTO `strategy` VALUES ('34', '为闵庙村小学学生举行公益放映活动', '/uploads/images/NzVsSGSuF31497798949.JPG', '<p style=\"text-align: left;\">2012年10月11日，华谊兄弟公益基金的员工第二次来到闵庙村小学，为全校68名学生进行一次电影主题绘画分享课，正在集合准备做广播体操的孩子们看见我们的到来，既好奇又害羞。</p><p style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 操场上尘土飞扬，教室依然破旧不堪，孩子们的笑容始终纯真灿烂。我们调试好设备、准备好桌椅后，孩子们也陆续排队走进了教室。观影的孩子小到3岁，大到4年级，他们乖巧的坐在独凳或长椅上，等待活动开始。孩子们非常的羞涩，和他们互动，也不愿多说话，但是当提到会看电影时，孩子们响亮又兴奋的声音溢满校园。</p><p style=\"text-align: left;\">我们放映了动画片《九色鹿》，孩子们看电影时专注又认真，观影后，还准备了5本中英漫画书作为问答奖励，孩子们踊跃回答问题，并从电影中学到美好的品质。由于2年级以下的孩子不太适合进行绘画创作，在用500色铅笔引导孩子以“我心中的九色鹿”为主题创作时，只选出了3-4年级的学生参与。孩子们拿到丰富多彩的铅笔开始绘画时，兴奋又开心，门外、窗外还有低年级的小朋友好奇的往里看，有的还说希望自己也可以用美丽的铅笔画画，我们便邀请渴望画画的孩子也参与进来一起绘画。</p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798892391620.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">学生们正在观看动画片《九色鹿》</p><p style=\"text-align:center\"><img alt=\"\" width=\"400\" height=\"600\" src=\"/uploads/ueditor/php/upload/image/1497798892532777.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">学生们正在认真地选择500色铅笔</p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798893357662.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">孩子们画得很开心</p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798893796071.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">孩子们手拿画笔，渴望绘画</p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798894528193.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">趴在窗边渴望画画的低年级学生</p><p style=\"text-align:center\"><img alt=\"\" width=\"600\" height=\"400\" src=\"/uploads/ueditor/php/upload/image/1497798894669135.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-weight: inherit; list-style-type: none;\"/></p><p style=\"text-align: left;\">创作完成后孩子们开心地合影</p><p><br style=\"text-align: left;\"/></p>', '0', '1', '0', '1', '1497798949', '0', '0', '0', '0', '', '', null, '华谊兄弟公益基金的员工第二次来到闵庙村小学，为全校68名学生进行一次电影主题绘画分享课，正在集合准备做广播体操的孩子们看见我们的到来，既好奇又害羞。', null, null);
INSERT INTO `strategy` VALUES ('35', '四度携手联合国 华谊兄弟公益初冬暖人心', '/uploads/images/VIuIk4X5Ch1502869309.jpg', '<p style=\"text-align: center;\">&nbsp;<img src=\"/uploads/ueditor/php/upload/image/1497923699659961.jpg\" width=\"500\" height=\"375\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p>&nbsp;</p><p style=\"text-align: justify;\">10月23日晚，太庙红毯铺地，照明灯辉映，姚晨、刘雯、刘亦菲、倪妮、蒋勤勤、霍思燕夫妇，还有刚刚从《琅琊榜》里走出的靳东、王凯等明星华服亮相，引爆聚光灯和无数菲林。这一晚的太庙被 “2015嘉人中国风”时尚大赏加温，变身成了热闹的时尚盛宴。天气虽已入初冬，但盛典却给现场的人们带来了融融暖意。&nbsp;</p><p><br/></p><h3 style=\"text-align: center;\">“指尖上的幸福”：苗绣撑起母爱天空&nbsp;</h3><p><br/></p><p style=\"text-align: justify;\">“指尖上的幸福”公益行动在盛典当晚正式启动：这是华谊兄弟公益基金携手嘉人女性幸福基金、联合国开发计划署、中国宋庆龄基金会等机构共同发起的公益行动。为了让中国女性过有尊严的幸福生活、让传统手艺因现代设计而重生，“指尖上的幸福”公益行动从2016年开始，集合各方优势力量共同出发，为保护传统手艺、改善偏远地区女性和儿童的生活，探索出可持续发展的道路。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1497923700926018.jpg\" width=\"500\" height=\"375\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p>&nbsp;</p><p style=\"text-align: justify;\">盛典现场，华谊兄弟公益基金负责人在发言时说：“华谊兄弟公益基金一直致力于让贫困地区的儿童在电影的陪伴下成长，我们在贫困地区的学校，包括贵州的黔东南地区都在积极地搭建’零钱电影院’，通过影像的方式创造更多善的力量。母亲的发展也是孩子们健康成长的保障，我们也希望通过这次合作，可以带动到更多的机构或企业为’指尖上的幸福’创造更多善的力量。”</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1497923700484583.jpg\" width=\"500\" height=\"332\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p>&nbsp;</p><p style=\"text-align: justify;\">联合国开发署中国区主任文女士表示：“少数民族占中国总人口的9％，但是贫困人口数量却占到了全国贫困人口的1/3，其中性别不平等的问题特别凸显。过去10年，联合国开发署一直致力于为少数民族的妇女赋权，帮她们通过民族传统工艺以及技能，改善自身生活条件和社会经济地位，同时保护独特的少数民族文化。我们在黔东南地区开发了3个重点项目，推广苗绣产业，帮助少数民族妇女建立自主能力。”&nbsp;</p><h3 style=\"text-align: justify;\"><br/></h3><h3 style=\"text-align: center;\">华谊兄弟公益基金：与联合国的不解之缘&nbsp;</h3><p><br/></p><p>华谊兄弟公益基金的核心项目是“零钱电影院”，大部分时间，项目人员都奔波在大山深处，为那里的孩子带去电影，还童年时光以应有的快乐。&nbsp;</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1497923701255015.jpg\" width=\"500\" height=\"374\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">“零钱电影院”为大山里的孩子带来电影</span></p><p>&nbsp;</p><p style=\"text-align: justify;\">在搭建“零钱电影院”的路上，在辗转在偏远地区的路上，中国第一部公益电影《有一天》诞生了。《有一天》在不到一年的时间里，连续3次在联合国的舞台展现其独特的魅力：2014年12月3日，电影《有一天》作为唯一一部来自中国的作品在纽约联合国总部举行的联合国残疾人电影节上展映；2015年3月20日，《有一天》的发起人兼制片人携同参演小演员顺利受邀参加在联合国纽约总部召开的世界唐氏综合症日活动，并现场分享中国的公益电影如何创造更多善的力量；2015年6月14日，“有一天合唱团”登上联合国70周年音乐会的舞台。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1497923701383160.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">中国第一部公益电影《有一天》</span></p><p>&nbsp;</p><p style=\"text-align: justify;\">2015年10月23日，“指尖上的幸福”的启动成为华谊兄弟公益基金与联合国的第四次携手。在与联合国各机构间的不断接触、合作的过程中，华谊兄弟公益基金正在逐渐培养其更具国际视角、更具专业性的公益机构的运营理念和开阔的资源整合能力。华谊兄弟公益基金期待下一个五年，创造更多善的力量。</p><p style=\"text-align: justify;\"><br/></p>', '0', '0', '0', '4', '1502869309', '0', '0', '0', '0', '', '', null, '10月23日晚， “2015嘉人中国风”盛典在太庙举行。华谊兄弟公益基金携手联合国开发计划署等机构共同发起的“指尖上的幸福”公益行动在盛典当晚正式启动。  ', '华谊兄弟，时尚，公益，联合国', '2015-10-23');
INSERT INTO `strategy` VALUES ('36', '华谊兄弟总裁王中磊获评“2014年度中国慈善推动者”', '/uploads/images/IdJ7wxct0q1502869916.jpg', '<p style=\"text-align: justify;\">&nbsp;2015年1月15日，“慈善的力量·2014中国慈善年会”在中华世纪坛举行，颁发“2014年度中国慈善推动者”，以表彰一年来通过创新的思想、政策等解决社会问题，推动中国慈善事业进步的个人、团队和机构。华谊兄弟总裁、中国第一部公益电影《有一天》总制片人王中磊获评“2014年度行业发展推动者”。</p><p><br type=\"_moz\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1497924060239613.jpg\" width=\"500\" height=\"272\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/><br type=\"_moz\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">华谊兄弟副董事长、CEO王中磊</span></p><p><br type=\"_moz\"/></p><p style=\"text-align: justify;\">第五届中国慈善年会上，评委会特别评价《有一天》的创新理念，“公益组织开创筹款拍摄电影的先河，以9个孩子的故事，让特殊儿童群体的生命通过影像绽放，《有一天》让我们在光影艺术间发现慈善传播的创新模式”。全国人大常委会委员郑功成为王中磊颁发奖项，《有一天》的发起人兼制片人也在现场与来宾分享电影的诞生历程。</p><p><br type=\"_moz\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1497924060192926.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/><br type=\"_moz\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">王中磊获评“2014年度行业发展推动者”</span></p><p><br type=\"_moz\"/></p><p style=\"text-align: justify;\">王中磊表示，这个奖项是对《有一天》团队最好的鼓励，很感谢苏芒女士和电影的发起人兼制片人孙阿美，用她们的力量开启了《有一天》这个梦想，感谢周迅、张涵予、徐帆、冯绍峰、韩庚、袁姗姗、斯琴高娃、王千源、安以轩、咏梅、冯嘉怡、熊乃瑾12位明星捐出时间参演，感谢所有台前幕后工作人员的付出，通过影像，以轻松、容易的方式让更多人了解特殊儿童。慈善与公益没有强弱之分，希望社会各界共同加入，用更好的方式去扩散公益思想。</p><p><br type=\"_moz\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1497924061461248.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/><br type=\"_moz\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">华谊兄弟公益基金“用影像的方式创造善的力量”</span></p><p><br type=\"_moz\"/></p><p style=\"text-align: justify;\">孙阿美非常感谢中国慈善年会给予《有一天》的认可与鼓励，她表示，在2012年开始为《有一天》筹款拍摄制作时，这对公益机构来说是异想天开的事。但历经900天的努力，《有一天》开创了先河，并前后得到了500人的接力支持，12位明星“零片酬“参演，是非常感动的历程，希望在华谊兄弟公益基金的推动下，会有更多的公益机构与电影公司用影像的方式创造善的力量。</p><p><br type=\"_moz\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1497924061789149.jpg\" width=\"500\" height=\"333\" alt=\"\" style=\";padding: 0px;border: 0px;outline: 0px;font-weight: inherit;list-style-type: none\"/><br type=\"_moz\"/></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">王中磊、孙阿美发表获奖感言</span></p><p><br type=\"_moz\"/></p><p style=\"text-align: justify;\">电影《有一天》由12位明星零片酬出演，历时900天筹款拍摄完成，以9个不同的励志故事向公众展现了包括贫困、聋哑、读写困难、留守、被拐卖、肥胖、聋哑等特殊儿童群体有笑有泪的追梦历程，呼吁更多人关注和关爱社会特殊儿童群体。在选题上关注社会问题、在上映时采用大众低票价、明星零片酬、放映院线化，唤起了公众的爱心和对社会问题的责任感，是慈善价值传播的创新模式。&nbsp;</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">《有一天》无障碍电影版本即将完成，通过对画面进行解说描述的方式，让更多盲童小朋友可以看到一部与他们有关的电影，通过影像的力量向社会传播更多的正能量。</p><p><br/></p>', '0', '0', '0', '4', '1502869916', '0', '0', '0', '0', '', '', null, ' 2015年1月15日，“慈善的力量·2014中国慈善年会”在中华世纪坛举行，颁发“2014年度中国慈善推动者”。华谊兄弟总裁、中国第一部公益电影《有一天》总制片人王中磊获评“2014年度行业发展推动者”。 ', '华谊兄弟，公益', '2015-01-15');
INSERT INTO `strategy` VALUES ('37', '测试新闻', '/uploads/images/CSyY4r41Uy1497925861.jpg', '<p>测试测试测试测试测试测试测试测试测试测试测试测试测试测试测试</p>', '0', '0', '1', '6', '1497925861', '0', '1', '0', '0', '', '', null, '测试测试测试测试测试测试测试测试测试测试', null, null);
INSERT INTO `strategy` VALUES ('38', '公益放映走进大凉山', '/uploads/images/AzqFOFpdYD1497925934.jpg', '<p style=\"text-align: justify;\">2011年3月中旬，华谊兄弟公益基金走进大凉山做项目考察，并为索玛花爱心学校的孩子们放映了露天电影，凉山的孩子们第一次看电影，被跳动的画面深深吸引。露天电影带给我们的是美好回忆，带给山里的孩子们的更多是新鲜，全校三百多名彝族小学生围坐在操场上盼着。一年级的小朋友，刚刚开始学汉语，不太能看得懂，但依然边看边笑，动画片就是有这样的魔力。</p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: justify;\">放映队的工作人员说，他们一年中大半年都是在各村落中度过，条件艰苦，在这样的条件下依然坚持传递美好，应该向他们致敬。学校校长说，要不是我们来，放映队很难走进学校，不是不关注这里，一年3269场乡村放映，对于11台长途跋涉的放映车，确实力不从心。我们来了，很好。</p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 16px; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502868520468119.png\" title=\"1502868520468119.png\" alt=\"image.png\"/></span></p><p><span style=\"font-size: 16px; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 16px; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502868730727515.png\" title=\"1502868730727515.png\" alt=\"image.png\"/></span></p><p><span style=\"font-size: 16px; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 16px; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502868557557592.png\" title=\"1502868557557592.png\" alt=\"image.png\"/></span></p><p><span style=\"font-size: 16px; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 16px; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502868568225576.png\" title=\"1502868568225576.png\" alt=\"image.png\"/></span></p><p><span style=\"font-size: 16px; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p>', '0', '0', '1', '6', '1503024688', '0', '0', '0', '0', '', '', null, '2011年3月中旬，华谊兄弟公益基金走进大凉山，为索玛花爱心学校的孩子们放映了露天电影，为孩子们留下了美好的回忆。 ', '华谊兄弟，公益，公益放映', '2011-03-20');
INSERT INTO `strategy` VALUES ('39', '华谊兄弟公益基金五周年 第100所“零钱电影院”落成', '/uploads/images/256koDLqFv1502784023.jpg', '<p style=\"text-align: justify;\"><strong>2016年10月26日 – 云南丽江</strong> 华谊兄弟公益基金迎来了成立五周年的重要时刻，作为该基金最重要的项目之一，华谊兄弟公益基金捐赠的第100所“零钱电影院”也于云南省丽江市玉龙纳西族自治县拉市镇海南完小正式落成，这是一个里程碑式的节点。云南省政府领导代表，华谊兄弟副董事长、CEO王中磊先生出席了第100所零钱电影院捐赠仪式，共同开启孩子们光影世界的大门。华谊兄弟公益基金五周年的历程，离不开华谊兄弟公司自上而下的不懈努力，也离不开王中军的坚持。作为企业掌门人，董事长王中军先生曾先后多次通过义卖画作，为华谊兄弟公益基金筹款，并将所得善款全部捐赠，用艺术的力量为孩子们创造属于他们的精神世界。</p><p style=\"text-align: justify;\"><span style=\"font-size: 16px;\"><span style=\"line-height: 150%; font-family: 微软雅黑, sans-serif;\"><br/></span></span></p><p style=\"text-align:center\"><span style=\"line-height: 150%; font-family: 微软雅黑, sans-serif; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502783499461978.png\" title=\"1502783499461978.png\" alt=\"image.png\"/></span></p><p><span style=\"line-height: 150%; font-family: 微软雅黑, sans-serif; font-size: 16px;\"><br/></span></p><p style=\"text-align: justify;\">华谊兄弟副董事长、CEO王中磊先生表示：“华谊兄弟公益基金从最初的简单想法，逐渐成长为一个非常专业的公益组织。在过去的5年里，我们也一直在思考，如何才能给孩子们带来更多的快乐和更长远的积极影响，答案就是‘创造力公益’。区别于传统公益简单直接的物质馈赠形式，‘创造力公益’是一种不断创新、可持续的公益模式，更注重于丰富孩子们的精神世界。”事实上，“创造力公益”是华谊兄弟公益基金的品牌内涵。在公益实践中，华谊兄弟公益基金为儿童带来的不只是好的电影，还为他们搭建了一个可以尽情发挥创造力的艺术空间，这是对孩子们精神的塑造。华谊兄弟公益基金希望以此为沃土，让更多的儿童可以全面地认识世界，从而更有勇气追逐梦想，创造未来的无限可能。同时，创造力的培养将是未来中国教育的一大重点，华谊兄弟公益基金“创造力公益”的新理念，也正与这一教育趋势不谋而合。</p><p style=\"text-align: justify;\"><span style=\"line-height: 150%; font-family: 微软雅黑, sans-serif; font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><span style=\"line-height: 150%; font-family: 微软雅黑, sans-serif; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502783512167389.png\" title=\"1502783512167389.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center\"><span style=\"line-height: 150%; font-family: 微软雅黑, sans-serif; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502783525849473.png\" title=\"1502783525849473.png\" alt=\"image.png\"/></span></p><p style=\"line-height:150%;text-autospace:none\"><span style=\"line-height: 150%; font-family: 微软雅黑, sans-serif; font-size: 16px;\"><br/></span></p><p style=\"text-align: justify;\">作为华谊兄弟公益基金的核心项目之一，五年来，“零钱电影院”已走过北京、上海、广州、河南、云南、青海等全国多个省市，惠及儿童近15万名。除此之外，华谊兄弟公益基金还积极汇聚影视资源，以多种方式实践善举，聚点滴小爱为大爱。例如筹备制作关注弱势儿童的公益电影《有一天》，创办 “融合教育”合唱团“有一天合唱团”，积极投身国际合作及灾后重建等等。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><span style=\"line-height: 150%; font-family: 微软雅黑, sans-serif; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502783543464373.png\" title=\"1502783543464373.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center\"><span style=\"line-height: 150%; font-family: 微软雅黑, sans-serif; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502783553456831.png\" title=\"1502783553456831.png\" alt=\"image.png\"/></span></p><p style=\"line-height:150%\"><span style=\"line-height: 150%; font-family: 微软雅黑, sans-serif; font-size: 16px;\"><br/></span></p><p style=\"text-align: justify;\">作为中国娱乐行业的领军企业，成立22年的华谊兄弟一直关注创造力，尊重且敬畏内容，也因此而凝聚了众多精品内容的创造者及创意人才，致力于为观众创造出更多品质与态度并存的优秀影视作品。“创造力公益”也正是对华谊兄弟企业基因的延续。作为独具特色且实践经验丰富的公益组织，在下一个五年甚至是更久的时间内，华谊兄弟公益基金将继续以“创造力公益”为主导，用愈加饱满的姿态延续慈善力量，为更多孩子的美好人生贡献己力。</p><p><br/></p>', '0', '1', '0', '5', '1503023406', '0', '0', '0', '0', '', '', null, '华谊兄弟公益基金捐建的第100所“零钱电影院”于云南省丽江市玉龙纳西族自治县拉市镇海南完小正式落成，这是一个里程碑式的节点。          ', '华谊兄弟公益基金,零钱电影院,五周年', '2016-10-26');
INSERT INTO `strategy` VALUES ('40', '周迅与“有一天合唱团”点亮“ONE NIGHT 2016”，为爱发声', '/uploads/images/jq85GgA8eX1502785464.jpg', '<p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">2016年7月16日，由华谊兄弟公益基金创办的“有一天合唱团”于北京奥体中心体育馆为“ONE NIGHT给小孩”关爱残障儿童公益演唱会开场献唱。演唱会上，“有一天合唱团”为观众演唱电影《有一天》也是“ONE NIGHT”的主题曲《给小孩》，以温暖的方式诠释了公益演唱会的理念。周迅作为“ONE NIGHT”项目的发起人，不仅分享了公益理念，更邀请了圈中多位好友陈坤、白百合、姚晨、秦海璐、董洁来到了现场。</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">&nbsp;</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">“有一天合唱团”&nbsp;创建于2015年，由中国第一部公益电影《有一天》孕育而生，是中国第一支“融合教育”合唱团。合唱团每年进行一次演出，成员由不同生活背景的孩子们组成，每一次表演都是一个彼此从陌生、相识、接纳、融合、到拥抱爱的过程。华谊兄弟公益基金希望通过这种方式延续电影《有一天》的理念，用音乐的方式给孩子们创造新的潜能。</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">&nbsp;</span></p><p style=\"text-align: center\"><img src=\"/uploads/ueditor/php/upload/image/1502851797120405.jpeg\" title=\"1502851797120405.jpeg\" alt=\"3.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/ueditor/php/upload/image/1502852040821370.jpeg\" title=\"1502852040821370.jpeg\" alt=\"4.jpg\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502852433437675.jpeg\" title=\"1502852433437675.jpeg\" alt=\"5.jpg\"/></p><p style=\"text-align:center;text-indent:32px;line-height:150%\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">“有一天合唱团”</span></p><p style=\"text-align:center;text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">&nbsp;</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">今年“ONE NIGHT”依旧由阿雅助阵担任主持。晚会邀请到许巍、周笔畅两位资深实力唱将；德云社“hiphop担当”岳云鹏与金牌搭档孙越，作为演唱会相声嘉宾，在现场负责起了搞笑担当的角色。</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">&nbsp;</span></p><p style=\"text-align: center\"><img src=\"/uploads/ueditor/php/upload/image/1502852679574540.jpeg\" title=\"1502852679574540.jpeg\" alt=\"6.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/ueditor/php/upload/image/1502852903187815.jpeg\" title=\"1502852903187815.jpeg\" alt=\"6.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/ueditor/php/upload/image/1502853018367060.jpeg\" title=\"1502853018367060.jpeg\" alt=\"7.jpg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/ueditor/php/upload/image/1502853283488850.jpeg\" title=\"1502853283488850.jpeg\" alt=\"8.jpg\"/></p><p style=\"text-align: center;\"><span style=\"text-indent: 32px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">众星齐聚为公益发声，让爱点亮夜空</span></p><p style=\"text-align:center;text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">&nbsp;</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">“ONE NIGHT”公益晚会的举办，源于华谊兄弟益基金投拍的儿童公益电影《ONE DAY》（《有一天》）。影片由9个温暖励志的小故事组成，讲述9个弱势儿童群体追梦的故事。9个故事分别关注贫困儿童、读写困难儿童、智障儿童、被拐卖儿童、盲童、肥胖儿童、聋哑儿童、留守儿童、服刑人员子女。周迅也出演了九个故事中的一个，温暖的故事和公益理念的理念让她希望能为这些孩子做的更多，对应着“ONE DAY”就有了“ONE NIGHT”。从ONE DAY到ONE NIGHT，从电影到演唱会，不变的是始终保持对孩子的诚挚关爱。</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">&nbsp;</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">晚会以“关爱残障儿童成长与发展”为主题，所获得的相关收益，除去本次演出成本后，全部以“ONE NIGHT给小孩”项目名义捐赠给华谊兄弟公益基金，用于心智障碍儿童融合教育、孤残儿童康复治疗、重症孤儿临终关怀项目。今年再度联手华谊兄弟公益基金，是对华谊兄弟公益基金的信任，也是对其专业度和公信力的肯定。</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p>', '0', '1', '0', '4', '1502853300', '0', '1', '0', '0', '', '', null, '2016年7月16日，由华谊兄弟公益基金创办的“有一天合唱团”于北京奥体中心体育馆为关爱残障儿童公益演唱会开场献唱。         ', '周迅,有一天合唱团,华谊兄弟公益基金', '2016-07-17');
INSERT INTO `strategy` VALUES ('41', '华谊兄弟公益基金第一届“零钱电影院”校长峰会在京召开', '/uploads/images/Z3xuaK0szm1502787760.jpg', '<p style=\"text-align: justify;\">7月23日，来自全国10个省市的100余位“零钱电影院”所在学校的校长相聚北京，共同出席了华谊兄弟公益基金第一届“零钱电影院”校长峰会的启动典礼。华谊兄弟创始人、董事长王中军，联合创始人、副董事长兼CEO王中磊，中华思源工程扶贫基金会副理事长兼秘书长李晓林，北京电影学院科研信息化处处长、中国电影教育研究中心主任刘军等也出席了典礼并致辞。典礼上，王中军、王中磊一同敲响了上课铃，标志着为期一周的首届校长峰会正式拉开帷幕，同时也宣告了华谊兄弟公益基金面向一百所“零钱电影院”的全面升级计划正式启动。<br/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: center;\"><span style=\";font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502787495242115.png\" title=\"1502787495242115.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">华谊兄弟创始人、董事长王中军</span></p><p style=\"line-height: 26px;\"><span style=\"font-size:12px;font-family:宋体;color:#888888\"><br/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502787506123238.png\" title=\"1502787506123238.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">华谊兄弟联合创始人、副董事长兼</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">CEO</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">王中磊</span></p><p style=\"line-height: 26px;\"><span style=\"font-size:12px;font-family:宋体;color:#888888\"><br/></span></p><p style=\"text-align: justify;\">“零钱电影院”公益项目始于2011年，旨在号召人们用手边的零钱帮助贫困地区儿童的教育，通过将闲置的图书室、自习室或教室等进行翻修改造、配备全套放映设备以及电影光盘，让更多贫困地区儿童可以用“零分钱”免费欣赏电影。创立6年间，华谊兄弟公益基金已累计在全国各偏远地区的学校里搭建100所“零钱电影院”，为超过15万贫困儿童创造了欣赏、了解、感受电影的机会。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">步入2017年，“零钱电影院”开启了项目升级，在软硬件两方面进行全面提升。相比数量的增加，华谊兄弟公益基金更在乎每一所“零钱电影院”的使用效率，更关注孩子们是不是能真正从中获益。项目升级期间，华谊兄弟公益基金将全面回访已有的逾百所“零钱电影院”。完善、升级硬件设备，并进一步提升学校对电影教育重要性的认识，推动电影教育的落地普及，从而弥补不同地区间发展和教育水平的差距，为推动教育公平贡献力量。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align:center;line-height:26px\"><span style=\";font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502787518860445.png\" title=\"1502787518860445.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">全国政协委员、民建中央常委、</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">&nbsp;</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">中华思源工程扶贫基金会副理事长兼秘书长李晓林</span></p><p style=\"line-height: 26px;\"><span style=\"font-size:12px;font-family:宋体;color:#888888\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\"><img src=\"/uploads/ueditor/php/upload/image/1502787527652544.png\" title=\"1502787527652544.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">北京电影学院科研信息化处处长、中国电影教育研究中心主任刘军教授</span></p><p><span style=\"font-size:12px;font-family:宋体;color:#888888\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\"><img src=\"/uploads/ueditor/php/upload/image/1502787535287343.png\" title=\"1502787535287343.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">甘肃省会宁县翟家所镇焦河小学郭守敬校长</span></p><p><span style=\"font-size:12px;font-family:宋体;color:#888888\"><br/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\"><img src=\"/uploads/ueditor/php/upload/image/1502787543213269.png\" title=\"1502787543213269.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">北京市朝阳区民办博文学校徐熙媛小朋友</span></p><p style=\"line-height: 26px;\"><span style=\"font-size:12px;font-family:宋体;color:#888888\"><br/></span></p><p style=\"text-align: justify;\">华谊兄弟董事长王中军表示：“华谊兄弟公益基金将艺术和公益做了一个很好的结合，公益基金也是我绘画的一个动力。未来，不仅是‘零钱电影院’，还会有公益幼儿园等更多项目在计划、实施中。我们希望通过艺术的力量让孩子们更好的发掘创造力，获得更多的精神财富。”同时，他还提到了“精准公益”的概念，即针对不同贫困地区的状况，更有针对性的制定公益计划，更好的帮助到当地的孩子们，这也是华谊兄弟公益基金一直秉持的理念之一。峰会期间，华谊兄弟公益基金将对校长们展开为期一周的培训，旨在让校长们了解如何更好的利用电影开展儿童教育，让更多的孩子们受益。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">作为华谊兄弟公益基金最有力的支持者，董事长王中军先生曾先后多次义卖画作，将所得2000万善款全部捐赠给华谊兄弟公益基金，用艺术的力量为孩子们创造属于他们的精神世界。在“零钱电影院”里，孩子们不仅可以看电影，观影后还会在老师的带领下“画电影”，并定期举办“小小画展”。零钱电影院不仅为孩子们带来了光影之美，同时“画电影”的形式也为他们开启了通过绘画表达自我的艺术之窗。其承载的不仅是华谊兄弟公益基金对践行创造力公益的坚定信念，更是王中军先生对普及绘画艺术教育、推动教育公平的殷切之心。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502787556671341.png\" title=\"1502787556671341.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">华谊兄弟董事长王中军，华谊兄弟副董事长、</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">CEO</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">王中磊共同敲响第一届</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">“</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">零钱电影院</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">”</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">校长峰会的上课铃</span></p><p style=\"text-align:center\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502787565770044.png\" title=\"1502787565770044.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲“</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">零钱电影院</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">”</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">校长峰会各位演讲嘉宾合影</span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502787575366950.png\" title=\"1502787575366950.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">新疆的郝建英校长为王中军、王中磊送上小朋友的画作</span></p><p style=\"line-height: 26px;\"><span style=\"font-size:12px;font-family:宋体;color:#888888\"><br/></span></p><p style=\"text-align: center;\"><strong>普及电影教育：每一个孩子都需要“尊重、陪伴、启迪”</strong></p><p style=\"text-align: center;\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span></p><p style=\"text-align: justify;\">“零钱电影院”所承载的儿童电影教育是华谊兄弟公益基金重要的组成部分之一。近年来，国内儿童电影教育正在逐渐引起人们的关注。电影教育在承担着启发孩子们心智作用的同时，兼有教育、认识、审美、娱乐等层面的功能。电影不仅借助色彩、图像、音乐艺术颜色展示了音乐美、画面美，更传达了文学作品中的人性美，让孩子在获得感官愉悦的基础上获得心灵的愉悦。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">在“零钱电影院”项目长达6年的实践中，华谊兄弟公益基金率先提出了“以人为本”的儿童电影教育——以“尊重、陪伴、启迪”为核心，将电影融入儿童成长的各个阶段，在充分尊重儿童对于电影的理解和需要的基础上，让儿童通过电影认识更大的世界、启迪儿童的创造力，陪伴他们的成长，让电影能够更好地匹配他们的需要，并助力他们未来的发展。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><span style=\"font-size: 18px;font-family: Helvetica, sans-serif\"><img src=\"/uploads/ueditor/php/upload/image/1502787585763812.png\" title=\"1502787585763812.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲“</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">零钱电影院</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">”</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">校长</span></p><p style=\"line-height: 26px;\"><span style=\"font-size:12px;font-family:宋体;color:#888888\"><br/></span></p><p style=\"text-align: justify;\">作为华谊兄弟公益基金的亲身践行者，华谊兄弟副董事长、CEO王中磊曾多次参与到公益实践中，了解“零钱电影院”的使用情况。典礼现场，王中磊表示：“我们一直相信电影的力量，更相信优秀的儿童电影教育能够更好地激发孩子们的想象力和创造力，丰富孩子们的精神世界、点燃孩子们探寻更广阔天地的梦想。”</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: center;\"><strong>倡导融合教育：在歌声中激发孩子与生俱来的潜能</strong></p><p><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span></p><p style=\"text-align: justify;\">作为华谊兄弟公益基金的另一个重要组成部分，融合教育则承担了关爱残障儿童的责任，让他们和正常儿童在融合与接纳的环境中充分发挥自己的潜能，促进教育公平。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">2014年，由华谊兄弟出品的公益电影《有一天》促进了社会对特殊儿童群体的认知。次年，“有一天合唱团”孕育而生，并每年进行一次演出，成为中国第一支“融合教育”合唱团。“有一天合唱团”成员由来自不同生活背景的孩子们组成，每一次表演都是一个彼此从陌生、相识、接纳、融合、到拥抱爱的过程。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">2016年7月，“有一天合唱团”在北京奥体中心体育馆登上由周迅携手华谊兄弟公益基金举办的“ONE NIGHT给小孩”关爱残障儿童公益演唱会的舞台。向社会传递了“融合教育”的理念，展现残障儿童的潜能，推动公众对特殊儿童群体的观念改变，鼓励更多的特殊儿童群体自信、快乐地成长。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: center;\"><strong>创造力公益：让孩子们平等的享受精神的滋养</strong></p><p style=\"text-align: center;\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span></p><p style=\"text-align: justify;\">在成立的6年里，华谊兄弟公益基金一直在思考如何才能给孩子们带来更多的快乐和更长远的积极影响，答案就是“创造力公益”。区别于传统公益简单直接的物质馈赠形式，“创造力公益”是一种不断创新、可持续的公益模式，以推动电影教育的发展、倡导并践行教育公平理念，同时促进国际联系交流。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><span style=\";font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502787596501292.png\" title=\"1502787596501292.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">百位</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">“</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">零钱电影院</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">”</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">校长与王中军、王中磊等嘉宾合影留念</span></p><p style=\"line-height: 26px;\"><span style=\"font-size:12px;font-family:宋体;color:#888888\"><br/></span></p><p style=\"text-align: justify;\">华谊兄弟公益基金曾五度与联合国携手推动公益事业的进展，并在国际上得到了广泛认可，充分展现出华谊兄弟公益基金的专业度及国际影响力。2015年的世界唐氏综合症日，华谊兄弟公益基金代表及参演小演员受邀到联合国纽约总部进行演讲，分享《有一天》的拍摄理念，唐氏综合症群体在中国的现状得到了世界的关注。同年10月，华谊兄弟公益基金携手、联合国开发计划署、中国宋庆龄基金会等机构共同发起“指尖上的幸福”公益行动。11月，华谊兄弟公益基金与联合国开发计划署在北京签署合作协议，共同推动少数民族女性赋权与发展。2016年5月，由联合国开发计划署、成都大熊猫繁育研究基地、华谊兄弟公益基金等联合举办的“全球首次大熊猫艺术巡展-北京站”于北京自然博物馆开幕。同年6月，“有一天合唱团”与国际钢琴家郎朗共同在联合国70周年音乐会上登台表演电影《有一天》主题曲《给小孩》，用音乐的方式向世界传递和平与发展的声音。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">2017年6月，“华谊兄弟公益基金第100所零钱电影院”传播项目在诸多国际品牌项目中脱颖而出，在PR Awards Asia 2017（亚洲公关大奖）获得“Corporate Social Responsibility Campaign of the Year （年度CSR传播项目）”大奖。此次获奖不仅是对华谊兄弟公益基金多年来践行社会责任、坚守公益初心的肯定与鼓励，更重要的是其开创并一直倡导的“创造力公益”理念再度获得了国际性、跨行业的更大认可。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">作为中国娱乐行业的领军企业，成立23年的华谊兄弟一直关注创造力，尊重且敬畏内容，也因此而凝聚了众多精品内容的创造者及创意人才，致力于为观众创造出更多品质与态度并存的优秀影视作品。“创造力公益”也正是对华谊兄弟企业基因的延续。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">同时，华谊兄弟公益基金也定期在全球范围内举办展示贫困地区儿童创造力的“小小画展”、发展重症儿童舒缓疗护项目，用实际行动关爱儿童群体，让每一个孩子都有被照顾、被尊重和受教育的权利，助力教育公平，推动社会的发展与进步。此外，华谊兄弟公益基金也积极汇聚资源，以多种方式实践善举，投身国际合作及灾后重建等公益项目。未来，华谊兄弟公益基金将继续践行创造力公益的核心理念，用实际行动助力我国公益事业的发展。</p><p><br/></p>', '0', '1', '0', '5', '1503368276', '0', '0', '0', '0', '', '', null, '来自全国10个省市的100余位“零钱电影院”所在学校的校长相聚北京，共同出席了第一届“零钱电影院”校长峰会的启动典礼 。          ', '“零钱电影院”校长峰会,华谊兄弟公益基金,公益', '2017-07-23');
INSERT INTO `strategy` VALUES ('42', '华谊兄弟公益基金第一届“零钱电影院”校长峰会在京召开', '/uploads/images/QyFdaRB2gr1502788731.jpg', '<p style=\"text-align: justify;\">7月23日，来自全国10个省市的100余位“零钱电影院”所在学校的校长相聚北京，共同出席了华谊兄弟公益基金第一届“零钱电影院”校长峰会的启动典礼。华谊兄弟创始人、董事长王中军，联合创始人、副董事长兼CEO王中磊，中华思源工程扶贫基金会副理事长兼秘书长李晓林，北京电影学院科研信息化处处长、中国电影教育研究中心主任刘军等也出席了典礼并致辞。典礼上，王中军、王中磊一同敲响了上课铃，标志着为期一周的首届校长峰会正式拉开帷幕，同时也宣告了华谊兄弟公益基金面向一百所“零钱电影院”的全面升级计划正式启动。</p><p style=\"text-align: justify;\"><span style=\"font-family: 宋体; color: rgb(62, 62, 62);\"><br/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\";font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502788562194815.png\" title=\"1502788562194815.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">华谊兄弟创始人、董事长王中军</span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502788570334602.png\" title=\"1502788570334602.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">华谊兄弟联合创始人、副董事长兼</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">CEO</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">王中磊</span></p><p style=\"text-align: justify;\">“零钱电影院”公益项目始于2011年，旨在号召人们用手边的零钱帮助贫困地区儿童的教育，通过将闲置的图书室、自习室或教室等进行翻修改造、配备全套放映设备以及电影光盘，让更多贫困地区儿童可以用“零分钱”免费欣赏电影。创立6年间，华谊兄弟公益基金已累计在全国各偏远地区的学校里搭建100所“零钱电影院”，为超过15万贫困儿童创造了欣赏、了解、感受电影的机会。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">步入2017年，“零钱电影院”开启了项目升级，在软硬件两方面进行全面提升。相比数量的增加，华谊兄弟公益基金更在乎每一所“零钱电影院”的使用效率，更关注孩子们是不是能真正从中获益。项目升级期间，华谊兄弟公益基金将全面回访已有的逾百所“零钱电影院”。完善、升级硬件设备，并进一步提升学校对电影教育重要性的认识，推动电影教育的落地普及，从而弥补不同地区间发展和教育水平的差距，为推动教育公平贡献力量。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align:center;line-height:26px\"><span style=\";font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502788579164710.png\" title=\"1502788579164710.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">全国政协委员、民建中央常委、</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">&nbsp;</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">中华思源工程扶贫基金会副理事长兼秘书长李晓林</span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\"><img src=\"/uploads/ueditor/php/upload/image/1502788586106146.png\" title=\"1502788586106146.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">北京电影学院科研信息化处处长、中国电影教育研究中心主任刘军教授</span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\"><img src=\"/uploads/ueditor/php/upload/image/1502788594100248.png\" title=\"1502788594100248.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">甘肃省会宁县翟家所镇焦河小学郭守敬校长</span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\"><img src=\"/uploads/ueditor/php/upload/image/1502788601743966.png\" title=\"1502788601743966.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">北京市朝阳区民办博文学校徐熙媛小朋友</span></p><p style=\"text-align: justify;\">华谊兄弟董事长王中军表示：“华谊兄弟公益基金将艺术和公益做了一个很好的结合，公益基金也是我绘画的一个动力。未来，不仅是‘零钱电影院’，还会有公益幼儿园等更多项目在计划、实施中。我们希望通过艺术的力量让孩子们更好的发掘创造力，获得更多的精神财富。”同时，他还提到了“精准公益”的概念，即针对不同贫困地区的状况，更有针对性的制定公益计划，更好的帮助到当地的孩子们，这也是华谊兄弟公益基金一直秉持的理念之一。峰会期间，华谊兄弟公益基金将对校长们展开为期一周的培训，旨在让校长们了解如何更好的利用电影开展儿童教育，让更多的孩子们受益。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">作为华谊兄弟公益基金最有力的支持者，董事长王中军先生曾先后多次义卖画作，将所得2000万善款全部捐赠给华谊兄弟公益基金，用艺术的力量为孩子们创造属于他们的精神世界。在“零钱电影院”里，孩子们不仅可以看电影，观影后还会在老师的带领下“画电影”，并定期举办“小小画展”。零钱电影院不仅为孩子们带来了光影之美，同时“画电影”的形式也为他们开启了通过绘画表达自我的艺术之窗。其承载的不仅是华谊兄弟公益基金对践行创造力公益的坚定信念，更是王中军先生对普及绘画艺术教育、推动教育公平的殷切之心。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502788610425126.png\" title=\"1502788610425126.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">华谊兄弟董事长王中军，华谊兄弟副董事长、</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">CEO</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">王中磊共同敲响第一届</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">“</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">零钱电影院</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">”</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">校长峰会的上课铃</span></p><p style=\"text-align:center\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502788617396031.png\" title=\"1502788617396031.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲“</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">零钱电影院</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">”</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">校长峰会各位演讲嘉宾合影</span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502788624129895.png\" title=\"1502788624129895.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">新疆的郝建英校长为王中军、王中磊送上小朋友的画作</span></p><p style=\"line-height: 26px;\"><span style=\"font-size:12px;font-family:宋体;color:#888888\"><br/></span></p><p style=\"text-align: center;\"><strong>普及电影教育：每一个孩子都需要“尊重、陪伴、启迪”</strong></p><p style=\"text-align: center;\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span></p><p style=\"text-align: justify;\">“零钱电影院”所承载的儿童电影教育是华谊兄弟公益基金重要的组成部分之一。近年来，国内儿童电影教育正在逐渐引起人们的关注。电影教育在承担着启发孩子们心智作用的同时，兼有教育、认识、审美、娱乐等层面的功能。电影不仅借助色彩、图像、音乐艺术颜色展示了音乐美、画面美，更传达了文学作品中的人性美，让孩子在获得感官愉悦的基础上获得心灵的愉悦。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">在“零钱电影院”项目长达6年的实践中，华谊兄弟公益基金率先提出了“以人为本”的儿童电影教育——以“尊重、陪伴、启迪”为核心，将电影融入儿童成长的各个阶段，在充分尊重儿童对于电影的理解和需要的基础上，让儿童通过电影认识更大的世界、启迪儿童的创造力，陪伴他们的成长，让电影能够更好地匹配他们的需要，并助力他们未来的发展。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><span style=\"font-size: 18px;font-family: Helvetica, sans-serif\"><img src=\"/uploads/ueditor/php/upload/image/1502788634181771.png\" title=\"1502788634181771.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲“</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">零钱电影院</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">”</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">校长</span></p><p style=\"text-align: justify;\">作为华谊兄弟公益基金的亲身践行者，华谊兄弟副董事长、CEO王中磊曾多次参与到公益实践中，了解“零钱电影院”的使用情况。典礼现场，王中磊表示：“我们一直相信电影的力量，更相信优秀的儿童电影教育能够更好地激发孩子们的想象力和创造力，丰富孩子们的精神世界、点燃孩子们探寻更广阔天地的梦想。”</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: center;\"><strong>倡导融合教育：在歌声中激发孩子与生俱来的潜能</strong></p><p style=\"text-align: center;\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span></p><p style=\"text-align: justify;\">作为华谊兄弟公益基金的另一个重要组成部分，融合教育则承担了关爱残障儿童的责任，让他们和正常儿童在融合与接纳的环境中充分发挥自己的潜能，促进教育公平。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">2014年，由华谊兄弟出品的公益电影《有一天》促进了社会对特殊儿童群体的认知。次年，“有一天合唱团”孕育而生，并每年进行一次演出，成为中国第一支“融合教育”合唱团。“有一天合唱团”成员由来自不同生活背景的孩子们组成，每一次表演都是一个彼此从陌生、相识、接纳、融合、到拥抱爱的过程。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">2016年7月，“有一天合唱团”在北京奥体中心体育馆登上由周迅携手华谊兄弟公益基金举办的“ONE NIGHT给小孩”关爱残障儿童公益演唱会的舞台。向社会传递了“融合教育”的理念，展现残障儿童的潜能，推动公众对特殊儿童群体的观念改变，鼓励更多的特殊儿童群体自信、快乐地成长。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: center;\"><strong>创造力公益：让孩子们平等的享受精神的滋养</strong></p><p style=\"text-align: center;\"><span style=\"font-size:16px;font-family:宋体\">&nbsp;</span></p><p style=\"text-align: justify;\">在成立的6年里，华谊兄弟公益基金一直在思考如何才能给孩子们带来更多的快乐和更长远的积极影响，答案就是“创造力公益”。区别于传统公益简单直接的物质馈赠形式，“创造力公益”是一种不断创新、可持续的公益模式，以推动电影教育的发展、倡导并践行教育公平理念，同时促进国际联系交流。</p><p style=\"text-align: justify;\"><span style=\";font-family:宋体;color:#3E3E3E\"><br/></span></p><p style=\"text-align: center; line-height: 26px;\"><span style=\";font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\"><img src=\"/uploads/ueditor/php/upload/image/1502788645318430.png\" title=\"1502788645318430.png\" alt=\"image.png\"/></span></p><p style=\"text-align:center;line-height:26px\"><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">▲</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">百位</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">“</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">零钱电影院</span><span style=\"font-size:12px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#888888\">”</span><span style=\"font-size:12px;font-family:宋体;color:#888888\">校长与王中军、王中磊等嘉宾合影留念</span></p><p style=\"text-align: justify;\">华谊兄弟公益基金曾五度与联合国携手推动公益事业的进展，并在国际上得到了广泛认可，充分展现出华谊兄弟公益基金的专业度及国际影响力。2015年的世界唐氏综合症日，华谊兄弟公益基金代表及参演小演员受邀到联合国纽约总部进行演讲，分享《有一天》的拍摄理念，唐氏综合症群体在中国的现状得到了世界的关注。同年10月，华谊兄弟公益基金携手、联合国开发计划署、中国宋庆龄基金会等机构共同发起“指尖上的幸福”公益行动。11月，华谊兄弟公益基金与联合国开发计划署在北京签署合作协议，共同推动少数民族女性赋权与发展。2016年5月，由联合国开发计划署、成都大熊猫繁育研究基地、华谊兄弟公益基金等联合举办的“全球首次大熊猫艺术巡展-北京站”于北京自然博物馆开幕。同年6月，“有一天合唱团”与国际钢琴家郎朗共同在联合国70周年音乐会上登台表演电影《有一天》主题曲《给小孩》，用音乐的方式向世界传递和平与发展的声音。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">2017年6月，“华谊兄弟公益基金第100所零钱电影院”传播项目在诸多国际品牌项目中脱颖而出，在PR Awards Asia 2017（亚洲公关大奖）获得“Corporate Social Responsibility Campaign of the Year （年度CSR传播项目）”大奖。此次获奖不仅是对华谊兄弟公益基金多年来践行社会责任、坚守公益初心的肯定与鼓励，更重要的是其开创并一直倡导的“创造力公益”理念再度获得了国际性、跨行业的更大认可。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">作为中国娱乐行业的领军企业，成立23年的华谊兄弟一直关注创造力，尊重且敬畏内容，也因此而凝聚了众多精品内容的创造者及创意人才，致力于为观众创造出更多品质与态度并存的优秀影视作品。“创造力公益”也正是对华谊兄弟企业基因的延续。</p><p style=\"text-align: justify;\"><span style=\"font-size:18px;font-family:&#39;Helvetica&#39;,&#39;sans-serif&#39;;color:#3E3E3E\">&nbsp;</span></p><p style=\"text-align: justify;\">同时，华谊兄弟公益基金也定期在全球范围内举办展示贫困地区儿童创造力的“小小画展”、发展重症儿童舒缓疗护项目，用实际行动关爱儿童群体，让每一个孩子都有被照顾、被尊重和受教育的权利，助力教育公平，推动社会的发展与进步。此外，华谊兄弟公益基金也积极汇聚资源，以多种方式实践善举，投身国际合作及灾后重建等公益项目。未来，华谊兄弟公益基金将继续践行创造力公益的核心理念，用实际行动助力我国公益事业的发展。</p><p><br/></p>', '0', '0', '1', '4', '1503022942', '0', '0', '0', '0', '', '', null, '来自全国10个省市的100余位“零钱电影院”所在学校的校长相聚北京，共同出席了第一届“零钱电影院”校长峰会的启动典礼。      ', '华谊兄弟公益基金,“零钱电影院”校长峰会,公益', '2017-07-23');
INSERT INTO `strategy` VALUES ('82', '首届“零钱电影院”校长峰会在京圆满落幕', '/uploads/images/061Prowqsg1503026948.jpg', '<p>为期一周的华谊兄弟公益基金首届“零钱电影院”校长峰会于上周末在北京落下了帷幕。本次峰会期间，共有来自全国10个省市的100余位“零钱电影院”所在学校校长出席并参加了儿童电影教育的培训。</p><p>&nbsp;</p><p>峰会期间，华谊兄弟公益基金邀请到了北京电影学院科研信息化处处长刘军等多位电影教育领域的权威、资深人士，围绕“入戏入画，引导孩子们在电影中释放创造的能量”的主题，为校长们展开了为期三天的培训。培训期间，专家们通过丰富的案例为校长们详细讲解了何为参与式电影教学、如何对学生开展参与式电影教学等问题，并针对授课技巧、讲课思路、如何调动课堂气氛、做好与学生的沟通等主题为校长们进行了培训。除此以外，校长们也通过组内讨论、上台分享等方式交换了对于儿童电影教育的看法，并对可能面临的问题和解决方法进行了探讨，加强了对参与式电影教学的理解。</p><p>&nbsp;</p><p>在第一天的培训中，刘军教授为大家介绍了电影教育对儿童的积极影响。在谈及国内儿童电影教育的现状时，刘军教授表示，中国儿童电影教育起步很早，有着很广阔的发展空间，通过围绕电影开展的不同类型的教学活动，能够更好地提升儿童的视听素养和创造力。深入浅出的方式、启发式的教学，让在座的校长从原有的固定思路中走了出来，更加准确地把握儿童电影教育的重要性及作用。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1503026469551200.jpeg\" title=\"1503026469551200.jpeg\" alt=\"114.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">▲第一天的培训现场</span></p><p><br/></p><p style=\"text-align: justify;\">在第二天、第三天的培训中，参与式教学领域的权威人士为大家带来了一堂堂别开生面的课程。“鸡尾酒会”、“焦点讨论”、“教与学”、“电影片段观赏”，一个个极富创意的环节给大家带来了耳目一新的感受，让大家体会到自发寻找答案的乐趣的同时，也让校长们迅速地熟悉了彼此。校长们表示，通过培训，他们切身感受到了参与式教学的魅力，对如何更好地利用“零钱电影院”有了新的思考，迫不及待地想回去与自己的同事分享。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1503026516768038.jpeg\" title=\"1503026516768038.jpeg\" alt=\"115.jpg\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1503026557125376.jpeg\" title=\"1503026557125376.jpeg\" alt=\"116.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">▲第二天、第三天的培训现场&nbsp;校长们感受到了参与式教学的魅力</span></p><p><br/></p><p style=\"text-align: justify;\">随后的几天里，我们带领着校长们参观了中国电影博物馆、中国影视大乐园，并在华谊兄弟电影汇观看了电影，让校长们全方位地感受电影的魅力。</p><p style=\"text-align: justify;\"><span style=\"font-size: 18px;font-family: Helvetica, sans-serif\">&nbsp;</span></p><p style=\"text-align: justify;\">在中国电影博物馆的参观中，我们和校长们一起穿越电影的百年历程，在集权威性、知识性、艺术性于一体的艺术殿堂里，感受电影的每一次突破，体会电影带来的每一次震撼；在中国影视大乐园里，校长们真正走入了电影，从特效化妆、场景拍摄、人物配音，再到角色扮演，逐一尝试各个环节，体会电影的乐趣，揭开电影背后的秘密；在华谊兄弟电影汇，校长们坐在顶级配置的奢享式影院中全身心地沉浸在电影的魅力中。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1503026605962199.jpeg\" title=\"1503026605962199.jpeg\" alt=\"117.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">▲参观中国电影博物馆</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1503026712715818.jpeg\" title=\"1503026712715818.jpeg\" alt=\"118.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><img src=\"/uploads/ueditor/php/upload/image/1503026792708059.jpeg\" title=\"1503026792708059.jpeg\" alt=\"119.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">▲参观中国影视大乐园</span></p><p><br/></p><p style=\"text-align: justify;\">参与本次培训的校长中有许多人是第一次来到北京，因而接下来的几天里，我们为校长们安排了北京精华景点的观光，从天安门到故宫，从长城到奥林匹克公园，都留下了校长们的足迹和欢声笑语。</p><p><br/></p><p></p><p><img src=\"/uploads/ueditor/php/upload/image/1503026858240711.jpeg\" title=\"1503026858240711.jpeg\" alt=\"121.jpg\"/></p><p><img src=\"/uploads/ueditor/php/upload/image/1503026939572209.jpeg\" title=\"1503026939572209.jpeg\" alt=\"120.jpg\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">▲游玩北京精华景点</span></p><p><br/></p><p style=\"text-align: justify;\">为期一周的培训结束了，而我们的“零钱电影院”升级项目才刚刚开始，儿童电影教育的未来任重道远。我们将在全面升级硬件设备的同时，持续对校长们开展儿童电影教育和参与式教学的培训，进一步提升学校对儿童电影教育重要性的认识，推动儿童电影教育的落地普及，从而弥补不同地区间发展和教育水平的差距，为推动教育公平贡献力量。儿童电影教育的普及是一个长久的过程，希望未来能有更多的伙伴和我们一起，让“零钱电影院”更好地服务儿童电影教育，为更多孩子们带去放飞梦想的翅膀。</p><p style=\"text-align: justify;\"><br/></p>', '0', '1', '0', '4', '1503026948', '0', '1', '0', '0', '', '', null, '峰会期间，共有来自全国10个省市的100余位“零钱电影院”所在学校校长出席并参加了儿童电影教育的培训。', '华谊兄弟，华谊兄弟公益基金，零钱电影院，校长峰会', '2017-07-29');
INSERT INTO `strategy` VALUES ('43', '大爱公益薪火相传 华谊兄弟公益基金6周年回首', '/uploads/images/WglW41YZSq1502789820.jpg', '<p style=\"text-align: justify;\">作为一个在影视行业深耕多年的企业，华谊兄弟深知光影能够为观众带来的感动与影响，同时希望能够把这份光影的力量传递给更多偏远地区的儿童，启迪孩子们与生俱来的想象力和创造力，并陪伴他们的成长。秉承着这样的思考，2011年6月1日，华谊兄弟公益基金正式启动，致力于关注孩子们的精神世界，传递善的力量。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">6年公益之路，华谊兄弟公益基金以多种方式实践善举，履行企业的社会责任以及使命：在持续为偏远地区小朋友带来难忘的光影记忆，并将以“尊重、启迪、陪伴”理念为核心的儿童电影教育融入到他们成长过程的同时，也保持着对心智障碍群体的关注，倡导用“融合教育”理念，让他们能够更好的认识自己、接纳别人，学习爱与被爱。除此以外，华谊兄弟公益基金也通过自身的影响力汇集更多公益的力量，向国际领域不断延伸。</p><p><br/></p><p style=\"text-align: justify;\">在下一个6年或更久，我们将延续善的力量，帮助更多的孩子们拥有美好丰富的精神世界，让“创造力公益”薪火相传。</p><p style=\"text-align: justify;\"><br/></p><p style=\";margin-bottom:0;text-align:justify;text-justify: inter-ideograph;line-height:24px\"><span style=\"font-size: 14px;font-family: Helvetica, sans-serif\">&nbsp;</span></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502789141583412.jpeg\" title=\"1502789141583412.jpeg\" style=\"text-align: center;\"/></p><p style=\"text-align: center\"><img src=\"/uploads/ueditor/php/upload/image/1502789141406410.jpeg\" style=\"\" title=\"1502789141406410.jpeg\"/></p><p style=\"text-align: center\"><img src=\"/uploads/ueditor/php/upload/image/1502789142148505.jpeg\" style=\"\" title=\"1502789142148505.jpeg\"/></p><p><br/></p><p><br/></p>', '0', '0', '1', '4', '1503023036', '0', '0', '0', '0', '', '', null, '6年公益之路，华谊兄弟公益基金以多种方式实践善举，履行企业的社会责任以及使命。 ', '华谊兄弟公益基金,6周年,公益', '2017-06-01');
INSERT INTO `strategy` VALUES ('44', '校园暴力如何预防？ 是否降低刑责年龄争议仍持续', '/uploads/images/6Qo9vFxRkp1502854310.jpg', '<p style=\"text-align: justify;\">在安徽的某艺术学校，几个如花似玉的女孩群殴另一位女同学，还拿凉水浇，造成其心理出现问题。最终，学校把带头打人的13岁孩子开除了。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">“她们可是学习艺术的啊！我也很揪心，这么小的孩子到社会上能干什么？”全国政协委员侯露，是安徽省戏剧家协会副主席，此事曾让她一度很痛心，便现场给法律专家讲述了这一案例。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">近日，在共青团中央大楼内，由团中央维护青少年权益工作部牵头的完善《未成年人保护法》论证会暨团中央议案建议提案办理答复会现场，全国人大代表、全国政协委员、专家和相关部门工作人员20多人围坐在一起热烈讨论，说问题、讲真话，共同为如何更好保护未成年人出谋划策。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: center;\"><strong>声音：有人大代表呼吁降低最低刑事责任年龄</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">侯露很较真，专门找到学校老师，了解到打人的孩子平时学习很认真，在之前没有一点征兆，来自离异家庭。“就连她的老师也哭了两天两夜，要知道选拔一名学戏的人才有多难，才13岁就制造了这样一起悲剧。”她说起这事儿时有些激动。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">校园暴力该如何预防？侯露发现了一个悖论：公安部门推到学校，学校推给了家庭，家庭推给了老师，老师推给了学生自己。她提出：“要知道这些孩子就是不懂，他们要懂了就不会这样做了。”</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">侯露直言：“很多父母教育不当，就会埋下隐患，孩子早晚会出事，不在学校出事也会在单位出事。”她认为，比如在修改《中华人民共和国预防未成年人犯罪法》时，应该把对校园暴力的预防纳入教育范畴。“我们是不是对校园暴力能够有一个专门的法？管住这个事。”她提出建议。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">法学专家、坐在对面的团中央维护青少年权益部副部长姚建龙回应：“针对校园欺凌进行专项立法是很多国家的经验和做法，不论是采用行政法规的形式还是专门法律的形式，我国确实也可以考虑。”</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">刘宏艳是全国人大代表，也是辽宁省北票市的一名高中教师：“未成年人犯罪的年龄越来越低、越来越小，犯罪的群体和规模不断地扩大。”《未成年人保护法》第五十四条规定：“对违法犯罪的未成年人，实行教育、感化、挽救的方针，坚持教育为主、惩罚为辅的原则。对违法犯罪的未成年人，应当依法从轻、减轻或者免除处罚。”刘宏艳建议把这一条修改为“对违法犯罪的未成年人在服刑期间要充分地实行教育、感化、挽救的方针，坚持文化教育为主，劳动教育为辅的原则，对违法犯罪的未成年人依法从轻、减轻或者免除处罚。”</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">我国法律规定，14周岁以下的未成年人犯罪不负刑事责任。近年来，一直有社会舆论呼吁降低这一年龄，刘宏艳也不例外。“这种标准没法约束一些犯罪。”刘宏艳给出理由。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: center;\"><strong>支持：引入“恶意补足年龄”来震慑这些孩子</strong></p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">刘宏艳的观点，得到了坐在身边的王家娟老师的支持。王家娟是全国人大代表，是辽宁省辽阳市的一位高中老师，已经当了26年的班主任。在普法的时候，王家娟曾经问过一些学生：“你做错了事该怎么办？”“找我爸，找我妈摆平。”有孩子回答，这让她有些失望。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">从2013年开始，王家娟一直关注青少年犯罪情况。她举例，在一个学校，有学生组织成“青龙帮”“虎头会”的“帮派”，成员基本上是富二代。一次，“青龙帮”成员到“虎头会”成员的寝室，把对方打得头破血流。王家娟得知后找到学校了解情况，学校却回答“没事，学生就破了点皮”。学校也没有给公安机关报案，双方就私了了。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">“媒体每年报道校园欺凌事件只是冰山一角，《未成年人保护法》实施到今天已经有26年了，为什么我们国家的青少年还屡屡受到侵害？就因为《未成年人保护法》是一个‘软法’，只有修《刑法》才能硬起来。”带着东北口音的王家娟说出自己的观点。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">“我今年提出修改《未成年人保护法》，(提出)降低刑事责任年龄。”她给出的理由是：“(很多孩子认为)我是未成年人，你能把我怎么的？对他们从轻处罚甚至是免责，就等同放纵他们犯罪。”</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">《刑法》第十七条对刑事责任年龄规定：“已满十六周岁的人犯罪，应当负刑事责任。已满十四周岁不满十六周岁的人，犯故意杀人、故意伤害致人重伤或者死亡、强奸、抢劫、贩卖毒品、放火、爆炸、投毒罪的，应当负刑事责任。”王家娟提出建议，对于12~14周岁的未成年人，如果在这个年龄范围内累犯的话，应该取消刑事豁免权，追究其刑事责任。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">有一种观点是“恶意补足年龄”。在一些西方国家，对于10岁和10岁以上不满14岁的人制定了特殊规则。这些人年龄小，被推定为无实施犯罪行为的能力，但是，如果证明某个儿童“对危害行为有辨别能力”，即了解行为是错误却还有意为之，就可反驳这一推定，属于“恶意补足年龄”。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">王家娟进一步建议，有一些案件性质非常轻微，出于保护未成年人可以采用不起诉的方式。未成年人保护，不应该盲目地反对降低刑事责任年龄，而是对特别恶意的犯罪采用“恶意补足年龄”，震慑住这些孩子。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: center;\"><strong>反对：不降低“刑责年龄”不等于“放羊”</strong></p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">“我理解大家降低刑事犯罪年龄的心情，看到个案我们会有情绪，但是立法还是要理性。北京师范大学刑事法律科学研究院副院长、教授宋英辉回应这个问题，他并不主张降低14周岁这一刑事责任年龄。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">宋英辉直言，未成年人违法犯罪多数还是由家庭和社会环境造成的。如果这些孩子换一个环境就是很好的孩子，所以随意降低年龄，把家庭和社会的责任让未成年人自己承担就有点不公平了。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">从医学角度，他也给出理由，有大量的医学证明，真正成熟的人是在20~26岁之间，所以未成年人情绪控制和行为控制的能力跟成年人不一样，有时候控制不了自己。另外，他的认知能力不足，这种情况下简单的加重刑罚解决不了根本问题。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">他最担心，未成年人出现问题之后，社会对他们的干预方式是非专业的，简单用处罚方式对待这些孩子，他们的人格形成、再融入社会和正常人际关系交往都可能会形成障碍，甚至会形成反社会的倾向。“社会为此付出的后续代价会更多，所以各个国家不太强调简单的刑罚处罚！”他说</p><p style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;</p><p style=\"text-align: justify;\">有很多人关心，不降低14周岁的刑事责任年龄，是不是意味着没有其他办法？</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">“恰恰我们要补足这块儿，不降低刑事责任年龄不给他定罪，不是不管他。”他认为，如果建立了比较完备的教育矫正体系，民众对降低责任年龄的呼声也不会这么高，管束的时间可以更长，比如定罪3年，管束时间可能达到5年或者6年甚至更长时间，直到把他矫正好为止。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">道理很简单，少年案件与成年人案件相比，处理起来的差别太大了。作为学者，宋英辉还注意到，有的犯罪小孩儿抓了放，放了抓，一直解决不了根本问题。他强调，处理未成年人案件的机构一定要专业，比如应探讨是否设立专门的少年警务机构。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">宋英辉提出：“要知道，一个国家的司法体系中没有少年法庭、未成年人检察机构、少年警务，就像一个国家的医院没有儿科一样。”他警告称，如果机构不专业，光靠降低刑事责任年龄，不仅不能有效控制犯罪，而且可能制造出更多严重犯罪行为，办案效果有时候会相反。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">宋英辉还观察到一个现象，北京的检察机关做了统计，在学校，违法犯罪的孩子劝退率在60%，劝退之后的复学率是23%。劝退之后大部分孩子不能上学，那么问题就来了，这样下去他们的重复犯罪率就会越高。要知道，学校是教育人的地方，不能一味把学生劝退或者开除，实在管不了的孩子，可以进入专门学校或者其他机构，但不能轻易推到社会上，一推了之。</p><p><br style=\"text-align: left;\"/></p>', '0', '1', '0', '7', '1502967300', '0', '0', '0', '0', '', '', null, '在安徽的某艺术学校，几个如花似玉的女孩群殴另一位女同学，还拿凉水浇，造成其心理出现问题。最终，学校把带头打人的13岁孩子开除了。 ', '校园暴力,未成年人,立法', '2017-07-24');
INSERT INTO `strategy` VALUES ('45', '关爱特殊儿童 北师大融合教育研究中心实验基地落户青岛', '/uploads/images/c13BN8BMSk1502854742.jpg', '<p style=\"text-align: justify;\">北京师范大学融合教育研究中心实验基地揭牌仪式7月30日在青岛市二七剧场举行。伴随实验基地的揭牌，青岛幸福之家融合幼儿园正式成为北师大融合教育研究中心首家非公立实验基地。此外，五位国内知名院校专家成为特聘指导专家。</p><p style=\"text-align: justify;\"><br/></p><p><img src=\"/uploads/ueditor/php/upload/image/1502854533254882.jpg\" title=\"723b0a413dd62901c6395332e913929a.jpg\" alt=\"微信图片_20170730205627.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; max-width: 100%; display: block;\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">据了解，青岛幸福之家融合幼儿园成立于2007年，是全国首家以1:1比例模式进行融合的幼儿园，即将普通儿童与特殊儿童进行1：1融合。幼儿园始终秉承让所有的幼儿都能够接受平等的教育。到目前为止，已将500名自闭症儿童送入普通小学，同时培养了近千名品学兼优的普通幼儿。</p><p style=\"text-align: justify;\"><br/></p><p><img src=\"/uploads/ueditor/php/upload/image/1502854533990047.jpg\" title=\"d8f4393c8b44298a97a710c902a75655.jpg\" alt=\"微信图片_20170730205643.jpg\" style=\"margin: 0px auto; padding: 0px; border: 0px; max-width: 100%; display: block;\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">作为本次活动的重头戏，现场，北京师范大学融合教育研究中心与青岛幸福之家融合幼儿园签约并揭牌，幸福之家融合幼儿园正式成为北京师范大学融合教育研究中心融合实验基地。这代表着青岛幸福之家融合幼儿园学前融合教育的成果得到了高校的认可与支持。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">北京师范大学邓猛教授对幸福之家的教学现状给予了高度的评价，他表示，幸福之家有三点值得肯定，首先是零拒绝，其次是先进的教育模式；再者是以儿童综合发展为目标，以社会性发展为核心，以自然情景为依托，以游戏互动为基础。一个普通儿童带动一个特殊儿童，幸福之家营造了一个融合的集体，让所有的儿童在这里都得到爱的教育，实现零距离的融合教育。</p><p><br/></p>', '0', '1', '0', '7', '1502967157', '0', '0', '0', '0', '', '', null, '北京师范大学融合教育研究中心实验基地揭牌仪式7月30日在青岛市二七剧场举行。 ', '公益,特殊儿童,北师大', '2017-07-31');
INSERT INTO `strategy` VALUES ('46', '围绕“融合·支持”主题 融合教育国际研讨会在京举行', '/uploads/images/yQ22iNcgXu1502857408.jpg', '<p style=\"text-align: justify;\">5日上午，2017年北京海淀区融合教育国际研讨会开幕。在为期两天的研讨会中，来自美英日俄等国家及国内的30余名融合教育专家学者和400名与会代表围绕“融合·支持”的主题，对不同国家和地区融合教育发展模式研究、课程规划、支持策略等问题进行全面的分析和比较；并就融合教育支持保障体系、环境建设、支持策略和特殊学生的行为管理等议题开展深入探讨。研讨会旨在通过密切的专业交流，相互借鉴研究成果，共同推动融合教育事业发展。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">专家认为，全面推进融合教育发展符合世界教育潮流，旨在促进每个残障孩子都能接受适合的教育。众多融合教育理论与实践的探索者、推动者们在本土化探索和实践方面正在做出可喜成绩，构建符合中国国情的真正可以推广的支持保障模式和体系。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">北京海淀区在融合教育发展方面走在全国前列，并逐步建立了以特殊教育研究与指导中心为指导、以融合教育为主体、以特殊教育学校为骨干、以送教上门等其他教育形式为补充的办学新格局，融合教育覆盖区内134所中小学，建有84间资源教室，居北京市首位，并配有专兼职资源教师。</p><p><br/></p>', '0', '1', '0', '7', '1502967339', '0', '0', '0', '0', '', '', null, '5日上午，2017年北京海淀区融合教育国际研讨会开幕，30余名融合教育专家学者和400名与会代表围绕“融合·支持”的主题展开了探讨。 ', '融合教育,研讨会,北京', '2017-07-06');
INSERT INTO `strategy` VALUES ('47', '地震来了我们如何自救？', '/uploads/images/YvgZPZoxD71502857660.jpg', '<p style=\"text-align: center;\"><strong>新闻背景</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">8月8日21时19分46秒，四川阿坝州九寨沟县发生7.0级地震。</p><p style=\"text-align: justify;\">8月9日7时27分，新疆博尔塔拉州精河县发生6.6级地震。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">这两场灾难牵动着全国人民的心。地震是不可抗拒的自然灾害，发生地震后最重要的是抢救生命。抢救生命要讲究科学，否则会事与愿违。</p><p><br style=\"word-wrap: break-word; overflow: hidden; margin: 0px; padding: 0px; text-indent: 2em;\"/></p><p style=\"text-align: center;\"><strong>地震容易产生的损伤</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">颅脑、四肢、内脏损伤以及大出血、休克等；因口鼻、胸部受压或有毒性气体所导致的窒息；脊柱脊髓损伤；挤压综合征；断水断食导致酮症酸中毒、低血糖症；地震诱发火灾引起的烧伤；冻伤、中暑；有毒有害物质泄漏引起的中毒；海啸引起的淹溺；核泄漏引起的核辐射；精神障碍；其他，因环境潮湿、寒冷或闷热、污浊，使代谢紊乱、抵抗力下降。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: center;\"><strong>地震避险逃生原则</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">应急疏散。听从指挥、有秩序、防止拥挤、踩踏、避免盲目跳楼。</p><p style=\"text-align: justify;\">就地躲避。家具旁、内墙角、小开间房间。</p><p style=\"text-align: justify;\">在两次地震之间疏散到空旷的地方。</p><p style=\"text-align: justify;\">切断电源、关掉煤气开关，防止起火爆炸。</p><p style=\"text-align: justify;\">不能乘电梯，不能跳楼。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: center;\"><strong>地震避险逃生的方法</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">通常，一次地震的持续时间一般不超过1分钟。地震发生的最初10～15秒钟（平均12秒）内是上下的纵向震动，此时相对安全。逃生的时间平均只有这12秒钟。然后是破坏力极大的横向震动，建筑物倒塌都发生在这一时段。地震发生时，当人们从恐惧中清醒过来，再做出如何逃生的决定大概也就剩下几秒钟的时间了。此时最佳的自救方法是克服恐惧、就地自避。在寻求躲避时不要顾及任何财产，灾害面前人的生命是最宝贵的，可以随手拿一瓶水和食物再行躲避，一旦被倒塌物掩埋拥有水和食物也就有了生存下去的希望。身体蹲下或坐下，尽量蜷曲身体，降低身体重心。抓住桌腿等牢固的物体。保护头颈、眼睛、口鼻。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">不要随便点明火，甚至不能打手机，因为空气中可能有易燃易爆气体。地震时，要注意保护头部。可以用双手将枕头、面盆、书包等顶在头上，护住头部。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: center;\"><strong>不同场地的自救方法</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">在影剧院、体育馆等公众场地，应沉着冷静，不要尖叫乱跑。地震时场内很可能断电，不要慌，马上就地蹲下或躲在排椅下，注意避开吊灯、电扇等悬挂物，用皮包等物保护头部，等地震过后，听从工作人员指挥，有组织地撤离。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">商场、书店、展览馆等，同样不要慌不择路，否则容易出现踩踏事件。明智的选择是找到结实的柜台、商品（如低矮家具等）或柱子边，以及内墙角处就地蹲下，用手或其他东西护头，远离玻璃门窗和玻璃橱窗，也可在通道中蹲下，等待地震平息，有秩序地撤离。随人流行动时，要避免被挤到墙壁或栅栏处。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">上课的学生要在老师的指挥下迅速抱头、闭眼，躲在各自的课桌下，绝不能乱跑或跳楼，地震后，有组织地撤离教室，到就近的开阔地带避震。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">在行驶的公共电、汽车上时，要抓牢扶手，低头，以免摔倒或碰伤；可降低重心，躲在座位附近，以防发生意外事故。要等车停稳、地震过去之后再下车。司机要关好车窗，不锁车门，车钥匙应留在车上，并和同车人一起行动。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">在家遇地震，找支撑三角形空间躲避。首先关闭电闸和煤气，将炉火扑灭。如果住在平房，且离门很近，则应冲出门外。如果住在楼房中，特别是高层楼房，切忌上阳台，不要乘电梯，不要下楼梯，不要到处跑。更不要跳楼！可以躲到结实的床、桌下，或躲进跨度较小的房间，如卫生间或厨房的墙根躲避。因为墙根为死角，坍塌物与墙角之间往往会形成一个三角形的支撑空间，地震逃生学称这个空间叫做生命金三角。另外要注意保护头部，以防异物砸伤。要用口罩捂住嘴和鼻子，身体取低位。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">如果恰巧在电梯里，不要试图撬门，要用力拍打或敲击电梯门，弄出声响引人注意，等待救援。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">初震过后所有人都要尽快撤离到广场、公园等地，以避余震。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">在街上行走的人应双手交叉放在头上，最好用合适的物件罩在头上、跑到空旷的地方去。注意避开高大的建筑物，特别是有玻璃墙的高建筑物、烟囱、水塔、广告牌、路灯、大吊车、砖瓦堆、水泥预制板墙、油库、危险品仓库、立交桥、过街天桥等。还要注意避开危旧房屋、狭窄的街道等危险之地。此时人员可以进入路旁大楼里，以免砸伤。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">地震时正在山区、水边，应迅速离开山边、水边等危险地，以防滑坡、地裂、涨水、海啸等突发事件。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">如果不幸被困，尽力给自己扩大生存空间。如果不幸被埋在废墟里，则要设法移动身边可动之物，扩大生存空间，并进行加固，以防余震。这时不要用明火，防止易燃气泄漏爆炸。要捂住口鼻，防止附近有毒气泄漏。然后找机会呼救，等待救援。</p><p><br/></p>', '0', '1', '0', '7', '1502967109', '0', '0', '0', '0', '', '', null, '地震是不可抗拒的自然灾害，发生地震后最重要的是抢救生命。抢救生命要讲究科学，否则会事与愿违。 ', '地震,自救,常识', '2017-08-16');
INSERT INTO `strategy` VALUES ('48', '留守一定会导致心理问题吗？', '/uploads/images/2y0NWaT0fC1502858051.jpg', '<p style=\"text-align: justify;\">留守儿童缺乏与父母的情感交流，有些孩子觉得自己失去了束缚，沉溺于玩乐之中，学习成绩下降。虽然父母外出打拼会使家庭经济条件有所改善，但物质上的满足难以弥补心灵上的缺憾。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: center;\"></p><p style=\"text-align: center;\"><strong>留守儿童心理问题现状</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\"><strong>1 孤僻抑郁</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">留守儿童自小便与父母相隔两地，尽管身边有爷爷奶奶的陪伴，但这与父母之爱比起来还是有明显不同的。相对来说，儿童是很容易满足的，只要给他们好吃的或者陪他们玩，他们就会很开心。但是儿童也具有强烈的攀比心理，他们喜欢与其他的小朋友进行比较，父母不在身边的留守儿童自觉没有资本与那些父母在身边的儿童相比，觉得自己家里贫穷，父母才会丢下自己外出打工。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">留守儿童不理解父母的良苦用心，他们觉得自己被父母抛弃了，感到无助、抑郁、孤独，这种想法使一部分留守儿童产生了极大的自卑心理，从而造就了他们孤僻的性格。</p><p><br/></p><p style=\"text-align: justify;\"><strong>2 不善交流</strong></p><p><br/></p><p style=\"text-align: justify;\">父母是孩子的榜样，父母的一言一行莫不影响着孩子的成长。父母可以指导孩子的行为习惯、心理健康等方面向着正确的方向发展。可是对于留守儿童来说，父母无法随时密切关注他们的成长，留守儿童身边没有父母的陪伴，无论遇到什么事情都要自己承担，就算受了委屈也要自己忍着，压抑着自己的情绪，久而久之他们也就不愿再向父母倾诉，更不愿意与他人分享自己的情感世界。</p><p><br/></p><p style=\"text-align: justify;\">调查显示，仅有28.1%的父母会经常与留守儿童进行沟通，15.4%的父母从来不与孩子谈心。沟通情况对心理健康造成的影响最为显著，不少女孩子会将自己的内心世界封闭，而有些男孩子则会惹事生非，严重破环自己的人际关系。随着父母在外务工时间的增多，心理问题得不到及时解决，孩子就更不愿与人进行沟通交流。</p><p><br/></p><p><img src=\"http://p0.ifengimg.com/pmop/2017/0812/3E94D31DC904EAED02C34D6C5AFA61973E927AD7_size5_w240_h200.jpeg\" alt=\"\" style=\"border: 0px; vertical-align: bottom; display: block; margin: 0px auto; max-width: 100%; height: auto;\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\"><strong>3 厌学心理</strong></p><p><br/></p><p style=\"text-align: justify;\">父母在外务工，与孩子相隔甚远，而大多数爷爷奶奶的文化水平都比较低，在学习上不能指导孩子完成作业，而父母又不能有效地监管孩子的学业，再加上孩子的自控力本来就差，他们会放任自己去玩游戏，而使学业荒废。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">另外，留守儿童心理比较孤单，对父母有些想念，在课堂上容易分散注意力，不能够专注于学习。在学习上不会的东西多了，就会感到学习压力大，厌恶学习，进而对学习失去兴趣，甚至有的留守儿童干脆放弃学习，甚至产生辍学的想法。</p><p><br/></p><p style=\"text-align: justify;\"><strong>4 对生活敏感</strong></p><p><br/></p><p style=\"text-align: justify;\">留守儿童缺乏父母的保护，心理比较敏感。生活中一点不愉快的小事情就有可能让他觉得自己被欺负了，产生仇恨心理，对他人充满敌意。而大多数老人都比较疼爱孩子，对孩子的要求无论是否合理都尽量满足，这助长了孩子以自我为中心的意识，认为每个人都应该围着自己转，一旦遇到不顺心的事，孩子就会对他人发脾气、怨恨他人。</p><p><br/></p><p style=\"text-align: justify;\"><strong>5 不良行为习惯</strong></p><p><br/></p><p style=\"text-align: justify;\">有学者调研发现，留守儿童行为问题的发生率为41.3%，远高于全国的标准12.97%。儿童期是养成良好行为习惯的关键时期，而外出务工的父母不能对孩子进行有效的监管，使得一些留守儿童出现违反学校纪律、对长辈的管教产生逆反等心理行为问题。比如，一些孩子沉溺于网络游戏，还有的孩子结交了一些社会上的不良朋友，诱使自己走上违法犯罪之路。孩子的自控能力差，久而久之就形成了不良的行为习惯。</p><p><br/></p><p style=\"text-align: center;\"><strong>优秀的留守儿童</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">小金为安徽省庐江县某学校的学生，他的父母远在广州务工，自小跟随年迈的爷爷、奶奶一起生活。爷爷身体不好，生活长期难以自理。从懂事开始，小金为就在每天放学后，一边做功课，一边照看爷爷。在爷爷心情不好的时候，他总是讲些有趣的事情，让爷爷高兴。在农忙时节，他不但会承担日常的家务劳动，如煮饭、扫地、喂猪，而且还到田间地头帮奶奶干农活，尽量减轻奶奶的负担。</p><p><span style=\"text-align: justify;\"><br/></span></p><p style=\"text-align: justify;\"><span style=\"text-align: justify;\">在学校，小金为是一个品学兼优、全面发展的好学生，每次期末考试都能取得非常好的成绩。他兴趣广泛，是“校园广播站”的播音员，也是学校美术小组、科技小组、写作小组、环保小组等多个课外兴趣小组的成员之一。他还是班里的班长，热心帮助学习上有困难的同学。作为留守儿童的一员，在老师的帮助下，他还组织了“留守学生小分队”，带领留守的同学们开展了很多有益的活动，比如帮助特困生、帮助孤寡老人。</span><br/></p><p><br/></p><p style=\"text-align: justify;\">幼稚的面庞，粗糙的小手，乐观的生活态度，优异的表现……小金为就是这样用言行告诉人们：留守儿童也能够活出自己的风采，也能够表现出当代中国少年应有的品质和风范。</p><p><br/></p><p style=\"text-align: center;\"><strong>不是所有留守都会出现问题</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">留守儿童最初是作为一个紧迫的“社会问题”提出的，媒体报道的极端案例更是直接推动了全社会对它的关注。</p><p><br/></p><p style=\"text-align: justify;\">长期以来，留守儿童被贴上“问题儿童”的标签，被“问题化”或“污名化”的倾向比较突出，甚至出现了把留守儿童等同于“问题儿童”的趋势。人们也普遍认为，留守儿童处于父母亲情的相对缺失和家庭监管力度相对不足的环境下，极易产生认识、价值上的偏离和个性、心理发展的异常，有的留守儿童甚至走上违法犯罪道路。这实际上反映了人们所持有的一种观点：处境不利的留守儿童或多或少，或早或晚地存在或轻或重的缺陷或问题。媒体报道以及调查的结果使大众不禁怀疑甚至认同了留守儿童就是问题儿童的结论。</p><p><br/></p><p><img src=\"http://p0.ifengimg.com/pmop/2017/0812/FD0DA9DD4AA25DEC764DB59117DBD66E3A97109E_size43_w499_h453.jpeg\" alt=\"\" style=\"border: 0px; vertical-align: bottom; display: block; margin: 0px auto; max-width: 100%; height: auto;\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">然而近几年的研究发现，并不是所有的留守儿童都出现了“问题”，其中不乏小金为这样的优秀儿童，他们不仅没有因为父母外出打工而产生各种问题，有的甚至比非留守儿童发展得更好。这不禁引起我们思考：留守儿童有积极发展的可能吗？</p><p><br/></p><p style=\"text-align: justify;\">“问题儿童”的标签，被“问题化”或“污名化”的倾向比较突出，甚至出现了把留守儿童等同于“问题儿童”的趋势。人们也普遍认为，留守儿童处于父母亲情的相对缺失和家庭监管力度相对不足的环境下，极易产生认识、价值上的偏离和个性、心理发展的异常，有的留守儿童甚至走上违法犯罪道路。这实际上反映了人们所持有的一种观点：处境不利的留守儿童或多或少，或早或晚地存在或轻或重的缺陷或问题。媒体报道以及调查的结果使大众不禁怀疑甚至认同了留守儿童就是问题儿童的结论。</p><p><br/></p><p style=\"text-align: justify;\">笔者在访谈中也发现，同大众对留守儿童的认知不同，绝大部分留守儿童对自己有着积极的认知评价，比如“我是一个乐于助人的人”，“我很爱跟同学交往，有很多朋友”，“我很爱学习”。留守儿童的同伴对其也有很积极的评价，比如“他喜欢帮助别人”，“学习成绩好”，“自理能力强”。一位教师在访谈中提到，虽然某同学是一名双亲外出的留守儿童，但她并没有表现出问题行为，相反其在生活、学习上的表现得很优秀。父母外出打工的不易，更激发了她的学习劲头，她爱帮助和照顾同学，自理能力强，在家中她也是个孝顺懂事的好孩子，经常帮爷爷奶奶做些力所能及的家务，帮助照看自己的妹妹。</p><p><br/></p><p><img src=\"http://p0.ifengimg.com/pmop/2017/0812/9F666B2F946883E43DAB22A8F3EAF5E1F6B1F84D_size15_w640_h480.jpeg\" alt=\"\" style=\"border: 0px; vertical-align: bottom; display: block; margin: 0px auto; max-width: 100%; height: auto;\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">所以，作为成长和发展的个体，留守儿童同样蕴含着巨大的成长力量，具有积极发展的潜力，他们与其他儿童一样都具有良好发展的态势和可能。我们在看待留守儿童现象时，不能只关注负面影响、心理和行为问题，而是要多用积极发展的眼光看待他们，发掘他们自身发展的潜力。</p><p><br style=\"text-align: left;\"/></p>', '0', '0', '1', '7', '1502966994', '0', '0', '0', '0', '', '', null, '留守儿童缺乏与父母的情感交流，有些孩子觉得自己失去了束缚，沉溺于玩乐之中，学习成绩下降。虽然父母外出打拼会使家庭经济条件有所改善，但物质上的满足难以弥补心灵上的缺憾。    ', '留守儿童,心理问题,儿童', '2017-08-12');
INSERT INTO `strategy` VALUES ('49', '70.2万社会组织、827.0亿元捐赠、28.8万社工，公益行业大数据来了！', '/uploads/images/CLbXaw4mrQ1502858448.jpg', '<p style=\"text-align: justify;\">8月初，民政部发布了《2016年社会服务发展统计公报》。《公报》显示，截至2016年底，全国共有社会组织70.2万个，比上年增长6.0%;吸纳社会各类人员就业763.7万人，比上年增长3.9%。全年共查处社会组织违法违规案件2363起，其中取缔非法社会组织16起，行政处罚2347起。2016年全年包括社会组织在内全年共接收社会捐赠款827.0亿元，比上年增长26.4%。全国持证社会工作者共计28.8万人，其中社会工作师6.9万人，助理工作师21.9万人。</p><p><br/></p><p style=\"text-align: center;\"><strong>社会组织快速发展基金会增速最快</strong></p><p><br/></p><p style=\"text-align: justify;\">在70.2万个社会组织中，最多的是民办非企业单位，有36.1万个，但增速最快的是基金会，共5559个，比上年增长16.2%。</p><p style=\"text-align: justify;\"><br/><img src=\"http://p0.ifengimg.com/pmop/2017/0811/55BFF79FC2E7C7F05F7B633182DC0A93CAFA0361_size27_w646_h315.jpeg\" alt=\"\" style=\"border: 0px; vertical-align: bottom; display: block; margin: 0px auto; max-width: 100%; height: auto;\"/><br/></p><p style=\"text-align: justify;\">全国共有社会团体33.6万个，比上年增长2.3%。其中：工商服务业类3.8万个，科技研究类1.6万个，教育类1.0万个，卫生类0.9万个，社会服务类4.8万个，文化类3.5万个，体育类2.5万个，生态环境类0.6万个，法律类0.3万个，宗教类0.5万个，农业及农村发展类6.1万个，职业及从业组织类2.0万个，其他5.8万个。全年共查处社会团体违法违规案件1565起，其中取缔非法社会团体9起，行政处罚1556起。</p><p><br/></p><p style=\"text-align: justify;\">全国共有各类基金会5559个，比上年增长16.2%。其中：公募基金会1730个，非公募基金会3791个;民政部登记的基金会245个(其中：涉外基金会9个、境外基金会代表机构29个)。公募基金会和非公募基金会共接收社会各界捐赠625.5亿元。全年对基金会作出行政处罚15起。</p><p><br/></p><p style=\"text-align: justify;\">全国共有民办非企业单位36.1万个，比上年增长9.7%。其中：科技服务类1.8万个，生态环境类444个，教育类19.9万个，卫生类2.5万个，社会服务类5.4万个，文化类1.8万个，体育类1.7万个，法律类617个，工商业服务类3459个，宗教类102个，国际及其他涉外组织类9个，其他2.4万个。全年共查处民办非企业单位违法违纪案件783起，其中取缔非法民办非企业单位7起，行政处罚776起。</p><p><br/></p><p style=\"text-align: center;\"><strong>全年共接收社会捐赠款827.0亿元</strong></p><p><br/></p><p style=\"text-align: justify;\">慈善捐赠事业在2016年也处于高速发展中。《公报》显示2016年全年包括社会组织在内全年共接收社会捐赠款827.0亿元，比上年增长26.4%。</p><p style=\"text-align: justify;\"><br/></p><p><img src=\"http://p0.ifengimg.com/pmop/2017/0811/5C2F55243B0728A7B838D33C78E4D16C28AE47E0_size24_w646_h320.jpeg\" alt=\"\" style=\"border: 0px; vertical-align: bottom; display: block; margin: 0px auto; max-width: 100%; height: auto;\"/></p><p><br/></p><p style=\"text-align: justify;\">其中民政部门直接接收社会各界捐款40.3亿元，各类社会组织接收捐款786.7亿元。全年各地民政部门直接接收捐赠物资价值折合人民币7.4亿元，捐赠衣被6638.3万件。间接接收其他部门转入的捐赠物资折款1.4亿元，社会捐款5.9亿元，衣被488.0万件。全年有1165.8万人次困难群众受益。全年有931.0万人次在社会服务领域提供了2522.6万小时的志愿服务。</p><p><br/></p><p style=\"text-align: justify;\">作为公益事业重要的资金来源，彩票公益金在2016年也有所增长。2016年中国福利彩票销售2064.9亿元，比上年增加49.8亿元，增长2.5%。全年筹集福利彩票公益金591.5亿元，比上年增长4.9%。全年民政系统共支出彩票公益金268.3亿元，比上年减少20.6亿元，下降7.1%，其中用于抚恤7.1亿元，退役安置0.8亿元，社会福利172.9亿元，社会救助30.0亿元，自然灾害救助2.7亿元。</p><p><br/></p><p style=\"text-align: center;\"><strong>全国各类养老服务机构和设施14.0万个</strong></p><p><br/></p><p style=\"text-align: justify;\">在老龄服务方面，《公报》显示，截至2016年底，全国60岁及以上老年人口23086万人，占总人口的16.7%，其中65岁及以上人口15003万人，占总人口的10.8%。</p><p style=\"text-align: justify;\"><br/></p><p><img src=\"http://p0.ifengimg.com/pmop/2017/0811/5D18E69CECC8349A2610863A7201493BFDCA2A66_size19_w556_h314.jpeg\" alt=\"\" style=\"border: 0px; vertical-align: bottom; display: block; margin: 0px auto; max-width: 100%; height: auto;\"/></p><p><br/></p><p style=\"text-align: justify;\">全国共有老龄事业单位1828个，老年法律援助中心1.9万个，老年维权协调组织7.0万个，老年学校5.4万个、在校学习人员710.2万人，各类老年活动室35.9万个;享受高龄补贴的老年人2355.4万人，比上年增长9.3%;享受护理补贴的老年人40.5万人，比上年增长52.8%;享受养老服务补贴的老年人282.9万人，比上年增长9.7%。</p><p><br/></p><p style=\"text-align: justify;\">全国各类养老服务机构和设施14.0万个，比上年增长20.7%，其中：注册登记的养老服务机构2.9万个，社区养老服务机构和设施3.5万个，社区互助型养老设施7.6万个;各类养老床位合计730.2万张，比上年增长8.6%(每千名老年人拥有养老床位31.6张，比上年增长4.3%),其中社区留宿和日间照料床位322.9万张。</p><p><br/></p><p style=\"text-align: center;\"><strong>1.9亿人次不同程度受灾</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">在防灾减灾救灾方面，2016年全国各类自然灾害共造成1.9亿人次不同程度受灾，因灾死亡失踪1706人，紧急转移安置910.1万人次;农作物受灾面积26220.7千公顷，其中绝收面积2902.2千公顷;倒塌房屋52.1万间，损坏房屋334.0万间;因灾直接经济损失5032.9亿元。</p><p><br/></p><p style=\"text-align: justify;\">国家减灾委、民政部共启动国家救灾应急响应22次，向各受灾省份累计下拨中央自然灾害生活补助资金79.1亿元(含中央冬春救灾资金57.1亿元)，紧急调拨4.1万顶救灾帐篷、15万床棉被、1.6万件棉大衣、2.5万个睡袋、2.3万张折叠床等生活类中央救灾物资。</p><p><br/></p><p style=\"text-align: center;\"><strong>全国低保对象超6000万人</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">在社会救助方面，截至2016年底，全国有城市低保对象855.3万户、1480.2万人。全年各级财政共支出城市低保资金687.9亿元。2016年全国城市低保平均标准494.6元/人·月，比上年增长9.6%。</p><p style=\"text-align: justify;\"><br/></p><p><img src=\"http://p0.ifengimg.com/pmop/2017/0811/68243BB1FD635C7B4FBB907EFFDD1F37EFC4C8EA_size21_w562_h284.jpeg\" alt=\"\" style=\"border: 0px; vertical-align: bottom; display: block; margin: 0px auto; max-width: 100%; height: auto;\"/></p><p><br/></p><p style=\"text-align: justify;\">全国有农村低保对象2635.3万户、4586.5万人。全年各级财政共支出农村低保资金1014.5亿元。2016年全国农村低保平均标准3744.0元/人·年，比上年增长17.8%。</p><p><br/></p><p style=\"text-align: justify;\">截至2016年底，全国农村特困人员救助供养496.9万人，比上年减少3.9%。全年各级财政共支出农村特困人员救助供养资金228.9亿元，比上年增长9.0%。2016年临时救助累计救助850.7万人次，支出救助资金87.7亿元，平均救助水平1031.3元/人次，其中：救助非本地户籍对象24.4万人次。</p><p><br/></p><p style=\"text-align: justify;\">2016年资助参加基本医疗保险5560.4万人，支出资助参加基本医疗保险资金63.4亿元，资助参加基本医疗保险人均补助水平113.9元。2016年实施住院和门诊医疗救助2696.1万人次,支出资金232.7亿元，住院和门诊人次均救助水平分别为1709.1元和190.0元。 2016年全年累计资助优抚对象409.2万人次，优抚医疗补助资金36.2亿元，人均补助水平885.5元。</p><p><br style=\"text-align: left;\"/></p>', '0', '0', '1', '7', '1502966851', '0', '0', '0', '0', '', '', null, '截至2016年底，全国共有社会组织70.2万个，比上年增长6.0%;吸纳社会各类人员就业763.7万人，比上年增长3.9%。  ', '公益行业,大数据,统计', '2017-08-11');
INSERT INTO `strategy` VALUES ('50', '《老夫子》举办儿童公益首映', '/uploads/images/A1OnIHhBNx1502866319.jpg', '<p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">2011年1月24日上午，由华谊兄弟与老夫子哈媒体共同出品的电影《老夫子之小水虎传奇》举行了特别意义的“公益首映”，邀请同心实验学校、北京木兰花开文化发展中心、白云路小学等近百名师生共同参加。</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"font-size: 16px; line-height: 150%; font-family: Arial, sans-serif; border: 1px none windowtext; padding: 0px;\">&nbsp;</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">参加放映的学生大多是留京过年的农民工子弟，他们中的很多人从没有机会走进真正意义上的影院，更没有机会能与城市里的孩子同步享受到优秀电影。这次公益首映让他们有机会提前看到自己喜欢的动画片《老夫子》，孩子们都感到非常高兴。当天，华谊兄弟总裁王中磊更亲自带队《老夫子》明星配音团的蔡康永和李晨，和孩子们一起观影，并为孩子们提前庆贺春节。</span></span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 16px;line-height: 150%;font-family: 宋体;border: 1px none windowtext;padding: 0\"><br/></span></p><p style=\"text-align:center;line-height:150%\"><br/></p><p style=\"text-align:center;line-height:150%\"><span style=\"font-size: 16px;line-height: 150%;font-family: 宋体;border: 1px none windowtext;padding: 0\"><img src=\"/uploads/ueditor/php/upload/image/1502866260609693.png\" title=\"1502866260609693.png\" alt=\"image.png\"/></span></p><p style=\"line-height: 150%; text-align: center;\"><span style=\"line-height: 150%; border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\">农民工子弟第一次走进电影院，纷纷合影留念</span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size: 16px;line-height: 150%;font-family: Arial, sans-serif;border: 1px none windowtext;padding: 0\">&nbsp;</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 16px;line-height: 150%;font-family: Arial, sans-serif;border: 1px none windowtext;padding: 0\">&nbsp;</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">首次观看《老夫子》全片的蔡康永，更是在影片开始前就来到影院，悄悄坐在孩子们中间。观影结束后，蔡康永和李晨与孩子们现场互动，两位明星现场重现电影中的台词，小朋友们很快就猜到了他们配音的角色，最后老夫子和大番薯两樽玩偶的现身，让气氛更加热闹。电影制片人、华谊兄弟总裁王中磊先生还代表出品方给每位孩子发送了新年礼物——《老夫子》的电影衍生品如红包袋、小水虎面具等，他表示“今天这场观影就是《老夫子》的首映礼，这部电影是为孩子们拍的，今天的这些孩子还没有机会进过电影院，今天我们把他们接过来，希望能给孩子们留下美好记忆。今后这种形式我们还会陆续做，让更多的孩子们看到好电影”。“华谊兄弟从今年开始，会拿出很多精力在公益方面，今天迈出了一小步，希望我们能汇聚更多的力量，让更多的孩子们享受到电影的魅力</span> <span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">。”</span></span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 12px;line-height: 150%;font-family: Simsun, serif\">&nbsp;</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: justify;\"><span style=\"font-size: 16px;line-height: 150%;font-family: Arial, sans-serif;border: 1px none windowtext;padding: 0\">&nbsp;</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">在下午的发布会上，王中磊进一步表示：</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">“</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">这是华谊兄弟第一次尝试动画电影，我们做的很努力很用心，希望孩子们能喜欢。</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">”</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">此外他更现场透露：</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">“</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">此次参与《老夫子》配音的明星，都是无偿配音，一切为了孩子</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">”</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">。蔡康永则表示：</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">“</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">看到小朋友们笑声连连，对电影里的桥段都心领神会，我很高兴，尽管配音时连着工作八小时，最后连嗓子都发不出声，但一切都很值得。</span><span style=\"font-size: 16px; line-height: 150%; border: 1px none windowtext; padding: 0px;\">”</span></span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size: 16px;line-height: 150%;font-family: Arial, sans-serif;border: 1px none windowtext;padding: 0\"><br/></span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size: 16px;line-height: 150%;font-family: Arial, sans-serif;border: 1px none windowtext;padding: 0\">&nbsp;</span></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502866252153396.png\" title=\"1502866252153396.png\" alt=\"image.png\"/></p><p style=\"text-align:center;line-height:150%\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 12px;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; border: 1px none windowtext; padding: 0px;\">王中磊、蔡康永、李晨</span> <span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 150%; border: 1px none windowtext; padding: 0px;\">与打工子弟合影</span></span></p><p><br/></p>', '0', '0', '0', '4', '1502866406', '0', '1', '0', '0', '', '', null, '由华谊兄弟与老夫子哈媒体共同出品的电影《老夫子之小水虎传奇》举行了特别意义的“公益首映”。 ', '公益，华谊兄弟，首映，老夫子', '2011-01-24');
INSERT INTO `strategy` VALUES ('51', '公益放映走进大凉山', '/uploads/images/yJME59eNmA1502866782.jpg', '<p style=\"text-align: justify;\">2011年3月中旬，华谊兄弟公益基金走进大凉山做项目考察，并为索玛花爱心学校的孩子们放映了露天电影，凉山的孩子们第一次看电影，被跳动的画面深深吸引。露天电影带给我们的是美好回忆，带给山里的孩子们的更多是新鲜，全校三百多名彝族小学生围坐在操场上盼着。一年级的小朋友，刚刚开始学汉语，不太能看得懂，但依然边看边笑，动画片就是有这样的魔力。</p><p><br/></p><p style=\"text-align: justify;\">放映队的工作人员说，他们一年中大半年都是在各村落中度过，条件艰苦，在这样的条件下依然坚持传递美好，应该向他们致敬。学校校长说，要不是我们来，放映队很难走进学校，不是不关注这里，一年3269场乡村放映，对于11台长途跋涉的放映车，确实力不从心。我们来了，很好。</p><p style=\"text-align: justify;\"><span style=\"border: 1px none windowtext; font-size: 16px; font-family: 宋体; padding: 0px;\"><br/></span></p><p style=\"text-align: center;\"><span style=\"border: 1px none windowtext; font-size: 16px; font-family: 宋体; padding: 0px;\"><img src=\"/uploads/ueditor/php/upload/image/1502866712861623.png\" title=\"1502866712861623.png\" alt=\"image.png\"/></span></p><p><span style=\"border: 1px none windowtext; font-size: 16px; font-family: 宋体; padding: 0px;\"><br/></span></p><p style=\"text-align: center;\"><span style=\"border: 1px none windowtext; font-size: 16px; font-family: 宋体; padding: 0px;\"><img src=\"/uploads/ueditor/php/upload/image/1502866720115310.png\" title=\"1502866720115310.png\" alt=\"image.png\"/></span></p><p><span style=\"border: 1px none windowtext; font-size: 16px; font-family: 宋体; padding: 0px;\"><br/></span></p><p style=\"text-align: center;\"><span style=\"border: 1px none windowtext; font-size: 16px; font-family: 宋体; padding: 0px;\"><img src=\"/uploads/ueditor/php/upload/image/1502866732235996.png\" title=\"1502866732235996.png\" alt=\"image.png\"/></span></p><p><span style=\"border: 1px none windowtext; font-size: 16px; font-family: 宋体; padding: 0px;\"><br/></span></p>', '0', '0', '0', '6', '1503024669', '0', '0', '0', '0', '', '', null, '2011年3月，华谊兄弟公益基金走进大凉山，为孩子们放映了露天电影。  ', '华谊兄弟，公益，公益放映', '2011-03-20');
INSERT INTO `strategy` VALUES ('52', '9位明星献声，让盲童在儿童节“看见”电影', '/uploads/images/JvnvFlEStP1502867745.jpg', '<p style=\"text-align: justify;\">6月1日国际儿童节，华谊兄弟公益基金带着电影《有一天》无障碍版走进北京市盲人学校，为全校师生带去了电影的乐趣。北京市盲人学校也正是影片中其中一位参演的盲童小演员小莹所就读的学校。电影放映之后，小莹也与大家分享了拍摄影片时的回忆和观影感受。这个儿童节，她最高兴的是可以与同学们观看自己参与拍摄的电影。</p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502867512678780.png\" title=\"1502867512678780.png\" alt=\"image.png\"/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">盲校师生观看无障碍电影</span></p><p style=\"text-align: center;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502867521883494.png\" title=\"1502867521883494.png\" alt=\"image.png\"/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">学生在进行影后分享</span></p><p style=\"text-align: center;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502867526988733.png\" title=\"1502867526988733.png\" alt=\"image.png\"/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">2012年6月小莹参与的电影拍摄</span></p><p style=\"text-align: center;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502867536135193.png\" title=\"1502867536135193.png\" alt=\"image.png\"/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">《有一天》剧照</span></p><p style=\"text-align: center;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502867550101053.png\" title=\"1502867550101053.png\" alt=\"image.png\"/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">小莹回忆说第一次拍电影很紧张，还很累</span></p><p style=\"text-align: center;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: justify;\">无障碍电影是专门为了方便残障人士观看的经过加工过的电影，通过增补大量配音解说的方式，让视力障碍者了解电影的内容，享受电影艺术乐趣。作为中国第一部真正意义上的公益电影《有一天》，无论从创新的筹款拍摄方式，到独特的拍摄视角，都已获得了国际多个电影节及联合国的青睐。电影更是特别制作了无障碍版，邀请了陈数、顾长卫、黄轩、李静、谢娜、辛柏青、许晴、瑶淼、袁泉，9位明星解说员参与录制。</p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/uploads/ueditor/php/upload/image/1502867583183035.png\" title=\"1502867583183035.png\" alt=\"image.png\"/></span></p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: justify;\">改变从一天开始，希望未来可以有更多的盲童小朋友看到无障碍版的《有一天》。</p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p>', '0', '0', '0', '6', '1503023881', '0', '0', '0', '0', '', '', null, '2015年“六·一”国际儿童节，华谊兄弟公益基金带着电影《有一天》无障碍版走进北京市盲人学校，为师生带去了电影的乐趣。  ', '华谊兄弟，公益，无障碍', '2015-06-01');
INSERT INTO `strategy` VALUES ('81', '“零钱电影院” 护航成长 为爱升级', '/uploads/images/unqOXQIvcV1503026246.jpg', '<p style=\"text-align: justify;\">从今年3月起，我们开始了对2015年之前捐建的“零钱电影院”所在学校的回访工作。北京、云南、河南、甘肃……四个月的实地考察让我们收获满满：我们充分聆听了孩子们的观影感受，也与校长、老师们一起探讨他们在硬件设备上及软件辅导上的进一步需求。基于这些实地考察、多年实践探索总结以及海内外教育案例的研究，在这个7月，华谊兄弟公益基金的小伙伴们已经整装待发，“零钱电影院”升级计划即将全面开启！</p><p style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img width=\"1px\" height=\"2px\" src=\"http://ad.hbfoundation.cn/laravel-u-editor/themes/default/images/spacer.gif\" word_img=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image001.png\" style=\"background-image: url(&quot;http://ad.hbfoundation.cn/laravel-u-editor/themes/default/images/word.gif&quot;); background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; border: 1px solid rgb(221, 221, 221);\"/></p><p style=\"text-align: center;\"><strong>“可持续”公益升级：</strong><strong>不变的是长久的陪伴与关爱</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1503025878284373.png\" title=\"1503025878284373.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align: justify;\">6年前，我们带着简单的公益愿望前往偏远地区走访时，如上图般破旧的校舍让我们印象深刻。大多数学校虽有藏书但却基本闲置，仅有的几位老师需要教导全校几十甚至上百位学生的全部课程，孩子们虽正当天真烂漫的年纪，却意外的内向与害羞，不愿与人交流……<br/><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1503025903529269.png\" title=\"1503025903529269.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">作为在电影行业深耕23年的领军企业，我们深知电影不止是一种简单的娱乐方式，它“亦师亦友”，能带领这些孩子看到外面更广阔的世界，也能启发孩子们的想象力与创造力，丰富他们的精神世界。秉承这样的初衷，“零钱电影院”项目开始了为偏远地区儿童普及电影的征程。6年间，“零钱电影院”已在全国10个省的100所学校中生根发芽。传统的教室、闲置的图书馆变身为小小电影院，各类优秀的儿童电影、丰富多彩的图书和绘本陪伴着孩子们的成长，帮助他们打开心扉、开阔眼界。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">这100所 “零钱电影院”是我们公益之路上的一个里程碑，而我们真正的目标远不止这一个数字，更不是“捐赠”这一行动本身。我们不仅希望孩子们拥有干净明亮的教室与完善的设备，更希望看到孩子们与电影里的人物一起冒险、一起经历、一起欢笑，并从这些光影的故事中有所收获；我们不仅希望“零钱电影院”成为老师们教学的得力帮手，更希望它能给孩子们带去课本之外的成长，浇灌他们的精神世界。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">要让公益行动对贫困地区的孩子们产生可持续的影响，“硬件”的到位只是第一步，而如何让老师们及家长们从观念上意识到电影在儿童教育上的重要作用，并真正引导孩子们从电影中汲取精神养分，是我们一直在思考的问题，也是我们进行下一步升级的原因。我们希望通过持续的关注、沟通与指导，让“零钱电影院”更好地发挥作用，让孩子们在各个成长阶段都能从电影中获益。</p><p style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img width=\"1px\" height=\"2px\" src=\"http://ad.hbfoundation.cn/laravel-u-editor/themes/default/images/spacer.gif\" word_img=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image001.png\" style=\"background-image: url(&quot;http://ad.hbfoundation.cn/laravel-u-editor/themes/default/images/word.gif&quot;); background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; border: 1px solid rgb(221, 221, 221);\"/></p><p style=\"text-align: center;\"><strong>观念升级：</strong><strong>“以人为本”的儿童电影教育理念</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1503025937679707.png\" title=\"1503025937679707.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">我们在回访中发现，由于生活学习环境的差异，偏远地区的贫困儿童及留守儿童对外界事物的接受程度与城市儿童有较大的差异。老师的单向教授和书本传递出的抽象概念，对于他们来说通常会难以理解。同样，如果仅仅给孩子们播放电影，而不进行引导、发散思考，同样不能取得最优效果。因此，从偏远贫困地区儿童的实际情况和需求出发，我们的电影教育实践和探索也需要“因地制宜”。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">针对这样的情况，我们提出“以儿童为本”的电影教育理念，真正从儿童角度出发，以电影为载体帮助孩子们打开内心，通过老师循循善诱的引导他们一览更广阔的世界。具体而言，即是将尊重、陪伴、启迪三个“原则”融会贯通，充分回应儿童成长的需求：</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">- <strong>尊重</strong>儿童对于电影的偏好，悉心聆听孩子们对电影的理解；同时引导孩子们在电影所包含的多元化价值观体系中，更好地发现自己、接受自己、融入社会；</p><p style=\"text-align: justify;\">- 让“零钱电影院”不只成为教学的场所，而是真正融入他们的生活，成为<strong>陪伴</strong>他们成长的自由空间；孩子们可以跟随电影一起去探索世界，也可以从故事中汲取生活的哲思与情感上的启发；</p><p style=\"text-align: justify;\">- 帮助孩子们通过电影认识更大的世界，引导孩子们认识电影中所包含的艺术元素，帮助他们发现美、认识美，<strong>启迪</strong>他们与生俱来的创造力和梦想</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">这样的儿童电影教育能够更好的匹配偏远地区孩子们的需要，并与他们的未来发展紧密相联，这也将是指导我们下一阶段升级行动的核心理念。</p><p style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img width=\"1px\" height=\"2px\" src=\"http://ad.hbfoundation.cn/laravel-u-editor/themes/default/images/spacer.gif\" word_img=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image001.png\" style=\"background-image: url(&quot;http://ad.hbfoundation.cn/laravel-u-editor/themes/default/images/word.gif&quot;); background-position: center center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; border: 1px solid rgb(221, 221, 221);\"/></p><p style=\"text-align: center;\"><strong>行动升级：</strong><strong>“软件”和“硬件”的全面整合</strong></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">秉承着“以人为本”的儿童教育理念，今年3月起，华谊兄弟公益基金团队行程万余公里，走访了位于全国5个省份的30所“零钱电影院”所在学校。在充分汲取回访中老师和学生们对使用“零钱电影院”的反馈后，我们正式开启了“零钱电影院”的全新升级计划。接下来，一方面我们将从实际硬件出发，不断增加“零钱电影院”数量并将现有设备和影片资源进行升级。同时，进一步深耕儿童电影教育领域，在软件上加强对教师课程安排、授课技巧的培训与指导，促进教育效果的进一步提升。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">作为“零钱电影院”升级计划“软件”部分的首次出击，我们的第一届“零钱电影院”校长峰会将在这个7月拉开帷幕，届时“零钱电影院”所在的全国100所学校的校长将在北京相聚，通过参与为期一周的儿童电影教育培训学习儿童电影教育的相关知识与教学方法。更多的升级计划还将持续进行，请大家拭目以待。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">如果说“零钱电影院”的成立与普及是我们公益事业当下的“小目标”，那么打造中国儿童电影教育发展的平台，让电影教育理念真正在中国起到作用，为社会的未来提供源源不断的创造力，便是华谊兄弟公益基金的长远理想。作为专注于内容创作，在影视娱乐领域深耕多年的企业，华谊兄弟见证了精品电影对广大观众的深刻影响；而华谊兄弟公益基金在这6年的实践中，见证了电影给孩子们带来的欢乐、启迪，以及在他们成长过程中所扮演的重要角色。现阶段的成果，正是我们不断进步、不断升级的源动力。我们希望不管是城市还是农村，每个孩子都能在一个个鲜活的电影故事的陪伴下，由光影中获得启迪，充分发挥自己的想象力，自由自在地进行创造，为自己、为未来。</p><p style=\"line-height: 26px;\"><br/></p>', '0', '1', '0', '4', '1503026246', '0', '1', '0', '0', '', '', null, '从实地考察、多年实践探索总结以及海内外教育案例的研究出发，华谊兄弟公益基金“零钱电影院”升级计划即将全面开启！', '公益，华谊兄弟公益基金，零钱电影院', '2017-07-13');
INSERT INTO `strategy` VALUES ('53', '华谊兄弟公益基金五周年 第100所“零钱电影院”落成', '/uploads/images/rBP57TloHT1502872224.jpg', '<p style=\"text-align: justify;\"><strong>2016年10月26日 – 云南丽江</strong> 华谊兄弟公益基金迎来了成立五周年的重要时刻，作为该基金最重要的项目之一，华谊兄弟公益基金捐赠的第100所“零钱电影院”也于云南省丽江市玉龙纳西族自治县拉市镇海南完小正式落成，这是一个里程碑式的节点。云南省政府领导代表，华谊兄弟副董事长、CEO王中磊先生出席了第100所零钱电影院捐赠仪式，共同开启孩子们光影世界的大门。华谊兄弟公益基金五周年的历程，离不开华谊兄弟公司自上而下的不懈努力，也离不开王中军的坚持。作为企业掌门人，董事长王中军先生曾先后多次通过义卖画作，为华谊兄弟公益基金筹款，并将所得善款全部捐赠，用艺术的力量为孩子们创造属于他们的精神世界。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502872071683919.png\" title=\"1502872071683919.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">华谊兄弟副董事长、CEO王中磊先生表示：“华谊兄弟公益基金从最初的简单想法，逐渐成长为一个非常专业的公益组织。在过去的5年里，我们也一直在思考，如何才能给孩子们带来更多的快乐和更长远的积极影响，答案就是‘创造力公益’。区别于传统公益简单直接的物质馈赠形式，‘创造力公益’是一种不断创新、可持续的公益模式，更注重于丰富孩子们的精神世界。”事实上，“创造力公益”是华谊兄弟公益基金的品牌内涵。在公益实践中，华谊兄弟公益基金为儿童带来的不只是好的电影，还为他们搭建了一个可以尽情发挥创造力的艺术空间，这是对孩子们精神的塑造。华谊兄弟公益基金希望以此为沃土，让更多的儿童可以全面地认识世界，从而更有勇气追逐梦想，创造未来的无限可能。同时，创造力的培养将是未来中国教育的一大重点，华谊兄弟公益基金“创造力公益”的新理念，也正与这一教育趋势不谋而合。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502872106333254.png\" title=\"1502872106333254.png\" alt=\"image.png\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502872123448010.png\" title=\"1502872123448010.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">作为华谊兄弟公益基金的核心项目之一，五年来，“零钱电影院”已走过北京、上海、广州、河南、云南、青海等全国多个省市，惠及儿童近15万名。除此之外，华谊兄弟公益基金还积极汇聚影视资源，以多种方式实践善举，聚点滴小爱为大爱。例如筹备制作关注弱势儿童的公益电影《有一天》，创办 “融合教育”合唱团“有一天合唱团”，积极投身国际合作及灾后重建等等。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502872153545129.png\" title=\"1502872153545129.png\" alt=\"image.png\"/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502872164961854.png\" title=\"1502872164961854.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">作为中国娱乐行业的领军企业，成立22年的华谊兄弟一直关注创造力，尊重且敬畏内容，也因此而凝聚了众多精品内容的创造者及创意人才，致力于为观众创造出更多品质与态度并存的优秀影视作品。“创造力公益”也正是对华谊兄弟企业基因的延续。作为独具特色且实践经验丰富的公益组织，在下一个五年甚至是更久的时间内，华谊兄弟公益基金将继续以“创造力公益”为主导，用愈加饱满的姿态延续慈善力量，为更多孩子的美好人生贡献己力。</p><p style=\"text-align: justify;\"><br/></p>', '0', '0', '1', '5', '1503024499', '0', '0', '0', '0', '', '', null, '2016年10月26日，由华谊兄弟公益基金捐建的第100所“零钱电影院”于云南省丽江市玉龙纳西族自治县拉市镇海南完小正式落成。      ', '华谊兄弟，零钱电影院，100所，公益', '2016-10-26');
INSERT INTO `strategy` VALUES ('54', '以爱之名 让电影之笔为教育添色——首届“零钱电影院”校长峰会在京圆满落幕', '/uploads/images/wn7GYKmi6P1502872869.jpg', '<p style=\"text-align: justify;\">为期一周的华谊兄弟公益基金首届“零钱电影院”校长峰会于本周末在北京落下了帷幕。本次峰会期间，共有来自全国10个省市的100余位“零钱电影院”所在学校校长出席并参加了儿童电影教育的培训。</p><p><br/></p><p style=\"text-align: justify;\">峰会期间，华谊兄弟公益基金邀请到了北京电影学院科研信息化处处长刘军等多位电影教育领域的权威、资深人士，围绕“入戏入画，引导孩子们在电影中释放创造的能量”的主题，为校长们展开了为期三天的培训。培训期间，专家们通过丰富的案例为校长们详细讲解了何为参与式电影教学、如何对学生开展参与式电影教学等问题，并针对授课技巧、讲课思路、如何调动课堂气氛、做好与学生的沟通等主题为校长们进行了培训。除此以外，校长们也通过组内讨论、上台分享等方式交换了对于儿童电影教育的看法，并对可能面临的问题和解决方法进行了探讨，加强了对参与式电影教学的理解。</p><p><br/></p><p style=\"text-align: justify;\">在第一天的培训中，刘军教授为大家介绍了电影教育对儿童的积极影响。在谈及国内儿童电影教育的现状时，刘军教授表示，中国儿童电影教育起步很早，有着很广阔的发展空间，通过围绕电影开展的不同类型的教学活动，能够更好地提升儿童的视听素养和创造力。深入浅出的方式、启发式的教学，让在座的校长从原有的固定思路中走了出来，更加准确地把握儿童电影教育的重要性及作用。</p><p style=\"text-align: justify;\"><span style=\"font-size: 14px;font-family: 等线\"></span><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502872346668169.png\" title=\"1502872346668169.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502872357638457.png\" title=\"1502872357638457.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">▲第一天的培训现场</span></p><p><br/></p><p style=\"text-align: justify;\">在第二天、第三天的培训中，参与式教学领域的权威人士为大家带来了一堂堂别开生面的课程。“鸡尾酒会”、“焦点讨论”、“教与学”、“电影片段观赏”，一个个极富创意的环节给大家带来了耳目一新的感受，让大家体会到自发寻找答案的乐趣的同时，也让校长们迅速地熟悉了彼此。校长们表示，通过培训，他们切身感受到了参与式教学的魅力，对如何更好地利用“零钱电影院”有了新的思考，迫不及待地想回去与自己的同事分享。</p><p style=\"text-align: justify;\"><span style=\"font-size: 14px;font-family: 等线\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 14px;font-family: 等线\"><img src=\"/uploads/ueditor/php/upload/image/1502872429607720.png\" title=\"1502872429607720.png\" alt=\"image.png\"/></span></p><p style=\"text-align: justify;\"><span style=\"font-size: 14px;font-family: 等线\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;font-family: 等线\"><img src=\"/uploads/ueditor/php/upload/image/1502872405358398.png\" title=\"1502872405358398.png\" alt=\"image.png\"/></span></p><p><span style=\"font-size: 14px;font-family: 等线\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;font-family: 等线\"><img src=\"/uploads/ueditor/php/upload/image/1502872427903067.png\" title=\"1502872427903067.png\" alt=\"image.png\"/></span></p><p style=\"text-align: justify;\"><span style=\"font-size: 14px;font-family: 等线\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;font-family: 等线\"><img src=\"/uploads/ueditor/php/upload/image/1502872425822396.png\" title=\"1502872425822396.png\" alt=\"image.png\"/></span></p><p><span style=\"font-size: 14px;font-family: 等线\"></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">▲第二天、第三天的培训现场 &nbsp;校长们感受到了参与式教学的魅力</span></p><p><br/></p><p style=\"text-align: justify;\">随后的几天里，我们带领着校长们参观了中国电影博物馆、中国影视大乐园，并在华谊兄弟电影汇观看了电影，让校长们全方位地感受电影的魅力。</p><p style=\"text-align: justify;\"><span style=\"font-size: 14px; text-align: justify;\"><br/></span></p><p style=\"text-align: justify;\">在中国电影博物馆的参观中，我们和校长们一起穿越电影的百年历程，在集权威性、知识性、艺术性于一体的艺术殿堂里，感受电影的每一次突破，体会电影带来的每一次震撼；在中国影视大乐园里，校长们真正走入了电影，从特效化妆、场景拍摄、人物配音，再到角色扮演，逐一尝试各个环节，体会电影的乐趣，揭开电影背后的秘密；在华谊兄弟电影汇，校长们坐在顶级配置的奢享式影院中全身心地沉浸在电影的魅力中。<br/></p><p style=\"text-align: justify;\"><span style=\"font-size: 14px;font-family: 等线\"></span><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502872484218482.png\" title=\"1502872484218482.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502872500893495.png\" title=\"1502872500893495.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502872532924395.png\" title=\"1502872532924395.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">▲参观中国电影博物馆</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502872549711098.png\" title=\"1502872549711098.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502872552435646.png\" title=\"1502872552435646.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502872569645246.png\" title=\"1502872569645246.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">▲参观中国影视大乐园</span></p><p><br/></p><p style=\"text-align: justify;\">参与本次培训的校长中有许多人是第一次来到北京，因而接下来的几天里，我们为校长们安排了北京精华景点的观光，从天安门到故宫，从长城到奥林匹克公园，都留下了校长们的足迹和欢声笑语。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502872609580634.png\" title=\"1502872609580634.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502872641284075.png\" title=\"1502872641284075.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502872653570723.png\" title=\"1502872653570723.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">▲游玩北京精华景点</span></p><p style=\"text-align: center;\"><span style=\"font-size:12px;font-family:&#39;微软雅黑&#39;,sans-serif;color:#888888;background:white\"><br/></span></p><p style=\"text-align: justify;\">为期一周的培训结束了，而我们的“零钱电影院”升级项目才刚刚开始，儿童电影教育的未来任重道远。我们将在全面升级硬件设备的同时，持续对校长们开展儿童电影教育和参与式教学的培训，进一步提升学校对儿童电影教育重要性的认识，推动儿童电影教育的落地普及，从而弥补不同地区间发展和教育水平的差距，为推动教育公平贡献力量。儿童电影教育的普及是一个长久的过程，希望未来能有更多的伙伴和我们一起，让“零钱电影院”更好地服务儿童电影教育，为更多孩子们带去放飞梦想的翅膀。</p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(0, 0, 0); background: white; font-size: 16px;\"><br/></span></p>', '0', '0', '1', '5', '1503282197', '0', '0', '0', '0', '', '', null, '2017年7月29日，为期一周的华谊兄弟公益基金首届“零钱电影院”校长峰会在北京落下了帷幕。峰会期间，共有来自全国10个省市的100余位“零钱电影院”所在学校校长出席并参加了儿童电影教育的培训。        ', '华谊兄弟，公益，零钱电影院，校长培训', '2017-07-30');
INSERT INTO `strategy` VALUES ('55', '“零钱电影院” 护航成长 为爱升级', '/uploads/images/bxhcejbK2K1502938635.jpg', '<p style=\"text-align: justify;\">从今年3月起，我们开始了对2015年之前捐建的“零钱电影院”所在学校的回访工作。北京、云南、河南、甘肃……四个月的实地考察让我们收获满满：我们充分聆听了孩子们的观影感受，也与校长、老师们一起探讨他们在硬件设备上及软件辅导上的进一步需求。基于这些实地考察、多年实践探索总结以及海内外教育案例的研究，在这个7月，华谊兄弟公益基金的小伙伴们已经整装待发，“零钱电影院”升级计划即将全面开启！</p><p style=\"text-align: justify;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></strong></p><p style=\"text-align: center;\"><strong>“可持续”公益升级：不变的是长久的陪伴与关爱</strong></p><p><span style=\"font-size: 14px;font-family: 宋体\"><br/></span></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502938156630121.png\" title=\"1502938156630121.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align: justify;\">6年前，我们带着简单的公益愿望前往偏远地区走访时，如上图般破旧的校舍让我们印象深刻。大多数学校虽有藏书但却基本闲置，仅有的几位老师需要教导全校几十甚至上百位学生的全部课程，孩子们虽正当天真烂漫的年纪，却意外的内向与害羞，不愿与人交流……</p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502938183256834.png\" title=\"1502938183256834.png\" alt=\"image.png\"/></span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align: justify;\">作为在电影行业深耕23年的领军企业，我们深知电影不止是一种简单的娱乐方式，它“亦师亦友”，能带领这些孩子看到外面更广阔的世界，也能启发孩子们的想象力与创造力，丰富他们的精神世界。秉承这样的初衷，“零钱电影院”项目开始了为偏远地区儿童普及电影的征程。6年间，“零钱电影院”已在全国10个省的100所学校中生根发芽。传统的教室、闲置的图书馆变身为小小电影院，各类优秀的儿童电影、丰富多彩的图书和绘本陪伴着孩子们的成长，帮助他们打开心扉、开阔眼界。</p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align: justify;\">这100所 “零钱电影院”是我们公益之路上的一个里程碑，而我们真正的目标远不止这一个数字，更不是“捐赠”这一行动本身。我们不仅希望孩子们拥有干净明亮的教室与完善的设备，更希望看到孩子们与电影里的人物一起冒险、一起经历、一起欢笑，并从这些光影的故事中有所收获；我们不仅希望“零钱电影院”成为老师们教学的得力帮手，更希望它能给孩子们带去课本之外的成长，浇灌他们的精神世界。</p><p><br/></p><p style=\"text-align: justify;\">要让公益行动对贫困地区的孩子们产生可持续的影响，“硬件”的到位只是第一步，而如何让老师们及家长们从观念上意识到电影在儿童教育上的重要作用，并真正引导孩子们从电影中汲取精神养分，是我们一直在思考的问题，也是我们进行下一步升级的原因。我们希望通过持续的关注、沟通与指导，让“零钱电影院”更好地发挥作用，让孩子们在各个成长阶段都能从电影中获益。</p><p style=\"text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align: justify; line-height: 1.5em;\"><br/></p><p style=\"text-align: center;\"><strong>观念升级：“以人为本”的儿童电影教育理念</strong></p><p style=\"text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502938302244013.png\" title=\"1502938302244013.png\" alt=\"image.png\"/></p><p style=\"text-align: justify; line-height: 1.5em;\"><br/></p><p style=\"text-align: justify;\">我们在回访中发现，由于生活学习环境的差异，偏远地区的贫困儿童及留守儿童对外界事物的接受程度与城市儿童有较大的差异。老师的单向教授和书本传递出的抽象概念，对于他们来说通常会难以理解。同样，如果仅仅给孩子们播放电影，而不进行引导、发散思考，同样不能取得最优效果。根据偏远贫困地区儿童的实际情况和需求，我们的电影教育实践和探索也需要“因地制宜”。</p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align: justify;\">针对这样的情况，我们提出“以儿童为本”的电影教育理念，真正从儿童角度出发，以电影为载体帮助孩子们打开内心，通过老师循循善诱的引导他们一览更广阔的世界。具体而言，即是将尊重、陪伴、启迪三个“原则”融会贯通，充分回应儿童成长的需求：</p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align: justify; line-height: 1.5em;\"><br/></p><p style=\"text-align: justify;\">- <strong>尊重</strong>儿童对于电影的偏好，悉心聆听孩子们对电影的理解；同时引导孩子们在电影所包含的多元化价值观体系中，更好地发现自己、接受自己、融入社会；</p><p style=\"text-align: justify;\">- 让“零钱电影院”不只成为教学的场所，而是真正融入他们的生活，成为<strong>陪伴</strong>他们成长的自由空间；孩子们可以跟随电影一起去探索世界，也可以从故事中汲取生活的哲思与情感上的启发；</p><p style=\"text-align: justify;\">- 帮助孩子们通过电影认识更大的世界，引导孩子们认识电影中所包含的艺术元素，帮助他们发现美、认识美，<strong>启迪</strong>他们与生俱来的创造力和梦想。</p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span>这样的儿童电影教育能够更好的匹配偏远地区孩子们的需要，并与他们的未来发展紧密相联，这也将是指导我们下一阶段升级行动的核心理念。</p><p style=\"text-align: justify;\"><span style=\"font-size: 14px;font-family: 宋体\"><br/></span></p><p><br/></p><p style=\"text-align: center;\"><strong>行动升级：“软件”和“硬件”的全面整合</strong></p><p><span style=\"font-size: 14px;font-family: 宋体\"><br/></span></p><p style=\"text-align: justify;\">秉承着“以人为本”的儿童教育理念，今年3月起，华谊兄弟公益基金团队行程万余公里，走访了位于全国5个省份的30所“零钱电影院”所在学校。在充分汲取回访中老师和学生们对使用“零钱电影院”的反馈后，我们正式开启了“零钱电影院”的全新升级计划。接下来，一方面我们将从实际硬件出发，不断增加“零钱电影院”数量并将现有设备和影片资源进行升级。同时，进一步深耕儿童电影教育领域，在软件上加强对教师课程安排、授课技巧的培训与指导，促进教育效果的进一步提升。<br/></p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"font-size: 16px;\"><br/></span></span></p><p style=\"text-align: justify;\">作为“零钱电影院”升级计划“软件”部分的首次出击，我们的第一届“零钱电影院”校长峰会将在这个7月拉开帷幕，届时“零钱电影院”所在的全国100所学校的校长将在北京相聚，通过参与为期一周的儿童电影教育培训学习儿童电影教育的相关知识与教学方法。更多的升级计划还将持续进行，请大家拭目以待。</p><p style=\"text-align: justify;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align: justify;\">如果说“零钱电影院”的成立与普及是我们公益事业当下的“小目标”，那么打造中国儿童电影教育发展的平台，让电影教育理念真正在中国起到作用，为社会的未来提供源源不断的创造力，便是华谊兄弟公益基金的长远理想。作为专注于内容创作，在影视娱乐领域深耕多年的企业，华谊兄弟见证了精品电影对广大观众的深刻影响；而华谊兄弟公益基金在这6年的实践中，见证了电影给孩子们带来的欢乐、启迪，以及在他们成长过程中所扮演的重要角色。现阶段的成果，正是我们不断进步、不断升级的源动力。我们希望不管是城市还是农村，每个孩子都能在一个个鲜活的电影故事的陪伴下，由光影中获得启迪，充分发挥自己的想象力，自由自在地进行创造，为自己、为未来。</p><p style=\"text-align: justify;\"><br/></p>', '0', '1', '0', '5', '1503368263', '0', '0', '0', '0', '', '', null, '今年，我们将进一步对“零钱电影院”项目和电影教育进行更为深入的探索和实践，“零钱电影院”升级计划即将全面开启！     ', '华谊兄弟，公益，零钱电影院', '2017-07-13');
INSERT INTO `strategy` VALUES ('83', '民政部等五部门联合印发 在农村留守儿童关爱保护中发挥社会工作专业人才作用的指导意见', '/uploads/images/HEx2awFikN1503294553.jpg', '<p style=\"text-align: justify;\">社会工作专业人才是开展农村留守儿童关爱保护的新兴力量，在回应农村留守儿童心理社会服务需求、促进农村留守儿童全面健康成长中具有积极作用。为贯彻落实《国务院关于加强农村留守儿童关爱保护工作的意见》（国发〔2016〕13号）、《国务院关于加强困境儿童保障工作的意见》（国发〔2016〕36号）和《国务院关于印发“十三五”脱贫攻坚规划的通知》（国发〔2016〕64号）有关精神要求，近日，民政部、教育部、财政部、共青团中央、全国妇联联合印发了《关于在农村留守儿童关爱保护中发挥社会工作专业人才作用的指导意见》（民发〔2017〕126号，以下简称《意见》）。《意见》围绕“引导家庭尽责、充实基层力量、注重因地制宜、强化专业作用”的原则，提出了一系列支持引导社会工作专业人才参与农村留守儿童关爱保护工作的政策措施。</p><p style=\"text-align: justify;\">《意见》明确了社会工作专业人才在农村留守儿童关爱保护中的主要任务：一是协助做好救助保护工作。协助开展农村留守儿童家庭随访，对农村留守儿童的家庭组成、监护照料、入学就学、身心健康等情况进行调查评估，对重点对象进行核查，确保农村留守儿童得到妥善照料。及时发现报告遭受或者疑似遭受家庭暴力或其他受虐行为，协助做好应急处置工作。协助做好对无人监护或遭受监护侵害农村留守儿童的心理疏导、精神关爱和临时监护照料工作。帮助农村留守儿童及其家庭链接社会救助、社会福利和公益慈善资源，引导公益慈善力量、相关社会组织和志愿者等社会力量为农村留守儿童及其家庭提供物质帮助和关爱服务。二是配合开展家庭教育指导。协助开展农村留守儿童监护法制宣传和家庭暴力预防教育，对农村留守儿童父母、受委托监护人开展家庭教育指导，引导其正确履行抚养义务和监护职责。配合调解农村留守儿童家庭矛盾，促进建立和谐家庭关系，为隔代照顾家庭提供代际沟通、关系调适和能力建设服务。引导外出务工家长关心留守儿童，增进家庭亲情关爱，帮助农村留守儿童通过电话、视频等方式加强与父母的情感联系和亲情交流。三是积极开展社会关爱服务。协助中小学校和农村社区做好安全教育，帮助农村留守儿童增强防范不法侵害的意识、掌握预防意外伤害的安全常识。协助做好农村留守儿童心理健康教育，及早发现并纠正心理问题，提供心理援助、成长陪伴和危机干预服务，疏导心理压力和负面情绪，促进农村留守儿童心理、人格健康发展。提供社会融入服务，增强农村留守儿童社会交往和社会适应能力。协助做好农村留守儿童不良行为临界预防，对有不良行为的留守儿童实施早期介入和行为干预，帮助其纠正偏差行为。</p><p style=\"text-align: justify;\">针对农村地区社会工作专业力量薄弱和留守儿童社会工作服务机制不完善的问题，《意见》提出以留守儿童关爱保护为重点，加大农村地区社会工作专业人才培养使用力度：一是加强社会工作专业人才培养。鼓励城市相关事业单位、高校科研机构、社会组织和街道社区中的社会工作专业人才通过对口支援、实习实训、提供培训督导等方式支持农村地区社会工作专业人才队伍建设。逐步将乡镇（街道）民政助理员、村“两委”成员、家庭关系调解员、儿童福利督导员、未成年人保护专干、基层青少年事务工作者、基层团干部、基层妇女干部等纳入相关部门社会工作培训范围、接受继续教育和职业培训，推动符合条件人员参加全国社会工作者职业水平评价和社会工作学历学位教育。二是积极培育发展社会工作服务机构。加快发展以农村留守儿童为重点服务对象的社会工作服务机构。未成年人保护机构和儿童福利机构可以通过免费提供场所、开展项目合作等方式孵化社会工作服务机构，延伸农村留守儿童关爱保护臂力。三是推进乡镇（街道）社会工作服务站点建设。将发挥社会工作专业人才作用作为充实基层农村留守儿童关爱保护工作力量的重要手段，推动在农村留守儿童集中的乡镇依托民政事务办公室、农村社区综合服务设施或妇女之家、儿童之家、青少年综合服务平台等现有综合服务设施设立社会工作站（室）。通过由具备社会工作教育培训背景的基层工作人员任职、公开招聘、委托社会工作服务机构运营等方式配备社会工作专业服务团队，在乡镇（街道）党委政府的领导下采取定点服务、巡回服务等方式为乡镇（街道）辖区内农村留守儿童提供服务。四是加强相关单位社会工作专业人才配备使用。推动未成年人保护机构、儿童福利机构、救助管理机构通过开发设置岗位、购买服务等方式配备使用社会工作专业人才。未成年人保护机构和儿童福利机构可根据需要将社会工作专业岗位明确为主体专业技术岗位。鼓励中小学校、青少年服务机构、妇女儿童服务机构根据工作需要设置社会工作专业岗位，配备使用社会工作专业人才。</p><p style=\"text-align: justify;\">《意见》明确了在农村留守儿童关爱保护中发挥社会工作专业人才作用的支持保障措施，从加强组织领导、强化保障措施、加强宣传引导方面提出了要求。在加强组织领导方面，明确了民政部门、教育部门、财政部门、共青团组织和妇联组织的职责任务。在强化保护措施方面，民政部将通过社会组织参与社会服务项目、社会工作专业人才服务“三区”计划、城乡社会工作服务机构“牵手计划”等方式，为各地探索在农村留守儿童关爱保护中发挥社会工作专业人才作用提供支持，并要求各地各有关部门推动将由政府提供且适宜社会承担的农村留守儿童关爱保护工作纳入政府购买服务指导性目录，通过政府购买服务方式支持社会工作服务机构协助开展信息收集、监护情况调查评估等农村留守儿童关爱保护工作。在加强宣传引导方面，要求选择一批农村留守儿童数量较多、服务需求较大的地区和单位先行开展试点，在试点基础上创建一批示范地区和单位，发挥其辐射带动作用，由点及面推动社会工作专业人才广泛参与农村留守儿童关爱保护工作；要求及时总结提炼社会工作专业人才介入农村留守儿童关爱保护工作的实践经验，广泛宣传先进典型和成熟做法，提高社会认知、扩大社会共识。</p><p style=\"text-align: justify;\">为贯彻落实《国务院关于加强困境儿童保障工作的意见》关于发挥社会工作专业人才作用的有关要求，促进农村困境儿童保障工作，《意见》最后提出，社会工作专业人才服务农村困境儿童相关工作参照农村留守儿童关爱保护工作开展。</p><p><br/></p>', '0', '1', '0', '7', '1503294610', '0', '0', '0', '0', '', '', null, '日前，民政部等五部门联合发文，支持社会工作专业人才参与农村留守儿童关爱保护工作。 ', '公益，华谊兄弟公益基金，留守儿童，民政部', '2017-08-21');
INSERT INTO `strategy` VALUES ('56', '华谊兄弟公益基金成立 启动零钱电影计划', '/uploads/images/RBAIJCAXVL1502939848.jpg', '<p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502939724588604.png\" title=\"1502939724588604.png\" alt=\"image.png\"/></p><p><br/></p><p><span style=\"font-size:16px;font-family: 宋体\">6</span><span style=\"font-size:16px;font-family:宋体\">月1日，华谊兄弟公益基金在京举行启动仪式。华谊兄弟公益基金是由华谊兄弟的明星和员工共同发起，在中华思源工程基金会的支持和管理下设立的专项公益基金。</span></p><p><span style=\"font-size:16px;font-family:宋体\"><br/></span></p><p style=\"text-align: center;\"><strong><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\">王中磊公布“零钱电影计划”</span></span></strong></p><p><br/></p><p><span style=\"font-size:16px;font-family:宋体\">华谊兄弟总裁王中磊公布了第一个公益梦想——“零钱电影计划”，核心公益产品是分布在各个贫困学校中、寓教于乐的“零钱电影院”。除完整的电影放映设备外，影院内还将有儿童课外图书、电脑、互联网等配套设施，让孩子能通过电影获得课本外的知识。“零钱电影计划”传达的公益观念是：小零钱大爱心，搭建孩子的电影天堂。作为全娱乐传媒集团，华谊兄弟也在积极尝试探索一条公益文化之路。</span></p><p><span style=\"font-size:16px;font-family:宋体\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size:16px;font-family:宋体\"><img src=\"/uploads/ueditor/php/upload/image/1502939773981850.png\" title=\"1502939773981850.png\" alt=\"image.png\"/></span></p><p><span style=\"font-size:16px;font-family:宋体\"><br/></span></p><p><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\">王中磊表示：“华谊兄弟在做公益方面是个新人，每一步都要脚踏实地。华谊兄弟公益基金在挑选公益项目方向时，经过多次的摸索和讨论，最终确定在电影与儿童的结合。‘零钱电影计划’也就此诞生。一个企业做公益，用其自身最大的资源去最大限度地帮助更多人，是负责任的。儿童与电影是最适合我们的，电影对儿童的影响是深远的。电影也是华谊人都能发挥作用的。华谊兄弟公益基金倡导可持续的公益行为，从每个人力所能及的事做起，哪怕我们每年为孩子们坚持做一件事，都是好的。”</span></span></p><p><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\"><br/></span></span></p><p><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\">当日，王中磊也表示：“华谊兄弟公益基金的发起人是华谊兄弟，但这个基金是一个公众的平台，希望和愿意加入进来的朋友，一起努力，实现公益梦想未来。”相信也会有越来越多的伙伴力量加入进来，大手拉小手，把爱传下去，一个人的承诺，带动更多人的关注，就可以有所改变。</span></span></span></p><p><span style=\"font-size:16px;font-family:宋体\"><span style=\"font-size:16px;font-family:宋体\"><br/></span></span></p>', '0', '0', '0', '1', '1502939848', '0', '0', '0', '0', '', '', null, '2011年6月1日，由华谊兄弟的明星和员工共同发起，在中华思源工程扶贫基金会的支持和管理下设立的专项公益基金——华谊兄弟公益基金在京举行启动仪式。', '公益，华谊兄弟，华谊兄弟公益基金', '2011-06-01');
INSERT INTO `strategy` VALUES ('57', '华谊兄弟公益基金成立 启动零钱电影计划', '/uploads/images/XVm4pAC4t91502940053.jpg', '<p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502939971255883.png\" title=\"1502939971255883.png\" alt=\"image.png\"/></p><p style=\"text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体; font-size: 16px;\"><br/></span></p><p style=\"text-align: justify;\">6月1日，华谊兄弟公益基金在京举行启动仪式。华谊兄弟公益基金是由华谊兄弟的明星和员工共同发起，在中华思源工程基金会的支持和管理下设立的专项公益基金。</p><p style=\"text-align: justify;\"><span style=\"font-size:16px;font-family:宋体\"><br/></span></p><p style=\"text-align: justify;\">华谊兄弟总裁王中磊公布了第一个公益梦想——“零钱电影计划”，核心公益产品是分布在各个贫困学校中、寓教于乐的“零钱电影院”。除完整的电影放映设备外，影院内还将有儿童课外图书、电脑、互联网等配套设施，让孩子能通过电影获得课本外的知识。“零钱电影计划”传达的公益观念是：小零钱大爱心，搭建孩子的电影天堂。作为全娱乐传媒集团，华谊兄弟也在积极尝试探索一条公益文化之路。</p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 宋体;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-size: 16px; font-family: 宋体;\"><img src=\"/uploads/ueditor/php/upload/image/1502940012989466.png\" title=\"1502940012989466.png\" alt=\"image.png\"/></span></p><p><span style=\"font-size: 16px; font-family: 宋体;\"><br/></span></p><p style=\"text-align: justify;\">王中磊表示：“华谊兄弟在做公益方面是个新人，每一步都要脚踏实地。华谊兄弟公益基金在挑选公益项目方向时，经过多次的摸索和讨论，最终确定在电影与儿童的结合。‘零钱电影计划’也就此诞生。一个企业做公益，用其自身最大的资源去最大限度地帮助更多人，是负责任的。儿童与电影是最适合我们的，电影对儿童的影响是深远的。电影也是华谊人都能发挥作用的。华谊兄弟公益基金倡导可持续的公益行为，从每个人力所能及的事做起，哪怕我们每年为孩子们坚持做一件事，都是好的。”</p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 宋体;\"><br/></span></p><p style=\"text-align: justify;\">当日，王中磊也表示：“华谊兄弟公益基金的发起人是华谊兄弟，但这个基金是一个公众的平台，希望和愿意加入进来的朋友，一起努力，实现公益梦想未来。”相信也会有越来越多的伙伴力量加入进来，大手拉小手，把爱传下去，一个人的承诺，带动更多人的关注，就可以有所改变。</p><p style=\"text-align: justify;\"><span style=\"font-size: 16px; font-family: 宋体;\"><br/></span></p>', '0', '0', '0', '4', '1502966594', '0', '0', '0', '0', '', '', null, '2011年6月1日，由华谊兄弟的明星和员工共同发起，在中华思源工程扶贫基金会的支持和管理下设立的专项公益基金——华谊兄弟公益基金在京举行启动仪式。  ', '公益，华谊兄弟，华谊兄弟公益基金', '2011-06-01');
INSERT INTO `strategy` VALUES ('58', '群星闪耀华谊ELLE之夜 为儿童公益项目呐喊', '/uploads/images/KUQ1mfBa4x1502940732.jpg', '<p style=\"text-align: justify;\">6月12日，由华谊兄弟时尚文化传媒与《ELLE世界时装之苑》联手主办的“2011星光上海——华谊ELLE之夜”在毗邻东外滩的全新时尚地标——上海国际时尚中心盛大举办。华谊兄弟传媒股份有限公司总裁王中磊先生、华谊兄弟时尚文化传媒董事总经理赵磊先生、桦榭中国董事总经理艾弥尔先生、桦榭中国出版总经理杨玟女士、ELLE编辑总监晓雪女士、上海市杨浦区副区长唐海东等共同到场庆祝这一娱乐与时尚界的盛事。</p><p style=\"text-align: justify;\"><span style=\"line-height: 150%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify;\">现场红毯星光熠熠，李冰冰、刘晓庆、高圆圆、安以轩、霍思燕、张涵予、那英、桂纶镁、邓超、李晨等一众名星悉数亮相，名贵汽车与惊艳明星使现场高潮迭起，气氛不断升温。ELLE TV APP更在华谊之夜全程直播，尊享60多位明星齐聚的奢华盛宴。</p><p style=\"text-align: justify;\"><span style=\"line-height: 150%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify;\">当天新闻发布会，王中磊正式宣布了华谊兄弟全新子公司华谊兄弟时尚传媒有限公司的成立，这也预示着国内影视娱乐巨头正式进军时尚界，王中磊表示：“华谊兄弟一直关注于中国时尚业的发展，时刻掌握国内外时尚动态。在过去的几年里，华谊旗下的艺人积极参与时尚活动；同时，我们也成功举办了在文化艺术和时尚潮流界都颇具影响力的‘华谊之夜’等盛会。在电影、电视、唱片、主持、广告等领域，华谊的艺人均展现出现代、时尚、健康的形象，我认为华谊进军时尚业是水到渠成的事。”</p><p style=\"text-align: justify;\"><span style=\"line-height: 150%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify;\">而对于此次华谊兄弟时尚与ELLE杂志的强强联手，桦榭中国集团董事总经理艾弥尔表示：“这次的星光上海——华谊ELLE之夜，开创了国际高端女性杂志与顶尖影视时尚公司的合作新模式，在共享资源、扩展影响力的同时，我们也深刻意识到作为各自领域的佼佼者，我们肩负着重要的公益使命与社会责任，这也是为何此次活动加重了公益的环节，并希望所有来宾以及观众都能积极投身到关爱儿童的公益项目之中。”</p><p style=\"text-align: justify;\"><span style=\"line-height: 150%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify;\">自今年华谊ELLE之夜宣布设立公益环节以来，吸引了诸多品牌的鼎力支持，各大品牌纷纷献出各自经典产品，在MY ELLE PIECE上慷慨义卖，并将所得款项全数捐于今年儿童节正式成立的“华谊兄弟公益基金”，为贫困儿童建立“零钱电影院”；其中，来自西藏的高端天然矿泉水品牌夏木拉更当场宣布捐赠义卖等值金额给予“华谊兄弟公益基金”，携手“华谊ELLE之夜”一同在北京落实建立一个零钱电影院。</p><p style=\"text-align: justify;\"><span style=\"line-height: 150%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify;\">“ELLE是进入中国的第一本国际高端女性大刊，在风尚领域，电影和时装一直互相激发灵感与火花，这也是我们此次携手华谊的初衷。此外，ELLE一直致力于推广Love Kids的爱心项目，在我们杂志的6月刊上可以看到许多明星身穿我们ELLE Love Kids特别T恤拍摄富有爱心的大片，华谊兄弟公益基金也恰好在这时推出，可以说是孩子、是公益让我们两家走到了一起。”ELLE编辑总监晓雪如是说，“我真切地希望大家都来关注这个项目，从一点点小事做起，就像华谊兄弟公益基金的标语一样：‘关注就是力量’！”</p><p style=\"text-align: justify;\"><span style=\"line-height: 150%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify;\">与以往的“华谊之夜”相比，今年的华谊ELLE之夜处处体现出“星光上海”的群星汇聚与摩登时尚。红毯之后的时装秀精彩纷呈。华谊时尚旗下国际超模秦舒培、奚梦瑶，港台名模Lisa.S、Ana.R、林嘉绮等同场走秀、争奇斗艳，堪称近年来罕见的超豪华走秀阵容，由此体现了华谊时尚的超模阵容。此次华谊时尚特别邀请来自纽约的制作团队，为观众及时装迷们呈上了一场极富创意、惊喜连连的时尚盛宴；国内音乐才子李泉与来自丹麦的知名DJ Johannas Thrope跨国界表演令在场所有来宾及媒体啧啧称赞。</p><p style=\"text-align: justify;\"><span style=\"line-height: 150%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify;\">作为上海国际电影节期间的指定活动，2011星光上海——华谊ELLE之夜不忘其初衷，商界名流、明星名人、文化权威，照例在这每年一度的集体派对上欢聚一堂。华谊时尚董事总经理赵磊说：“华谊之夜今年已经是第4个年头，我们看着它一步步成长，从最初华谊兄弟的内部联谊到如今成为一场有主题、有内容，并肩负社会责任感和公益影响力的盛会，这是我们大家都为之欣喜的。相信今后的华谊ELLE之夜会更成功，也希望有更多的新朋友加入进来！”</p><p style=\"text-align: justify;\"><span style=\"line-height: 150%; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp;</span></p><p style=\"text-align: justify;\">仲夏之夜，黄浦江畔，华谊兄弟时尚与ELLE杂志为我们带来了一场充满爱的时尚盛宴，正如活动主题：“Style with love&quot;，有爱心，世界更有型！华谊ELLE之夜，期待来年再见！</p><p><br/></p>', '0', '0', '0', '4', '1503024363', '0', '0', '0', '0', '', '', null, '6月12日，由华谊兄弟时尚文化传媒与《ELLE世界时装之苑》联手主办的“2011星光上海——华谊ELLE之夜”在上海国际时尚中心盛大举办。公益环节义卖所得款项将全数捐赠给“华谊兄弟公益基金”，用于发展“零钱电影院”项目。  ', '公益，华谊兄弟，华谊兄弟时尚，ELLE', '2011-06-15');
INSERT INTO `strategy` VALUES ('59', '华谊兄弟举行小小画展　企业文化公益先行', '/uploads/images/4h88n7fRKQ1502941338.jpg', '<p style=\"text-align: justify;\">2011年11月22日，华谊兄弟公益基金在公司内启动了儿童公益画展，展览作品来自四川大凉山的孤儿。展览作品名为“我想念但是见不到的人”，由法国自由艺术家雨仁(Eric Rolland)和儿童艺术活动者傅果生发起。</p><p><br/></p><p style=\"text-align: justify;\">展览期间，华谊兄弟的员工、明星、联合国开发计划署官员、联合国儿基会官员、媒体朋友也纷纷亲临画展，欣赏了大凉山孩子们的作品。</p><p style=\"text-align: justify;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502940908298995.png\" title=\"1502940908298995.png\" alt=\"image.png\"/></span></p><p style=\"text-align: center; line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">法国艺术家雨仁(Eric Rolland)和中国儿童艺术活动者傅果生</span></p><p style=\"text-align: justify; line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502940994887722.png\" title=\"1502940994887722.png\" alt=\"image.png\"/></span></p><p style=\"text-align: center; line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">华谊兄弟“小小画展”</span></p><p style=\"line-height: 1.5em; text-align: justify;\"><br/></p><p style=\"text-align: justify;\">2009年开始，艺术家雨仁和傅果生行走于云南的德宏和四川大凉山，和因贫困、人口外流、毒品和艾滋病失去父母的少数民族儿童开展绘画、手工和雕塑等艺术活动。通过不同的艺术活动，希望鼓励孩子们找到自我表达和自我认同的方法。通过收集作品和举办展览，希望外面的世界能更加了解和认可孩子们。作品前后已有三百多幅，其中，参展的19幅画来自大凉山美姑县爱心学校的彝族孩子，在华谊兄弟展览的作品主题是“我想念但是见不到的人”。</p><p><br/></p><p style=\"text-align: justify;\">华谊兄弟公益基金曾经在2011年3月份考察项目时与大凉山的索玛花爱心学校及艺术家雨仁和傅果生结缘，并在爱心学校放映了一场露天电影，让这里的彝族孩子享受到了人生中第一场电影。10月底，雨仁和傅果生希望能让孩子们的作品得到更多的人关注和被欣赏的机会，来到北京奔波画廊，但都没有找到可以办展览的场地。11月，华谊兄弟公益基金将他们请到了公司内，完成了他们在北京的第一个心愿，并得到了华谊兄弟全体工作人员及姚晨、许晴、咏梅、尚文婕等明星的现场支持。</p><p style=\"text-align: justify; line-height: 1.5em;\"><br/></p><p style=\"text-align:center\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502940993984578.png\" title=\"1502940993984578.png\" alt=\"image.png\"/></span></p><p style=\"text-align: justify; line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502941005710618.png\" title=\"1502941005710618.png\" alt=\"image.png\"/></p><p style=\"text-align: justify; line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502941010851147.png\" title=\"1502941010851147.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502941132439887.png\" title=\"1502941132439887.png\" alt=\"image.png\"/></p><p style=\"text-align: center; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">尚雯婕参观画展<br/></span></p><p style=\"text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502941068740062.png\" title=\"1502941068740062.png\" alt=\"image.png\"/></span></p><p style=\"text-align: center; line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">姚晨参观画展，向大凉山的孩子们留下了祝福</span></p><p style=\"text-align: justify; line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502941067815914.png\" title=\"1502941067815914.png\" alt=\"image.png\"/></span></p><p style=\"text-align: center; line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">许晴参观画展，向大凉山的孩子们留下了祝福</span></p><p style=\"text-align: justify; line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><br/></span></p><p style=\"text-align:center\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><img src=\"/uploads/ueditor/php/upload/image/1502941071843634.png\" title=\"1502941071843634.png\" alt=\"image.png\"/></span></p><p style=\"text-align: center; line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">咏梅参观画展，向大凉山的孩子们留下了祝福</span></p><p style=\"line-height: 1.5em;\"><span style=\"border: 1px none windowtext; padding: 0px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><br/></span></p>', '0', '0', '0', '4', '1502966540', '0', '0', '0', '0', '', '', null, '2011年11月22日，华谊兄弟公益基金在公司内启动了儿童公益画展，展览作品来自四川大凉山的孤儿。       ', '公益，华谊兄弟公益基金，小小画展', '2011-11-23');
INSERT INTO `strategy` VALUES ('60', '王中磊携陆毅走进影院 与小学生分享电影', '/uploads/images/lSIkAYKMOZ1502942912.jpg', '<p style=\"text-align: justify;\">3月16日，华谊兄弟上海影院，举行了隆重的开业庆典。活动现场，华谊兄弟传媒股份有限公司总裁王中磊先生、华谊兄弟影院投资有限公司首席运营官盛群女士、内地影星陆毅一起参加了开业仪式。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502942674285050.png\" title=\"1502942674285050.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">华谊兄弟上海影院开业典礼</span></p><p><br/></p><p></p><p style=\"text-align: justify;\">活动现场，王中磊先生表示，华谊兄弟的各个业务版块都在以自己特有方式参与公益。影院拿出场地和票房，明星和员工拿出时间，同时，华谊兄弟也一直通过多种途径支持更多志愿者参与，希望有更多的儿童因为电影而受益。</p><p><span style=\"text-align: justify;\"><br/></span></p><p><span style=\"text-align: justify;\">上海浦东新区民办大别山小学的50名小学生被请到了影院，免费看了一场动画电影，成为影院的第一批观众。学生们表示，很久没有走进电影院看电影，也从来没有想到可以见到明星。当王中磊和陆毅走进放映厅时，同学们都欢呼雀跃，兴奋不已。王中磊现场承诺，今后会请更多的小朋友们到电影院来看电影，也会让更多的华谊人走进学校，为同学们放电影。</span><br/></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502942682326055.png\" title=\"1502942682326055.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">王中磊陆毅与打工子弟合影</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502942874242970.png\" title=\"1502942874242970.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">王中磊和陆毅与小学生分享电影</span></p><p><br/></p>', '0', '0', '0', '4', '1502942912', '0', '0', '0', '0', '', '', null, '3月16日，华谊兄弟上海影院，举行了隆重的开业庆典并邀请上海浦东一所民办小学的50名小学生免费看一场动画电影，成为影院的第一批观众。华谊兄弟传媒股份有限公司总裁王中磊先生、华谊兄弟影院投资有限公司首席运营官盛群女士、内地影星陆毅一起参加了开业仪式。', '公益，华谊兄弟，王中磊', '2012-03-16');
INSERT INTO `strategy` VALUES ('61', '第二届小小画展电影院开展 “零钱电影院”小艺术家到场参观', '/uploads/images/qzixI6i4u21502943382.jpg', '<p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943292489678.png\" title=\"1502943292489678.png\" alt=\"image.png\"/></p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">2012年9月26日， “零钱电影院”第二届小小画展在北京新影联华谊兄弟电影院（望京店）举行开幕活动。华谊兄弟公益基金邀请了“小小画展”北京的小画家和他们的同学——53名打工子弟和3名老师一起来到了现场。同时，望京社区的40名二年级小学生和3名老师也被邀请一同参观展览。&nbsp;</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943178838448.png\" title=\"1502943178838448.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">参观画展的“零钱电影院”孩子在留言薄写下感想</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943201736990.png\" title=\"1502943201736990.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">小画家和他的《山洪》绘画作品</span></p><p><br/></p><p style=\"text-align: justify;\">“零钱电影院”的孩子们看见自己的作品在影院展览，喜出望外，纷纷跑到自己的画作面前欣赏，还与自己的画作合影留念。观影后，孩子们纷纷在留言薄上留下自己想说的话，在小小画展大大的背景板上涂鸦绘画。</p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943245424806.png\" title=\"1502943245424806.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">观影时享用《小小画展》爆米花</span></p><p><br/></p><p style=\"text-align: justify;\">参观展览结束后，孩子们走进放映厅观看电影≪秘鲁大冒险≫，影院还为孩子们准备了香喷喷的爆米花，让孩子们兴奋不已。映后，互动问答游戏环节，孩子们高高举手，踊跃回答问题。最后，孩子们恋恋不舍地拿着奖品、举着“小小画展”的爆米花桶离开了影院。</p><p><br/></p><p style=\"text-align: justify;\">“零钱电影院”小小画展，已于9月26日—10月11日在北京新影联·华谊兄弟影院展览。小小电影梦，用画笔延续。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943246477390.png\" title=\"1502943246477390.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">参观完画展孩子们快乐的大合影</span></p><p><br/></p><p><br/></p>', '0', '0', '0', '4', '1502943382', '0', '0', '0', '0', '', '', null, '2012年9月26日， “零钱电影院”第二届小小画展在北京新影联华谊兄弟电影院（望京店）举行开幕活动。华谊兄弟公益基金邀请了“小小画展”北京的小画家和他们的同学——53名打工子弟和3名老师一起来到了现场。同时，望京社区的40名二年级小学生和3名老师也被邀请一同参观展览。', '公益，华谊兄弟公益基金，小小画展', '2012-09-26');
INSERT INTO `strategy` VALUES ('62', '陆庙图书馆“零钱电影院”迎来第一场电影主题绘画分享课', '/uploads/images/0rLBDGlz4y1502943753.jpg', '<p style=\"text-align: justify;\">2012年10月10日，华谊兄弟公益基金在陆庙图书馆“零钱电影院”，用独特的教育方式，为陆庙小学40名学生上了一堂生动的电影分享课，让孩子们在观看木偶动画片《神笔》的同时，通过手中的画笔，绘出自己对未来美好的憧憬。电影主题绘画分享课结束后，华谊兄弟公益基金的负责人还向受邀参加的图书管理员和志愿者老师分享了“零钱电影院”项目，希望更多的志愿者老师能够加入，让越来越多的贫困儿童能够免费看到好电影。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943578257534.png\" title=\"1502943578257534.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">观看《神笔》后与孩子们互动问答</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502943606319447.png\" title=\"1502943606319447.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">第一次接触颜料的孩子正在专心投入的绘画</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943648873478.png\" title=\"1502943648873478.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">学生们点评自己的绘画作品</span><br/></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943650818145.png\" title=\"1502943650818145.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">40名学生自豪地展示自己的绘画作品</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943686497552.png\" title=\"1502943686497552.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">大家正在分享和交流“零钱电影院”项目</span></p><p><span style=\"font-size: 14px;\"><br/></span></p>', '0', '0', '0', '5', '1502966475', '0', '0', '0', '0', '', '', null, '2012年10月10日，华谊兄弟公益基金在陆庙图书馆“零钱电影院”，用独特的教育方式，为陆庙小学40名学生上了一堂生动的电影分享课。 ', '公益，华谊兄弟公益基金，零钱电影院', '2012-10-10');
INSERT INTO `strategy` VALUES ('63', '第二届小小画展在华谊兄弟上海影院举办开幕活动', '/uploads/images/56bUL3bVaC1502944104.jpg', '<p style=\"text-align: justify;\">2012年10月18日星期四,上海风和日丽，第二届小小画展已巡回展览至华谊兄弟上海影院。开幕当日，华谊兄弟公益基金邀请了来自上海浦东新区民办大别山小学四年级三班的41名同学来到影院参观展览。华谊兄弟上海影院还邀请同学们到导演厅观看3D动画片《萨米大冒险》，并每人赠送一桶爆米花。孩子们享受了边吃爆米花边看电影的愿望，心满意足地回家了。</p><p><br/></p><p style=\"text-align: justify;\">“零钱电影院”小小画展于10月18日—11月2日在华谊兄弟上海影院展览（上海市普陀区大渡河路196号长风景畔广场5楼）。小小电影梦，用画笔延续。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943957276830.png\" title=\"1502943957276830.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">正在华谊兄弟上海影院参观展览的学生</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502943975394656.png\" title=\"1502943975394656.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">学生们开心地吃着爆米花观看3D电影《萨米大冒险》</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502944002973260.png\" title=\"1502944002973260.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">华谊兄弟上海影院“小小画展”</span></p><p><br/></p><p><br/></p>', '0', '0', '0', '4', '1502944104', '0', '0', '0', '0', '', '', null, '2012年10月18日，第二届小小画展巡回展览至华谊兄弟上海影院。开幕当日，华谊兄弟公益基金邀请了来自上海浦东一所民办小学四年级的41名同学来到影院参观展览。', '公益，华谊兄弟公益基金，华谊兄弟影院，小小画展', '2012-10-18');
INSERT INTO `strategy` VALUES ('64', '华谊兄弟王中军画作义卖善款全部用来做公益', '/uploads/images/k7GYEewHML1502944664.jpg', '<p style=\"text-align: justify;\">[文：新浪娱乐&nbsp;<a href=\"http://gongyi.sina.com.cn/gyzx/2013-12-09/102946757.html\" target=\"_blank\">华谊兄弟王中军画作义卖善款全部用来做公益</a>]</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">《天堂电影院》里乡村影院放映师阿尔夫莱多说过： “如果你不出去走走，你会以为这就是全世界”， 这句话引领小男孩萨尔瓦多最终走出乡村成为一位大导演。电影关乎梦想，华谊兄弟正精心呵护这个梦，多年来致力于为学校搭建“零钱电影院”，让儿童，特别是贫困儿童们免费看到好电影，了解外面的世界。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502944230412732.png\" title=\"1502944230412732.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">王中军自画像</span></p><p><br/></p><p style=\"text-align: center;\"><strong>王中军——每个人都应该有属于自己的梦想</strong></p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">《中军和他的朋友们》个人画展12月7日在元·空间举行，对王中军来说，这是一次绘画作品的展示，也是儿时梦想的实现。他小时候最大的梦想是当个画家，虽然不知道画家怎么生存，但是觉得当一个画家挺美好的。假如不是因为小时候爱上绘画，王中军也许不会后来从事摄影，也就没有了第一桶金以及后来的华谊兄弟，或者不会选择电影这条路，今天的华谊兄弟也许会是另一番景象。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">重新拾起画笔，王中军已经是亿万富翁，兴致之外，更多的是为了公益。绘画义卖的善款全部捐给华谊兄弟公益基金，用于贫困学校搭建“零钱电影院”，让越来越多的贫困儿童可以在学校里免费看到好电影，了解外面的世界。“零钱电影院”项目，已在北京，云南，青海，河南，上海等地开展。目前购买王总绘画作品的基金数额已经接近一千万元。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: center;\"><strong>零钱电影院——关乎梦想的公益</strong></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502944257274128.png\" title=\"1502944257274128.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">由闲置教室改建的“零钱电影院”</span></p><p><br/></p><p style=\"text-align: justify;\">一张小小的电影票，可能成为横亘在孩子与梦想之间的障碍，也可能是通往梦想云端的桥梁。贫困儿童吃饭、饮水、医疗都需要帮助，但更需要关注的，是他们心中小小的梦想，他们有权享有更多的精神关爱。华谊兄弟的“零钱电影计划”就是在这样一个理念下诞生，除了持续在做贫困儿童的公益放映之外，其核心公益项目是创造寓教于乐的电影梦空间，儿童可以在这里看电影、读书、绘画、做与梦想有关的主题活动，这个神奇的空间叫“零钱电影院”。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">零钱电影院从运营之初，似乎着就背负着社会的关注与质疑，但它就是这样一个具体而细微，在每一个细小领域都做出十分努力的项目。它是公益，关乎梦想的公益。</p><p><br/></p><p style=\"text-align: justify;\">就像影片《天堂电影院》里讲述的一样――乡村影院放映师阿尔夫莱多放映的电影胶片，和他说的那句“如果你不出去走走，你会以为这就是全世界”，恰如明灯照耀了小男孩萨尔瓦多的人生路，并最终引领他走出乡村，成为一位大导演。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">在这个时代，有太多的人把梦想束之高阁，但是梦想的力量却不能小觑。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">当梦想照进现实，成年人的世界里看到的可能会是梦想如何被现实击碎。但对于这些仍保持着童真之心的孩子们来说，梦想是现实中不可或缺的一抹亮色。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">因为绘画，王中军最终选择了电影。这当然不是梦想与现实相通的唯一路径。但，电影，可以开启梦想。而绘画，可以是电影的另一种表达。</p><p style=\"text-align: justify;\">　　</p><p style=\"text-align: justify;\">关于王中军，关于“零钱电影院”可以说的有很多很多。但是这个与梦想有关的公益项目确是唯一的，它打破孩子与梦想间的障碍，开启他们了解世界的另一种方式，牵起可以跨越时空的交流。</p><p><br/><br/></p>', '0', '0', '0', '4', '1502944739', '0', '0', '0', '0', '', '', null, '王中军连接电影、艺术与公益，用画笔的力量，让更多孩子感受光影的魅力。  ', '公益，华谊兄弟，王中军，华谊兄弟公益基金', '2013-12-09');
INSERT INTO `strategy` VALUES ('65', '“中军和他的朋友们”公益画展迎来小艺术家', '/uploads/images/jINSr2ivMp1502944998.jpg', '<p style=\"text-align: justify;\">2013年12月17日，北京星河双语学校的47名孩子来到“元·空间”，参加“中军和他的朋友们”公益画展。“元·空间”工作人员带领孩子们为每幅画做了生动有趣的讲解，孩子们也从刚进画廊的拘谨慢慢地放松起来，开始交流起自己对绘画作品的一些看法。华谊兄弟创始人、董事长王中军先生也到场与孩子们分享绘画感受，并和孩子们亲切合影留念。本次画展绘画作品义卖所得全部捐给华谊兄弟公益基金，为了让更多的贫困儿童和打工子弟的孩子们能够在学校的“零钱电影院”里看到好电影，获得更多的乐趣。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502944900456916.png\" title=\"1502944900456916.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502944905166296.png\" title=\"1502944905166296.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502944926891194.png\" title=\"1502944926891194.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502944934838957.png\" title=\"1502944934838957.png\" alt=\"image.png\"/></p><p><br/></p><p><br/></p>', '0', '0', '0', '4', '1502944998', '0', '0', '0', '0', '', '', null, '2013年12月17日，北京星河双语学校的47名孩子来到“元·空间”，参加“中军和他的朋友们”公益画展。华谊兄弟创始人、董事长王中军先生也到场与孩子们分享绘画感受，并和孩子们亲切合影留念。', '公益，华谊兄弟公益基金，华谊兄弟，王中军', '2013-12-17');
INSERT INTO `strategy` VALUES ('66', '《ONE DAY》受邀法国和意大利两大国际电影节', '/uploads/images/0x5MAfNfeu1502945326.jpg', '<p style=\"text-align: justify;\">由华谊兄弟公益基金投资拍摄的《ONE DAY》儿童公益短片系列之《礼物》获法国克莱蒙·费朗国际短片电影节邀请，参与2015年中国电影单元展映！该电影节有“短片戛纳”美誉之称，是全球第一个短片电影节，法国第二大电影节。电影节将于2015年1月30日至2月7日在法国克莱蒙·费朗举行。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502945193175370.png\" title=\"1502945193175370.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align: justify;\">好事成双，《礼物》同时还入围了意大利CinemAmbiente环保电影节的Panorama短片单元，该单元将展映来自全球各地的与环境问题相关的创意影片。电影节将于2014年5月31日至6月5日在意大利都灵举行。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502945202354017.png\" title=\"1502945202354017.png\" alt=\"image.png\"/></p><p><br/></p><p></p><p style=\"text-align: justify;\">《礼物》讲述了两位裕固族留守儿童的故事，感谢演员咏梅的特别支持。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: justify;\">此前，《ONE DAY》儿童公益短片系列之《看不见的画》已荣获美国西雅图儿童电影节“全球聚焦奖”，《小小电影院》入围日本东京国际儿童电影节，《秋扎》入围比利时布鲁塞尔国际儿童电影节。期待《ONE DAY》今年在全国院线上映。</p><p><br/><br/></p>', '0', '0', '0', '4', '1502945326', '0', '0', '0', '0', '', '', null, '由华谊兄弟公益基金投资拍摄的《ONE DAY》儿童公益短片系列之《礼物》获法国克莱蒙·费朗国际短片电影节邀请，参与2015年中国电影单元展映，并入围意大利CinemAmbiente环保电影节的Panorama短片单元。', '公益，华谊兄弟公益基金，有一天', '2014-05-15');
INSERT INTO `strategy` VALUES ('67', '总裁王中磊与威廉弟弟的“零钱电影院”之行', '/uploads/images/IpW1n20NVW1502953256.jpg', '<p style=\"text-align: justify;\">2014年5月，华谊兄弟总裁王中磊来到“零钱电影院”的云南学校，为全校师生分享了电影的魅力，由华谊兄弟公益基金制作拍摄的《ONE DAY》儿童公益短片系列之《小小电影院》，也在“零钱电影院”进行了一场特别的放映。</p><p><br/></p><p style=\"text-align: justify;\">近期，在电视节目《爸爸回来了》中大受欢迎的“威廉弟弟”也和总裁爸爸一起同行，不仅为住宿的全校师生放映了他喜欢的电影《十二生肖》，还和爸爸分别围绕短片《小小电影院》，创作了一幅他心目中的电影院。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502952989447790.png\" title=\"1502952989447790.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">总裁王中磊“零钱电影院”公益之行</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502953019637920.png\" title=\"1502953019637920.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">父子俩看了ONE DAY儿童公益短片系列之《小小电影院》后的“画电影”作品</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502953036861134.png\" title=\"1502953036861134.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">总裁王中磊与云南“零钱电影院”所在学校的石校长合影</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502953053656373.png\" title=\"1502953053656373.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">总裁王中磊、威廉弟弟与云南小朋友</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502953068132934.png\" title=\"1502953068132934.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">总裁王中磊为“零钱电影院”所在学校寄语</span></p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502953083394014.png\" title=\"1502953083394014.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">“零钱电影院” 大合影</span></p><p><br/></p><p></p><p style=\"text-align: justify;\">“零钱电影院”是华谊兄弟公益基金的核心公益项目，旨在贫困地区的学校或城市中的打工子弟的学校内搭建一间寓教于乐的电影空间，孩子们可以在里面看电影、画电影、阅读、开展各种影像教学相结合的活动。从2011年至今，已在全国的北京、上海、广州、河南、云南、青海，合计搭建了26个“零钱电影院”，放映了500场电影，受益儿童近5万名。电影，正在为这些学校的孩子们中带来改变。</p><p style=\"text-align: justify;\">&nbsp;<br/></p><p style=\"text-align: justify;\">感谢每一位华谊人及社会爱心人士对华谊兄弟公益基金的支持。小零钱，大爱心，让我们一起搭建孩子们的电影天堂。</p><p><br/><br/></p>', '0', '0', '0', '5', '1502953256', '0', '0', '0', '0', '', '', null, '2014年5月，华谊兄弟总裁王中磊来到“零钱电影院”的云南学校，为全校师生分享了电影的魅力，由华谊兄弟公益基金制作拍摄的《ONE DAY》儿童公益短片系列之《小小电影院》，也在“零钱电影院”进行了一场特别的放映。', '公益，华谊兄弟公益基金，王中磊，零钱电影院', '2014-06-12');
INSERT INTO `strategy` VALUES ('68', '群星助力“零钱电影院” 2014华谊ELLE之夜为爱募款', '/uploads/images/5wEBCXCacW1502953656.jpg', '<p style=\"text-align: justify;\">&nbsp;6月15日，“2014星光上海—华谊ELLE之夜”在上海世博创意秀场举办。今年已是华谊兄弟公益基金与“华谊ELLE之夜”第四年的合作，继续致力于让越来越多的贫困儿童免费看到好电影，为“零钱电影院”筹募善款。<br/> <br/> </p><p style=\"text-align: justify;\">今年的“华谊ELLE之夜”群星璀璨，华谊兄弟总裁王中磊携子威廉弟弟王元也、华谊兄弟时尚董事总经理赵磊、赫斯特中国董事总经理杨玟、《ELLE世界时装之苑》编辑总监晓雪，韩国著名男星宋承宪，导演滕华涛、徐峥、马伊琍、冯绍峰、马苏、袁姗姗、郑恺、安以轩、周杰、莫小棋、王阳明、刘力扬、王传君、安钧璨、王若心、刘云、张瑶、林芮西、蓝燕、母其弥雅，知名歌手姚贝娜，国际超模秦舒培、游天翼、吕燕，日本名模赤坂沙世，国内知名模特佟晨洁，华谊兄弟时尚新锐模特张桐、刘丽洁、蒋笑伊、薛冬琪、汤沁怡、王旭、赵美娟等纷纷为爱而来，为“零钱电影院”奉献爱心，搭建孩子们的电影天堂。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502953335517419.png\" title=\"1502953335517419.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">威廉弟弟和BOSS老爸</span></p><p><br/></p><p style=\"text-align: justify;\">汇聚爱心的公益展览区是当晚的一大亮点。今年“华谊ELLE之夜”独家邀请了31位明星名人与艺术家，以“童年”为灵感创作，在现场的“小小画展”展现他们记忆中的童年景象。参与“小小画展”的艺术家和明星名流有（按姓氏拼音首字母排序）：曹涤菲、池磊、陈燃、陈卓义、杜家毅、窦骁、弗洛伦泰因·霍夫曼（Florentijn Hofman）、郭鸿蔚、高瑀、黄晓明、黄奕、罗丹、刘建华、陆坤、李磊、刘力扬、刘韡、马伊俐、秦舒培、沈南鹏、奚梦瑶、宣晴、薛松、夏星、徐震、游天翼、羽西、杨勇、赵磊、庄泳、张震。所有作品均在晚宴上以默拍形式拍卖，感谢所有爱心人士的参与，31幅作品全部“名花有主”，拍卖善款将全部捐赠给华谊兄弟公益基金“零钱电影院”，圆孩子们一个电影梦。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502953370226666.png\" title=\"1502953370226666.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">“小小画展”展区</span></p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">今年的特邀合作伙伴DAZZLE携手艺术家岳敏君，以“零钱电影院”为主题创作了夏日限量版公益T恤。ELLE Kids也邀请了华谊兄弟总裁王中磊、艺人姚晨、吕燕、张梓琳特别设计4款爱心童装。DAZZLE公益T恤和ELLE Kids爱心童装均已在全国展开公益售卖，销售利润将捐赠给“零钱电影院”，用电影开启孩子们了解世界的另一种方式，推动他们实现更多的梦想。</p><p><br/><br/></p><p><br/></p>', '0', '0', '0', '4', '1502953656', '0', '0', '0', '0', '', '', null, '6月15日，“2014星光上海—华谊ELLE之夜”在上海世博创意秀场举办。今年已是华谊兄弟公益基金与“华谊ELLE之夜”第四年的合作，继续致力于让越来越多的贫困儿童免费看到好电影，为“零钱电影院”筹募善款。', '公益，华谊兄弟，华谊兄弟公益基金', '2014-06-18');
INSERT INTO `strategy` VALUES ('69', 'ONE NIGHT公益晚会成功举办 《有一天》9.10感动上映', '/uploads/images/DBIrIMj0hL1502954417.jpg', '<p style=\"text-align: justify;\">与公益电影《有一天》（《ONE DAY》）一脉相承的“关爱特殊儿童ONE NIGHT公益晚会”，7月16日在杭州黄龙体育馆温暖举行。当晚，韩庚、金志文、李荣浩、李宇春、佟大为倾情献唱，陈坤、桂纶镁、咏梅、袁姗姗也依次登台助阵，演唱会现场的嘉宾与电影《有一天》中的四位小主演在现场零距离互动，呼吁社会关爱、关注特殊儿童。作为压轴亮相的主角，周迅为观众动情演唱了电影《有一天》主题曲《给小孩》，更送出整场演出的最大“彩蛋”，周迅身着白色婚纱与男友高圣远牵手现身舞台，当场宣读誓言，交换戒指，完成结婚仪式。结婚仪式完成后，周迅更牵着丈夫的手说：“以后，我会和我的丈夫一起，帮助那些需要帮助的孩子，明年还会有ONE NIGHT演唱会，还会有《ONE DAY》大电影。我们明年见！”</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954049481890.png\" title=\"1502954049481890.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">“关爱特殊儿童ONE NIGHT公益晚会”成功举办 群星共同为特殊儿童发声</span></p><p><br/></p><p style=\"text-align: justify;\">电影《有一天》由9个以特殊儿童为主题的故事组成，聚集了12位明星0片酬出演。而16日当晚，电影的4位小演员代表也来到演唱会现场。《有一天》（《ONE DAY》）电影项目发起人带着小演员顺利打头阵登场，讲述了电影拍摄前后顺利令人高兴的变化，并和金志文合唱了一曲《小星星》引来全场第一次大合唱。此后，《有一天》主演咏梅也来到现场，呼吁大家更多地关注留守儿童。咏梅之后，韩庚动情演唱，并与大家分享拍摄电影《有一天》时，与盲童互动的故事与经历。袁姗姗也带着小演员世豪登台，更合唱了《两只老虎》，听不见声音的孩子唱出了歌曲，让全场为之感动不已，齐齐打起了拍子为孩子喝彩。而桂纶镁则和《有一天》里的小演员展鹏一起登台，讲述了对“零钱电影院”的支持，呼吁让更多贫困地区的孩子有机会看电影。最后陈坤携手《有一天》可爱的小演员小喆登场，小喆不但与全场观众畅谈拍摄电影的趣事，更励志要像陈坤一样当一个演员。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954077100657.png\" title=\"1502954077100657.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">《有一天》小演员顺利自豪地与观众分享自己拍摄前后的成长与变化</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954138342932.png\" title=\"1502954138342932.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">咏梅现场呼吁大家关注留守儿童</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954163935260.png\" title=\"1502954163935260.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">韩庚献唱并与讲述盲童故事的拍摄经历</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954184205598.png\" title=\"1502954184205598.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">袁姗姗与小演员世豪合唱歌曲《两只老虎》</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954268398783.png\" title=\"1502954268398783.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">桂纶镁与小演员展鹏呼吁让更多贫困地区的孩子有机会看电影</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954188727316.png\" title=\"1502954188727316.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">陈坤和小演员小喆讲述被拐卖儿童的故事</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954205819625.png\" title=\"1502954205819625.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">周迅温暖献唱电影《有一天》主题曲《给小孩》</span></p><p><br/></p><p style=\"text-align: justify;\">ONE NIGHT公益晚会上，华谊兄弟总裁、华谊兄弟公益基金发起人王中磊登台讲述 《有一天》的拍摄历程，《有一天》一共花费900天才完成，整个电影的拍摄过程就像是一个梦的过程，也是一个梦的接力。他更表示，“我们的理想是免费给大家看这部影片，至少华谊今天在这宣布这部电影在所有的放映当中都是零分账的，我们不会拿一分钱，希望这样一个举动能让更多的人去到电影院中感受这部不一样的电影”。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954239911321.png\" title=\"1502954239911321.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">总裁王中磊讲述《有一天》诞生的历程</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align: center;\">中国第一部公益电影《有一天》，9月10日上映，9个故事10分爱，改变从一天开始！</p><p style=\"text-align: center;\"><span style=\"font-size: 16px;\"><br/></span></p>', '0', '0', '0', '4', '1503024180', '0', '0', '0', '0', '', '', null, '与公益电影《有一天》（《ONE DAY》）一脉相承的“关爱特殊儿童ONE NIGHT公益晚会”，7月16日在杭州黄龙体育馆温暖举行。演唱会现场，嘉宾与电影《有一天》中的四位小主演零距离互动，呼吁社会关爱、关注特殊儿童。    ', '公益，华谊兄弟公益基金', '2014-07-17');
INSERT INTO `strategy` VALUES ('70', '最公益《有一天》今日上映：华谊0分账，全国5元看', '/uploads/images/G30JWfpXUC1502956242.jpg', '<p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954556938916.png\" title=\"1502954556938916.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">《有一天》海报</span></p><p><br/></p><p style=\"text-align: justify;\">9月10日，年度最公益电影《有一天》全国公映。《有一天》聚集了周迅、韩庚、冯绍峰、袁姗姗、张涵予、安以轩、徐帆、斯琴高娃、王千源、熊乃瑾、咏梅、冯嘉怡等十二位明星，通过九个感人而有趣的故事，呼吁大家关注九类特殊儿童群体。该片由华谊兄弟公益基金发起项目，演员0片酬出演，华谊0分账出品和发行，全国多院线达成一致以“5元公益价”上映，从制作到宣发屡屡突破传统，不但树立起国内公益电影的新标杆，更突破电影行业，将公益的接力棒传递给了更多的企业和品牌。</p><p style=\"text-align: justify;\">&nbsp;<br/></p><p style=\"text-align: center;\"><strong>华谊0分账 全国5元看 9月10日成“全国公益电影日”</strong></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502954602396797.png\" title=\"1502954602396797.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">全国院线爱心接力</span></p><p><br/></p><p style=\"text-align: justify;\">《有一天》从立项到制作，再到出品和宣发，每一步都是突破。从演员的0片酬出演，到华谊兄弟的0分账出品发行，再到全国院线空前一致的“5元公益票价”，《有一天》“最公益”的美名来自于整个电影行业以及社会各界的情义相挺。据悉，在《有一天》一路成长的旅程中，有近50家媒体或品牌以各自的方式支援《有一天》，关注特殊儿童。万达全院线将在本周推出“公益电影周”，支持《有一天》的上映；橙天嘉禾也将9月10日打造为整个院线的“公益日”，北京、南京、郑州、长沙、重庆、长春、青岛、深圳、济南、唐山、合肥、贵阳、西安、苏州、杭州、武汉、沈阳等城市也纷纷接棒点燃《有一天》之光，让9月10日成为了真正的“全国公益电影日”。</p><p><br/></p><p style=\"text-align: justify;\">在电影行业之外，跨行业的支持也让《有一天》绽放得更加灿烂。新浪网的深度媒体合作支持，腾讯视频网络版《有一天》公益上映，格瓦拉和卖座网提前4周开启预售，迄今为止已经售出超过2万张电影票；而包括乐视、陌陌、美拍、啪啪、窝窝团、团800等手机应用品牌；“理想国”、《大众电影》、《健康之友》等文化品牌，LOMO相机、花里鲜花等制造商品牌等，都发挥各自所长，让《有一天》传播的更远，走的更久。</p><p style=\"text-align: justify;\">&nbsp;<br/></p><p style=\"text-align: center;\"><strong>9月10日，《有一天》九个故事十分爱，公益之爱暖心传递！</strong></p><p><br/></p>', '0', '0', '0', '4', '1502966186', '0', '0', '0', '0', '', '', null, '9月10日，年度最公益电影《有一天》全国公映。《有一天》聚集了周迅、韩庚、冯绍峰、袁姗姗、张涵予、安以轩、徐帆、斯琴高娃、王千源、熊乃瑾、咏梅、冯嘉怡等十二位明星，通过九个感人而有趣的故事，呼吁大家关注九类特殊儿童群体。 ', '公益，华谊兄弟，华谊兄弟公益基金，有一天', '2014-09-10');
INSERT INTO `strategy` VALUES ('71', '电影《有一天》受邀联合国纽约总部', '/uploads/images/0RFDjPVS3k1502956787.jpg', '<p style=\"text-align: justify;\">2015年3月20日，世界唐氏综合症日会议在联合国纽约总部召开。华谊兄弟公益基金负责人、小演员顺利及其母亲受邀出席并演讲，分享《有一天》以影像改变公众对特殊儿童群体看法的拍摄理念，呼吁世界关注唐氏综合症群体在中国的现状。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502956619696249.png\" title=\"1502956619696249.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">《有一天》向世界分享电影公益理念</span></p><p><br/></p><p style=\"text-align: justify;\">会议现场，《有一天》发起人讲述了电影的拍摄起源。自2011年起，华谊兄弟公益基金开始到中国各地的学校放电影，希望通过我们的努力，让中国的贫困儿童可以在电影的陪伴下成长。放电影的路上，我们被很多真实的儿童群体触动，2012年初，我们决定筹款拍摄电影《有一天》，献给这些不如大家幸运的孩子们，也让公众思考和关注特殊儿童群体。</p><p><br/></p><p style=\"text-align: justify;\">《有一天》的全部过程，都离不开“改变”。患有唐氏综合症的小演员顺利在参与《有一天》拍摄后，变得更加独立、自信，并真的到了一家面包店工作。《有一天》也推动了唐氏综合症群体的培训学校积极行动，他们可以通过电影让企业、家长和孩子们自身更直接地了解，唐氏综合症群体“可以工作”。我们希望通过本次会议，为唐氏综合症群体带来更多改变。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502956645676819.png\" title=\"1502956645676819.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">《有一天》主创在联合国纽约总部发言</span></p><p><br/></p><p style=\"text-align: justify;\">今年是唐氏综合症日10周年，顺利也成为中国唐氏综合症群体代表赴联合国参会的中国第一人，在现场分享自己希望成为面包大师的梦想。顺利自信地告诉大家，自己的工作是“上面包、写出品单、贴价签、尽心尽力为客人服务”，获得了全场会议来宾的热烈掌声。顺利的母亲也特别感谢《有一天》为顺利及家庭带来的改变，希望更多企业为唐氏综合症群体提供就业机会，并关注他们未来的理财、婚姻、健康等问题。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502956672836134.png\" title=\"1502956672836134.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">《有一天》特别放映让参会嘉宾深受触动</span></p><p><br/></p><p style=\"text-align: justify;\">会议现场，电影《有一天》继2014年联合国残疾人电影节后，第二次在联合国总部进行特别放映。不少参会嘉宾都表示非常喜欢这部温暖美好的电影。这也是中国公益电影第一次真正走向世界。</p><p><br/></p><p style=\"text-align: justify;\">在此，我们要特别感谢近170位捐款帮助顺利母子来到联合国总部的爱心人士。</p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502956699815395.png\" title=\"1502956699815395.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">现场大屏幕特别感谢捐赠人的帮助</span></p><p><br/></p><p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502956715790486.png\" title=\"1502956715790486.png\" alt=\"image.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">电影《有一天》</span></p><p><br/></p><p><br/></p>', '0', '0', '0', '4', '1503023970', '0', '0', '0', '0', '', '', null, '2015年3月20日，世界唐氏综合症日会议在联合国纽约总部召开。华谊兄弟公益基金负责人、小演员顺利及其母亲受邀出席并演讲，分享《有一天》以影像改变公众对特殊儿童群体看法的拍摄理念，呼吁世界关注唐氏综合症群体在中国的现状。 ', '公益，华谊兄弟公益基金，有一天', '2015-03-20');
INSERT INTO `strategy` VALUES ('72', '《有一天》推出无障碍版，世界地球日让盲人“看见”光影', '/uploads/images/GHEhBd8z4o1502957247.jpg', '<p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502956885871659.png\" title=\"1502956885871659.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align: justify;\">中国第一部公益电影《有一天》在世界地球日推出了无障碍版，让更多的盲人可以享受到电影的乐趣。许晴、袁泉、黄轩、谢娜、陈数、李静、瑶淼、顾长卫、辛柏青，9位明星无偿为电影献声。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502956916122080.png\" title=\"1502956916122080.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align: justify;\">无障碍电影是专门为了方便残障人士观看的经过加工过的电影，通过增补大量配音解说的方式，让视力障碍者了解电影的内容，享受电影艺术乐趣。作为中国第一部真正意义上的公益电影《有一天》，无论从创新的筹款拍摄方式，到独特的拍摄视角，都已获得了国际多个电影节及联合国的的肯定，3月底，华谊兄弟公益基金负责人也走进了联合国纽约总部演讲，让世界了解到中国如何以电影的方式对特殊儿童群体的发展做出积极的贡献。</p><p><br/></p><p style=\"text-align:center\"><img src=\"/uploads/ueditor/php/upload/image/1502957195835458.png\" title=\"1502957195835458.png\" alt=\"image.png\"/></p><p><br/></p><p></p><p style=\"text-align: justify;\">电影已先后得到了包括周迅、徐帆、张涵予、冯绍峰、韩庚、许晴、谢娜、袁姗姗、黄轩、袁泉、陈数、李静等21位明星的接力支持，让越来越多的公众因为明星的影响力和电影的独特视角，产生共鸣。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">Tips: 关于世界地球日：世界地球日（World Earth Day）即每年的4月22日，是一项世界性的环境保护活动。2009年第63届联合国大会决议将每年的4月22日定为“世界地球日”。该活动最初在1970年的美国由盖洛德·尼尔森和丹尼斯·海斯发起，随后影响越来越大。活动宗旨在唤起人类爱护地球、保护家园的意识，促进资源开发与环境保护的协调发展，进而改善地球的整体环境。</p><p><br/></p>', '0', '0', '0', '4', '1502957247', '0', '0', '0', '0', '', '', null, '中国第一部公益电影《有一天》在世界地球日推出了无障碍版，让更多的盲人可以享受到电影的乐趣。许晴、袁泉、黄轩、谢娜、陈数、李静、瑶淼、顾长卫、辛柏青，9位明星无偿为电影献声。', '公益，华谊兄弟公益基金，有一天', '2015-04-22');
INSERT INTO `strategy` VALUES ('73', '华谊兄弟公益基金携手联合国，为少数民族女性共建“指尖上的幸福”', '/uploads/images/5YBN5LM5ZQ1502957493.jpg', '<p style=\"text-align: center;\"><img src=\"/uploads/ueditor/php/upload/image/1502957317301723.png\" title=\"1502957317301723.png\" alt=\"image.png\"/></p><p><br/></p><p style=\"text-align: justify;\">2015年11月24日，联合国开发计划署与华谊兄弟公益基金在北京签署合作协议，共同致力于推动少数民族女性赋权与发展。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">该合作协议围绕联合国开发计划署“促进少数民族地区女性综合发展项目”展开，计划通过发展传统手工艺，帮助少数民族地区贫困弱势群体进一步参与中国社会经济发展，并公平享有发展成果。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: center;\"><strong>扶助贫困地区精神脱贫从少数民族女性赋权开始</strong></p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">华谊兄弟公益基金一直致力于贫困地区儿童的精神教育，通过电影的方式创造更多善的力量，其核心公益项目“零钱电影院”也已在贵州黔东南地区开展起来。与此同时，为了多维度、多广度地实现少数民族地区脱贫目标，华谊兄弟公益基金将大力推动少数民族地区女性赋权及发展工作。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">本次签约仪式标志着华谊兄弟公益基金进一步关注少数民族女性赋权与发展问题，华谊兄弟公益基金负责人认为：“少数民族妇女的发展是儿童成长的基本保障。”华谊兄弟公益基金将参与项目指导委员会，作为经验丰富，有着独具特色的公益组织及联合国开发计划署的长期合作伙伴，为项目提供公益经验和财政支持。</p><p style=\"text-align: justify;\"><br/></p><p style=\"text-align: center;\"><strong>四度携手联合国 温情点亮公益</strong></p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">过去十年来，联合国开发计划署与中国政府开展扶贫合作，将传统与创新措施相结合，为云南、青海、新疆和内蒙古等地的27个少数民族聚居区域创造就业。开发署驻华代表处副国别主任何佩德先生在签约仪式上说，“有了华谊兄弟公益基金的协助，我们有信心帮助项目地区的少数民族女性摆脱贫困，改善她们的福祉。”</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">华谊兄弟公益基金曾四度携手联合国，结下不解之缘。作为联合国开发署的重要合作伙伴，华谊兄弟公益基金将鼎力支持“促进少数民族地区女性综合发展项目”，未来将在黔东南地区设立试点，重点解决苗族村落的贫困问题，协助少数民族女性凭借传统手工艺技能改善升级，提高社会经济地位，同时保护本民族的传统文化。</p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: center;\"><strong>关注就是力量 我们在路上</strong></p><p style=\"text-align: justify;\">&nbsp;</p><p style=\"text-align: justify;\">解决少数民族地区的贫困问题是中国实现脱贫的重中之重，少数民族人口占中国总人口的9%，其贫困人口却几乎占中国贫困人口的三分之一。少数民族女性在教育、工作和医疗保健上相较于男性更处于劣势地位，少数民族女性在社群规划和决策中缺少平等的发言权。</p><p><br/></p><p style=\"text-align: justify;\">与此同时，女性也承担着母亲的责任，让她们过上有尊严的生活，同样能给儿童带来精神幸福。从“零钱电影院”到公益电影《有一天》，再到此次与联合国开发署携手关注少数民族女性赋权，华谊兄弟公益基金持续加大关注力，救助贫困、弱势群体，我们从未止步！</p><p><br/></p>', '0', '0', '0', '4', '1502957493', '0', '1', '0', '0', '', '', null, '2015年11月24日，联合国开发计划署与华谊兄弟公益基金在北京签署合作协议，共同致力于推动少数民族女性赋权与发展。', '公益，华谊兄弟公益基金，联合国', '2015-11-25');
