-- ----------------------------
-- Table structure for video
-- ----------------------------
DROP TABLE IF EXISTS `video`;
CREATE TABLE `video` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL COMMENT '视频标题',
  `thumb` varchar(255) DEFAULT NULL COMMENT '视频图片',
  `content` text NOT NULL COMMENT '视频',
  `sort` tinyint(4) unsigned DEFAULT NULL COMMENT '排序',
  `create_time` int(11) unsigned DEFAULT NULL COMMENT '发布时间',
  `status` tinyint(1) unsigned DEFAULT '0' COMMENT '是否删除',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

